{
  "compileOnSave": true,
  "compilerOptions": {
    "target": "es2016",
    "noEmitOnError": false,
    "removeComments": true,
    "lib": ["es5", "ES2015", "ES2016"],
    "alwaysStrict": true,
    "strict": true,
    "moduleResolution": "node",
    "module": "ES6",
    "plugins": [
      {
        "transform": "ts-auto-mock/transformer",
        "cacheBetweenTests": false
      }
    ]
  },
  "typedocOptions": {
    "entryPoints": ["src/kwinscript"]
  },
  "include": ["src/**/*"]
}
