// NOTE: if you have trouble with a rule not working install eslint as global
// then use `eslint --print-config out.txt` and check the output for problems
{
    "env": {
        "es6": true,
        "node": true
    },
    "globals": {
        "Atomics": "readonly",
        "SharedArrayBuffer": "readonly"
    },
    "parser": "@typescript-eslint/parser",
    "parserOptions": {
        "ecmaVersion": 2018,
        "sourceType": "module"
    },
    "plugins": [
        "@typescript-eslint"
    ],
    "extends": [
        "eslint:recommended",
        "plugin:@typescript-eslint/eslint-recommended"
    ],
    "rules": {
        "semi": [2, "always"],
        "no-unused-vars": "off",
        "@typescript-eslint/no-unused-vars": "error",
        "quotes": [2, "single", { "avoidEscape": true }],
        "padding-line-between-statements": [
            "error",
            { "blankLine": "always", "prev": "*", "next": "return" }
        ],
        "brace-style": [2, "stroustrup", { "allowSingleLine": false }],
        "curly": ["error", "all"],
        "@typescript-eslint/consistent-type-definitions": ["error", "type"],
        "@typescript-eslint/explicit-function-return-type": "error",
        "@typescript-eslint/explicit-member-accessibility": "error",
        "@typescript-eslint/array-type": ["error", {"default": "generic"}],

        // these two rules are conflicting, I need some help solving this. I'll disable one for now
        // "@typescript-eslint/typedef": ["error", { "variableDeclaration": true }],
        "@typescript-eslint/no-inferrable-types": "error"
    }
}

$NodeVersion = Invoke-Expression "node.exe --version"
if ($NodeVersion.StartsWith("v8.")) {
    node.exe build\src\destreamer.js $args
}
else {
    node.exe --max-http-header-size 32768 build\src\destreamer.js $args
}

