module.exports = {
  root: true,
  extends: '@mlaursen/eslint-config',
  globals: {
    gtag: 'readonly',
  },
  rules: {
    // only used for prop-types and easily caught
    'no-empty': 0,
  },
  overrides: [
    {
      files: [
        'packages/documentation/src/**/*.ts',
        'packages/documentation/src/**/*.tsx',
      ],
      extends: 'plugin:@next/next/recommended',
      rules: {
        '@next/next/no-html-link-for-pages': [
          1,
          'packages/documentation/src/pages',
        ],
        // I don't really want the image optimizations for the documentation site
        // since I use random images from other services
        '@next/next/no-img-element': 0,
      },
    },
    {
      files: ['packages/documentation/src/components/Demos/**/*.tsx'],
      rules: {
        // normally don't care about these for demos
        'react/no-array-index-key': 0,
      },
    },
    {
      files: ['packages/*/src/index.ts'],
      rules: {
        // I don't know how to get @module to work but it's supported by typedoc
        'tsdoc/syntax': 0,
      },
    },
  ],
};

module.exports = {
  extends: ['./.eslintrc.js'],
  overrides: [
    {
      files: ['**/*.ts', '**/*.tsx'],
      rules: {
        '@typescript-eslint/consistent-type-imports': ['error'],
      },
    },
  ],
};

const { readFileSync } = require('fs');
const { resolve } = require('path');

const { tokens, packages } = require('./changelogData');

const PACKAGE_REGEXP = new RegExp(`${packages.join('|')}`);

const GROUPS = [
  'Breaking Changes',
  'Bug Fixes',
  'Features',
  'Documentation',
  'Performance Improvements',
  'Other Internal Changes',
];
const BREAKING_CHANGES = 'Breaking Changes';

/**
 * @param commit {import("conventional-commits-parser").Commit}
 */
function getCommitType({ type, scope, revert }) {
  if (type === 'revert' || revert) {
    return 'Reverts';
  }

  if (scope === 'docs') {
    return 'Documentation';
  }

  switch (type) {
    case 'feat':
      return 'Features';
    case 'fix':
    case 'bugfix':
      return 'Bug Fixes';
    case 'perf':
      return 'Performance Improvements';
    case 'docs':
      return 'Documentation';
    default:
      return 'Other Internal Changes';
  }
}

function getCommitScope({ scope }) {
  if (PACKAGE_REGEXP.test(scope)) {
    return `@react-md/${scope}`;
  }

  switch (scope) {
    // these are mostly to create the initial changelog
    case 'docs':
    case 'pages':
    case 'sassdoc':
    case 'sandbox':
    case 'demos':
    case 'blog':
    case 'indexer':
    case 'guides':
    case 'website':
      return 'react-md.dev';
    case 'slider':
      return '@react-md/form';
    case 'grid':
      return '@react-md/utils';
    default:
      return scope || '';
  }
}

const TOKEN_REGEXP = new RegExp(
  `(?<=\\s|^)(${tokens.join('|')})(?=\\s|$)`,
  'g'
);

function tokenize(subject) {
  return subject
    .replace(TOKEN_REGEXP, '`$1`')
    .replace(/([a-z][A-z]+Props)/g, '`$1`');
}

/**
 * @type {import("conventional-changelog-core").ParserOptions}
 */
const parserOpts = {
  headerPattern: /^(\w*)(?:\((.*)\))?: (.*)$/,
  headerCorrespondence: ['type', 'scope', 'subject'],
  noteKeywords: ['BREAKING CHANGE'],
  revertPattern:
    /^(?:Revert|revert:)\s"?([\s\S]+?)"?\s*This reverts commit (\w*)\./i,
  revertCorrespondence: ['header', 'hash'],
};

/**
 * @type {import("conventional-changelog-core").WriterOptions}
 */
const writerOpts = {
  transform: (commit, context) => {
    // don't want to show the release tag in changelog
    if (commit.scope === 'release') {
      return;
    }

    const issues = [];
    let isBreaking = false;
    commit.notes.forEach((note) => {
      isBreaking = true;
      note.title = BREAKING_CHANGES;
    });

    commit.type = getCommitType(commit);
    commit.scope = getCommitScope(commit);
    if (!isBreaking && (!commit.type || commit.scope.includes('deps'))) {
      // don't include un-typed commits in changelogs or deps
      return;
    }

    if (typeof commit.hash === 'string') {
      commit.shortHash = commit.hash.substring(0, 7);
    }

    if (typeof commit.subject === 'string') {
      let url = context.repository
        ? `${context.host}/${context.owner}/${context.repository}`
        : context.repoUrl;

      if (url) {
        url = `${url}/issues/`;
        commit.subject = commit.subject.replace(/#([0-9]+)/g, (_, issue) => {
          issues.push(issue);
          return `[#${issue}](${url}${issue})`;
        });
      }

      commit.subject = tokenize(commit.subject);
    }

    // remove references that already appear in the subject
    commit.references = commit.references.filter(
      (reference) => !issues.includes(reference.issue)
    );

    return commit;
  },
  groupBy: 'type',
  commitGroupsSort: function (a, b) {
    const aIndex = GROUPS.indexOf(a.title);
    const bIndex = GROUPS.indexOf(b.title);
    if (aIndex !== -1 && bIndex !== -1) {
      return aIndex - bIndex;
    }

    return a.title.localeCompare(b.title);
  },
  commitsSort: ['scope', 'subject'],
  noteGroupsSort: 'title',
  mainTemplate: readFileSync(
    resolve(__dirname, './templates/template.hbs'),
    'utf-8'
  ),
  headerPartial: readFileSync(
    resolve(__dirname, './templates/header.hbs'),
    'utf-8'
  ),
  commitPartial: readFileSync(
    resolve(__dirname, './templates/commit.hbs'),
    'utf-8'
  ),
  footerPartial: readFileSync(
    resolve(__dirname, './templates/footer.hbs'),
    'utf-8'
  ),
};

/**
 * This is basically the conventional-changelog-angular with a few changes to
 * allow more commit messages to appear. I also "tokenize" known packages and
 * exports from react-md in the changelogs.
 *
 * @type {import("conventional-changelog-core").Options.Config}
 */
module.exports = {
  parserOpts,
  writerOpts,
  conventionalChangelog: {
    writerOpts,
    parserOpts,
  },
  recommendedBumpOpts: {
    config: {
      parserOpts,
      writerOpts,
    },

    whatBump: (commits) => {
      let level = 2;
      let breaking = 0;
      let features = 0;
      commits.forEach((commit) => {
        if (commit.notes.length > 0) {
          breaking += commit.notes.length;
          level = 0;
        } else if (commit.type === 'feat') {
          features += 1;
          if (level === 2) {
            level = 1;
          }
        }
      });

      const verb = breaking === 1 ? 'is' : 'are';
      return {
        level,
        reason: `There ${verb} ${breaking} BREAKING CHANGES and ${features} features`,
      };
    },
  },
};

const fs = require('fs');
const path = require('path');

const packages = fs
  .readdirSync(path.join(process.cwd(), 'packages'))
  .filter((name) => !['dev-utils', 'codemod'].includes(name));

const roots = packages.map((name) => `<rootDir>/packages/${name}/src`);
roots.push('<rootDir>/packages/codemod');

const docsSrc = '<rootDir>/packages/documentation/src';

const isWatchMode = /(-w|--watch)(\s?.+)?$/.test(process.argv);

let collectCoverageFrom = [];
if (!isWatchMode) {
  collectCoverageFrom = [
    '<rootDir>/packages/*/src/**/*.{ts,tsx}',
    // internal usage and don't matter for the library coverage reports
    '!<rootDir>/packages/{dev-utils,documentation,material-icons,react-md}/src/**/*',
    '!<rootDir>/packages/codemod/**/*',
    // these are generated files
    '!<rootDir>/packages/*/src/scssVariables.ts',
    // index.ts files are always `export * from "./fileOrFolder"`
    '!<rootDir>/packages/**/index.ts',
  ];
}

module.exports = {
  preset: 'ts-jest',
  testEnvironment: 'jsdom',
  roots,
  globals: {
    'ts-jest': {
      tsconfig: './tsconfig.test.json',
    },
  },
  setupFilesAfterEnv: ['<rootDir>/testSetup/init.js'],
  transform: {
    '\\.svg$': '<rootDir>/testSetup/inlineSvgTransformer.js',
    '\\.(jpg|jpeg|png|gif|eot|otf|webp|ttf|woff|woff2|mp4|webm|wav|mp3|m4a|aac|oga)$':
      '<rootDir>/testSetup/fileTransformer.js',
  },
  moduleNameMapper: {
    '\\.scss$': 'identity-obj-proxy',
    '^constants/(.*)$': `${docsSrc}/constants/$1`,
    '^components/(.*)$': `${docsSrc}/components/$1`,
    '^hooks/(.*)$': `${docsSrc}/hooks/$1`,
    '^icons/(.*)$': `${docsSrc}/icons/$1`,
    '^pages/(.*)$': `${docsSrc}/pages/$1`,
    '^utils/(.*)$': `${docsSrc}/utils/$1`,
  },
  collectCoverageFrom,
  watchPlugins: [
    'jest-watch-typeahead/filename',
    'jest-watch-typeahead/testname',
  ],
};

{
  "packages": ["packages/*"],
  "command": {
    "version": {
      "message": "chore(release): publish %s",
      "conventionalCommits": true
    }
  },
  "ignoreChanges": ["**/__tests__/**", "**/*.md"],
  "changelogPreset": {
    "name": "./changelog.config.js"
  },
  "useWorkspaces": true,
  "npmClient": "yarn",
  "version": "5.1.6"
}

import resolve from '@rollup/plugin-node-resolve';
import commonjs from '@rollup/plugin-commonjs';
import replace from '@rollup/plugin-replace';
import { terser } from 'rollup-plugin-terser';

const rmdPkg = 'packages/react-md';
const prefix = `${rmdPkg}/dist/umd`;

function createConfig(type) {
  const globals = {
    react: 'React',
    'react-dom': 'ReactDOM',
  };

  let fileNameSuffix;
  switch (type) {
    case 'svg':
      fileNameSuffix = '-with-svg-icons';
      break;
    case 'font':
      fileNameSuffix = '-with-font-icons';
      break;
    default:
      fileNameSuffix = '';
  }

  return {
    input: `${rmdPkg}/src/${type}.ts`,
    output: [
      {
        file: `${prefix}/react-md${fileNameSuffix}.development.js`,
        name: 'ReactMD',
        format: 'umd',
        globals,
        sourcemap: true,
      },
      {
        file: `${prefix}/react-md${fileNameSuffix}.production.min.js`,
        name: 'ReactMD',
        format: 'umd',
        globals,
        plugins: [terser()],
      },
    ],
    onwarn: (warning, warn) => {
      if (warning.code === 'THIS_IS_UNDEFINED') {
        return;
      }

      warn(warning);
    },
    external: ['react', 'react-dom'],
    plugins: [
      resolve(),
      commonjs(),
      replace({
        'process.env.NODE_ENV': JSON.stringify('production'),
      }),
    ],
  };
}

module.exports = ['rollup', 'svg', 'font'].map(createConfig);

