		"keybindings": [
			{
				"key": "ctrl+shift+space",
				"command": "issue.suggestRefresh",
				"when": "suggestWidgetVisible"
			},
			{
				"key": "ctrl+s",
				"mac": "cmd+s",
				"command": "issue.createIssueFromFile",
				"when": "resourceScheme == newIssue && config.files.autoSave != off"
			},
			{
				"key": "ctrl+enter",
				"mac": "cmd+enter",
				"command": "issue.createIssueFromFile",
				"when": "resourceScheme == newIssue"
