{
  "extends": [
    "eslint:recommended",
    "plugin:@typescript-eslint/recommended",
    "plugin:react/recommended",
    "plugin:react-hooks/recommended",
    "eslint-config-airbnb",
    "plugin:storybook/recommended",
    "plugin:prettier/recommended"
  ],
  "env": {
    "browser": true,
    "node": true,
    "jest": true
  },
  "parser": "@typescript-eslint/parser",
  "plugins": ["react", "@typescript-eslint", "eslint-comments"],
  "rules": {
    "eslint-comments/no-unused-disable": "error",
    "prettier/prettier": [
      "error",
      {
        "printWidth": 120,
        "semi": true,
        "singleQuote": true,
        "tabWidth": 2,
        "useTabs": false,
        "trailingComma": "all",
        "arrowParens": "avoid",
        "endOfLine": "auto"
      }
    ],
    "import/extensions": "off",
    "valid-jsdoc": [
      "error",
      {
        "requireReturnType": false,
        "requireParamType": false
      }
    ],
    "react/no-did-update-set-state": "warn",
    "react/jsx-no-constructed-context-values": "warn",
    "react/no-unused-class-component-methods": "off",
    "react/sort-comp": "off",
    "react/jsx-uses-react": "error",
    "react/jsx-uses-vars": "error",
    "react/react-in-jsx-scope": "error",
    "react/jsx-filename-extension": ["error", { "extensions": [".ts", ".tsx", ".js", ".jsx"] }],

    "no-var": "error",
    "react/jsx-no-bind": "error",

    "no-unused-vars": "off",
    "one-var": ["error", { "uninitialized": "always" }],
    "one-var-declaration-per-line": ["error", "initializations"],
    "no-plusplus": "off",
    "no-bitwise": "off",
    // We have typescript, we don't need prop-types
    "react/prop-types": "off",
    "react/no-unused-prop-types": "off",
    "class-methods-use-this": "error",
    "import/no-extraneous-dependencies": [
      "error",
      {
        "devDependencies": true
      }
    ],
    "max-len": ["warn", { "code": 150, "ignoreComments": true }],
    "prefer-destructuring": "error",
    "object-curly-newline": "off",
    "function-paren-newline": "off",
    "react/require-default-props": "off",
    "no-restricted-globals": "error",
    "react/default-props-match-prop-types": "off",
    "react/function-component-definition": "off",
    "react/no-array-index-key": "warn",
    "no-multi-assign": "off",
    "react/no-access-state-in-setstate": "off",
    "react/destructuring-assignment": ["off"],
    "react/jsx-closing-tag-location": "error",
    "react/no-unused-state": "error",
    "react/prefer-stateless-function": "error",
    "react/jsx-props-no-spreading": "off",
    "react/state-in-constructor": "off",
    "react/static-property-placement": "off",
    "import/prefer-default-export": "off",
    "import/no-named-as-default": "error",
    "import/no-cycle": "error",
    "import/no-default-export": "error",
    "import/no-import-module-exports": "error",
    "import/no-relative-packages": "error",
    "no-use-before-define": "off",
    "@typescript-eslint/no-use-before-define": ["error"],
    "@typescript-eslint/no-explicit-any": "off",
    "@typescript-eslint/explicit-module-boundary-types": "off",
    "@typescript-eslint/ban-types": "off",
    "@typescript-eslint/no-unused-vars": [
      "error",
      {
        "args": "after-used",
        "ignoreRestSiblings": true,
        "argsIgnorePattern": "^_"
      }
    ],
    "no-restricted-imports": [
      "error",
      {
        "paths": [
          {
            "name": "lodash",
            "message": "Import [module] from lodash/[module] instead"
          }
        ]
      }
    ]
  },
  "overrides": [
    {
      "files": ["./test/**/*.tsx"],
      "rules": {
        "react/jsx-no-useless-fragment": "off"
      }
    },
    {
      "files": ["./storybook/**/*.stories.@(ts|tsx)", "./storybook/**/*.@(ts|tsx)", "*.js"],
      "rules": {
        "no-useless-constructor": "off",
        "import/no-default-export": "off",
        "react-hooks/rules-of-hooks": "off"
      }
    },
    {
      "files": [
        "src/state/*Slice.ts",
        "test/**/*.spec.tsx"
      ],
      // param-reassign is allowed in slices following Redux recommendation: https://redux-toolkit.js.org/usage/immer-reducers#linting-state-mutations
      "rules": { "no-param-reassign": ["error", { "props": false }] }
    }
  ],
  "settings": {
    "import/resolver": {
      "node": {
        "extensions": [".js", ".jsx", ".ts", ".tsx"]
      }
    }
  }
}

