const plugins = [
    '@babel/plugin-transform-typescript',
    'transform-class-properties',
    ['@babel/transform-template-literals', {
        'loose': true
    }],
    '@babel/transform-literals',
    '@babel/transform-block-scoped-functions',
    ['@babel/plugin-transform-classes', {
        'loose': true
    }],
    '@babel/transform-sticky-regex',
    '@babel/transform-unicode-regex',
    '@babel/transform-block-scoping',
    ['@babel/transform-runtime', {
        'regenerator': true
    }],
    '@babel/proposal-class-properties'
];

let presets = [
    [
        '@babel/typescript',
        {
            rewriteImportExtensions: true,
            loose: true,
            modules: false
        }
    ]
];

// console.log('babel: NODE_ENV: ' + process.env['NODE_ENV']);

if (process.env['NODE_ENV'] === 'es5') {
    presets = [
        [
            '@babel/typescript',
            {
                rewriteImportExtensions: true,
                loose: true,
                targets: {
                    edge: '107',
                    firefox: '107',
                    chrome: '108',
                    safari: '16.2'
                },
                useBuiltIns: false
            }]
    ];
    plugins.unshift('@babel/plugin-transform-modules-commonjs');
}

module.exports = {
    presets,
    plugins
};

{
    // @link https://iamturns.com/typescript-babel/
    "compilerOptions": {
        // Target latest version of ECMAScript.
        "target": "es2022",
        // Search under node_modules for non-relative imports.
        // https://www.youtube.com/watch?v=H91aqUHn8sE
        "moduleResolution": "node",
        "module": "es2022",
        // Process & infer types from .js files.
        "allowJs": false,
        // Enable strictest settings like strictNullChecks & noImplicitAny.
        "strict": true,
        // Disallow features that require
        // cross-file information for emit.
        "isolatedModules": false,
        // Import non-ES modules as default imports.
        "esModuleInterop": true,
        "outDir": "./dist/typings",
        "declaration": true,
        /**
        * @link https://spblog.net/post/2018/10/26/TypeScript-Tips-How-to-reduce-the-size-of-a-bundle
        */
        "importHelpers": true,
        "typeRoots": [
            "./src/types/modules"
        ],
        "types": [],
        "noEmit": true,
        "allowImportingTsExtensions": true
    },
    "formatCodeOptions": {
        "indentSize": 2,
        "tabSize": 2
    },
    "include": [
        "test",
        "src",
        "docs-src"
    ],
    "exclude": [
        "node_modules",
        "**/node_modules/*",
        "dist",
        "test/tutorials/*",
        "examples",
        "docs",
        "docs-src",
        "test_tmp"
    ],
    "compileOnSave": false
}
