{
  "ignoreRules": ["false-esm", "cjs-resolves-to-esm"]
}

{
  "parser": "@typescript-eslint/parser",
  "plugins": ["@typescript-eslint", "import", "local-rules"],
  "env": {
    "browser": true,
    "node": true,
    "es2021": true
  },
  "parserOptions": {
    "ecmaVersion": "latest"
  },
  "settings": {
    "import/parsers": {
      "@typescript-eslint/parser": [".ts", ".tsx"]
    },
    "import/resolver": {
      "typescript": {
        "alwaysTryTypes": true
      }
    }
  },
  "overrides": [
    {
      "files": ["**/*.ts", "**/*.tsx"],
      "excludedFiles": ["**/__tests__/**/*.*", "*.d.ts"],
      "extends": ["plugin:react-hooks/recommended"],
      "parserOptions": {
        "project": [
          "./tsconfig.json",
          "./scripts/codemods/data-masking/tsconfig.json"
        ]
      },
      "plugins": ["eslint-plugin-react-compiler"],
      "rules": {
        "react-compiler/react-compiler": "error",
        "@typescript-eslint/consistent-type-imports": [
          "error",
          {
            "prefer": "type-imports",
            "disallowTypeAnnotations": false,
            "fixStyle": "separate-type-imports"
          }
        ],
        "@typescript-eslint/consistent-type-exports": ["error"],
        "@typescript-eslint/no-import-type-side-effects": "error",
        "@typescript-eslint/ban-types": [
          "error",
          {
            "types": {
              "GraphQLError": {
                "message": "Use GraphQLFormattedError instead",
                "fixWith": "GraphQLFormattedError"
              },
              "ExecutionResult": {
                "message": "Use FormattedExecutionResult instead",
                "fixWith": "FormattedExecutionResult"
              }
            },
            "extendDefaults": false
          }
        ],
        "no-restricted-syntax": [
          "error",
          {
            "selector": "ImportDeclaration[source.value='react'][importKind!='type'] :matches(ImportSpecifier, ImportDefaultSpecifier)",
            "message": "Please only use the namespace import syntax (`import * as React from 'react'`) for React imports!"
          }
        ],
        "import/consistent-type-specifier-style": ["error", "prefer-top-level"],
        "import/extensions": [
          "error",
          "always",
          {
            "ignorePackages": true,
            "checkTypeImports": true
          }
        ],
        "local-rules/require-using-disposable": "error"
      }
    },
    {
      "files": [
        "**/__tests__/**/*.[jt]s",
        "**/__tests__/**/*.[jt]sx",
        "**/?(*.)+(test).[jt]s",
        "**/?(*.)+(test).[jt]sx"
      ],
      "extends": ["plugin:testing-library/react"],
      "parserOptions": {
        "project": "./tsconfig.tests.json"
      },
      "rules": {
        "testing-library/prefer-user-event": "error",
        "testing-library/no-wait-for-multiple-assertions": "off",
        "local-rules/require-using-disposable": "error",
        "local-rules/require-disable-act-environment": "error",
        "local-rules/forbid-act-in-disabled-act-environment": "error",
        "@typescript-eslint/no-floating-promises": "warn"
      }
    }
  ],
  "rules": {
    "import/no-unresolved": "error"
  }
}

const limits = require("./.size-limits.json");

const checks = [
  {
    path: "dist/apollo-client.min.cjs",
  },
  {
    path: "dist/main.cjs",
    import: "{ ApolloClient, InMemoryCache, HttpLink }",
  },
  {
    path: "dist/index.js",
    import: "{ ApolloClient, InMemoryCache, HttpLink }",
  },
  ...[
    "ApolloProvider",
    "useQuery",
    "useLazyQuery",
    "useMutation",
    "useSubscription",
    "useSuspenseQuery",
    "useBackgroundQuery",
    "useLoadableQuery",
    "useReadQuery",
    "useFragment",
  ].map((name) => ({ path: "dist/react/index.js", import: `{ ${name} }` })),
]
  .map((config) => ({
    ...config,
    name:
      config.name || config.import ?
        `import ${config.import} from "${config.path}"`
      : config.path,
    // newer versions of size-limit changed to brotli as a default
    // we'll stay on gzip for now, so results are easier to compare
    gzip: true,
    ignore: [
      ...(config.ignore || []),
      "rehackt",
      "react",
      "react-dom",
      "@graphql-typed-document-node/core",
      "@wry/caches",
      "@wry/context",
      "@wry/equality",
      "@wry/trie",
      "graphql-tag",
      "hoist-non-react-statics",
      "optimism",
      "prop-types",
      "response-iterator",
      "symbol-observable",
      "ts-invariant",
      "tslib",
      "zen-observable-ts",
    ],
  }))
  .flatMap((value) =>
    value.path == "dist/apollo-client.min.cjs" ?
      value
    : [
        value,
        {
          ...value,
          name: `${value.name} (production)`,
          modifyEsbuildConfig(config) {
            config.define = {
              "globalThis.__DEV__": `false`,
            };
            return config;
          },
        },
      ]
  )
  .map((value) => {
    value.limit = limits[value.name];
    return value;
  });

module.exports = checks;

