/**
 * Sass configuration file used by Parcel.js for running local demos.
 */
const path = require('path')
const glob = require('glob');

const CWD = process.cwd()
const includePaths = glob.sync(path.join(CWD, 'packages/mdc-*/node_modules'));

module.exports = {
  "includePaths": includePaths,
}

{
  "spec_dir": ".",
  "spec_files": [
    "packages/**/test/*.scss.test.ts"
  ],
  "helpers": [
    "testing/ts-node.register.js",
    "testing/featuretargeting/sass-test-compile.helper.ts"
  ],
  "stopSpecOnExpectationFailure": false
}

