module.exports = {
  extends: ['@commitlint/config-conventional'],
  ignores: [commit => commit.includes('[skip ci]')],
  rules: {
    'scope-enum': [
      2,
      'always',
      [
        '2d',
        'core',
        'create',
        'docs',
        'e2e',
        'examples',
        'ffmpeg',
        'legacy',
        'player',
        'ui',
        'vite-plugin',
      ],
    ],
  },
};

{
  "$schema": "https://developer.microsoft.com/json-schemas/tsdoc/v0/tsdoc.schema.json",
  "tagDefinitions": [
    {
      "tagName": "@module",
      "syntaxKind": "modifier"
    },
    {
      "tagName": "@ignore",
      "syntaxKind": "modifier"
    },
    {
      "tagName": "@category",
      "syntaxKind": "modifier"
    },
    {
      "tagName": "@preview",
      "syntaxKind": "block"
    }
  ],
  "supportForTags": {
    "@packageDocumentation": true,
    "@module": true,
    "@category": true,
    "@preview": true,
    "@param": true,
    "@deprecated": true,
    "@returns": true,
    "@remarks": true,
    "@example": true,
    "@link": true,
    "@internal": true,
    "@eventProperty": true,
    "@typeParam": true,
    "@ignore": true,
    "@inheritDoc": true,
    "@defaultValue": true,
    "@see": true,
    "@experimental": true
  }
}
