{
  "ecmaVersion": "es5",
  "modules": "false",
  "files": [
    "./dist/**/*.js"
  ]
}

module.exports = {
  env: {
    browser: true,
    commonjs: true,
    es6: true,
  },
  globals: {
    // Allowed globals
    console: true,

    // Compile-time defines
    __VERSION__: true,
    __USE_SUBTITLES__: true,
    __USE_ALT_AUDIO__: true,
    __USE_EME_DRM__: true,
    __USE_CMCD__: true,
    __USE_CONTENT_STEERING__: true,
    __USE_VARIABLE_SUBSTITUTION__: true,
    __USE_M2TS_ADVANCED_CODECS__: true,
    __USE_MEDIA_CAPABILITIES__: true,
    __USE_INTERSTITALS__: true,
  },
  // see https://github.com/standard/eslint-config-standard
  // 'prettier' (https://github.com/prettier/eslint-config-prettier) must be last
  extends: ['eslint:recommended', 'prettier'],
  parser: '@typescript-eslint/parser',
  parserOptions: {
    sourceType: 'module',
    project: './tsconfig.json',
  },
  plugins: ['@typescript-eslint', 'import'],
  rules: {
    'no-restricted-globals': [
      2,
      {
        name: 'window',
        message:
          'Use `self` instead of `window` to access the global context everywhere (including workers).',
      },
      {
        name: 'SourceBuffer',
        message: 'Use `self.SourceBuffer`',
      },
      {
        name: 'setTimeout',
        message: 'Use `self.setTimeout`',
      },
      {
        name: 'setInterval',
        message: 'Use `self.setInterval`',
      },
    ],

    'no-restricted-properties': [
      2,
      { property: 'findIndex' }, // Intended to block usage of Array.prototype.findIndex
      { property: 'find' }, // Intended to block usage of Array.prototype.find
    ],

    'import/first': 1,
    'no-var': 1,
    'no-empty': 1,
    'no-unused-vars': 'warn',
    'no-console': [
      1,
      {
        allow: ['assert'],
      },
    ],
    'no-fallthrough': 1,
    'no-case-declarations': 2,
    'no-self-assign': 1,
    'new-cap': 1,
    'no-undefined': 0,
    'no-global-assign': 2,
    'prefer-const': 2,
    'dot-notation': 2,
    'no-void': 2,
    'no-useless-catch': 2,
    'no-prototype-builtins': 0,
  },
  overrides: [
    {
      parserOptions: {
        project: ['./tsconfig.json'],
      },
      files: ['*.ts'],
      rules: {
        'no-unused-vars': 0,
        'no-undef': 0,
        'no-use-before-define': 'off',
        'import/order': [
          'warn',
          {
            alphabetize: {
              order: 'asc',
            },
            groups: [
              'builtin',
              'external',
              'internal',
              ['sibling', 'index'],
              'parent',
              'type',
            ],
            'newlines-between': 'never',
          },
        ],
        'sort-imports': [
          'error',
          {
            ignoreCase: true,
            ignoreDeclarationSort: true,
          },
        ],
        '@typescript-eslint/no-unused-vars': [
          'warn',
          {
            args: 'none',
          },
        ],
        '@typescript-eslint/prefer-optional-chain': 2,
        '@typescript-eslint/consistent-type-assertions': [
          2,
          {
            assertionStyle: 'as',
            objectLiteralTypeAssertions: 'never',
          },
        ],
        '@typescript-eslint/consistent-type-imports': 'error',
        '@typescript-eslint/no-import-type-side-effects': 'error',
        '@typescript-eslint/no-restricted-imports': 'error',
      },
    },
  ],
};

{
  "commitMessage": "Release %s",
  "tagName": "v%s"
}

{
  "singleQuote": true
}

{
  "$schema": "https://developer.microsoft.com/json-schemas/api-extractor/v7/api-extractor.schema.json",
  "mainEntryPointFilePath": "<projectFolder>/lib/hls.d.ts",
  "bundledPackages": [],
  "compiler": {
    "tsconfigFilePath": "<projectFolder>/tsconfig-lib.json"
  },
  "apiReport": {
    "enabled": true,
    "reportFolder": "<projectFolder>/api-extractor/report",
    "reportTempFolder": "<projectFolder>/api-extractor/report-temp"
  },
  "docModel": {
    "enabled": true,
    "apiJsonFilePath": "<projectFolder>/api-extractor/<unscopedPackageName>.api.json"
  },
  "dtsRollup": {
    "enabled": true,
    "untrimmedFilePath": "<projectFolder>/dist/hls.d.ts"
  },
  "tsdocMetadata": {
    "enabled": false
  },
  "newlineKind": "lf",
  "messages": {
    "compilerMessageReporting": {
      "default": {
        "logLevel": "warning",
        "addToApiReportFile": true
      }
    },
    "extractorMessageReporting": {
      "default": {
        "logLevel": "warning",
        "addToApiReportFile": true
      }
    },
    "tsdocMessageReporting": {
      "default": {
        "logLevel": "none",
        "addToApiReportFile": false
      }
    }
  }
}

const path = require('path');
const importHelper = require('@babel/helper-module-imports');
const { nodeResolve } = require('@rollup/plugin-node-resolve');
const commonjs = require('@rollup/plugin-commonjs');
const babel = require('@rollup/plugin-babel');
const alias = require('@rollup/plugin-alias');
const replace = require('@rollup/plugin-replace');
const terser = require('@rollup/plugin-terser');
const istanbul = require('rollup-plugin-istanbul');
const fs = require('fs');

const pkgJson = JSON.parse(
  fs.readFileSync('./package.json', { encoding: 'utf-8' }),
);

const BUILD_TYPE = {
  full: 'full',
  light: 'light',
};

const FORMAT = {
  umd: 'umd',
  esm: 'esm',
  iife: 'iife',
};

const buildTypeToOutputName = {
  full: `hls`,
  light: `hls.light`,
};

/* Allow to customize builds through env-vars */
// eslint-disable-next-line no-undef
const env = process.env;

const addSubtitleSupport = !!env.SUBTITLE || !!env.USE_SUBTITLES;
const addAltAudioSupport = !!env.ALT_AUDIO || !!env.USE_ALT_AUDIO;
const addEMESupport = !!env.EME_DRM || !!env.USE_EME_DRM;
const addCMCDSupport = !!env.CMCD || !!env.USE_CMCD;
const addContentSteeringSupport =
  !!env.CONTENT_STEERING || !!env.USE_CONTENT_STEERING;
const addVariableSubstitutionSupport =
  !!env.VARIABLE_SUBSTITUTION || !!env.USE_VARIABLE_SUBSTITUTION;
const addM2TSAdvancedCodecSupport =
  !!env.M2TS_ADVANCED_CODECS || !!env.USE_M2TS_ADVANCED_CODECS;
const addMediaCapabilitiesSupport =
  !!env.MEDIA_CAPABILITIES || !!env.USE_MEDIA_CAPABILITIES;
const addInterstitialSupport = !!env.INTERSTITALS || !!env.USE_INTERSTITALS;

const shouldBundleWorker = (format) => format !== FORMAT.esm;

const buildConstants = (type, additional = {}) => ({
  preventAssignment: true,
  values: {
    __VERSION__: JSON.stringify(pkgJson.version),
    __USE_SUBTITLES__: JSON.stringify(
      type === BUILD_TYPE.full || addSubtitleSupport,
    ),
    __USE_ALT_AUDIO__: JSON.stringify(
      type === BUILD_TYPE.full || addAltAudioSupport,
    ),
    __USE_EME_DRM__: JSON.stringify(type === BUILD_TYPE.full || addEMESupport),
    __USE_CMCD__: JSON.stringify(type === BUILD_TYPE.full || addCMCDSupport),
    __USE_CONTENT_STEERING__: JSON.stringify(
      type === BUILD_TYPE.full || BUILD_TYPE.light || addContentSteeringSupport,
    ),
    __USE_VARIABLE_SUBSTITUTION__: JSON.stringify(
      type === BUILD_TYPE.full || addVariableSubstitutionSupport,
    ),
    __USE_M2TS_ADVANCED_CODECS__: JSON.stringify(
      type === BUILD_TYPE.full || addM2TSAdvancedCodecSupport,
    ),
    __USE_MEDIA_CAPABILITIES__: JSON.stringify(
      type === BUILD_TYPE.full || addMediaCapabilitiesSupport,
    ),
    __USE_INTERSTITALS__: JSON.stringify(
      type === BUILD_TYPE.full || addInterstitialSupport,
    ),

    ...additional,
  },
});

const buildOnLog = ({ allowCircularDeps } = {}) => {
  return (level, log, handler) => {
    if (allowCircularDeps && log.code === 'CIRCULAR_DEPENDENCY') return;

    if (level === 'warn') {
      // treat warnings as errors
      handler('error', log);
    } else {
      handler(level, log);
    }
  };
};

const workerFnBanner = '(function __HLS_WORKER_BUNDLE__(__IN_WORKER__){';
const workerFnFooter = '})(false);';

const extensions = ['.ts', '.js'];

const babelPresetEnvTargets = {
  chrome: '47',
  firefox: '51',
  safari: '8',
  ios: '8',
  android: '4',
  samsung: '5',
  edge: '14',
};

const babelTsWithPresetEnvTargets = ({ targets, stripConsole }) =>
  babel({
    extensions,
    babelHelpers: 'bundled',
    exclude: /node_modules\/(?!(@svta)\/).*/,
    assumptions: {
      noDocumentAll: true,
      noClassCalls: true,
    },
    presets: [
      [
        '@babel/preset-typescript',
        {
          optimizeConstEnums: true,
        },
      ],
      [
        '@babel/preset-env',
        {
          loose: true,
          targets,
          bugfixes: true,
        },
      ],
    ],
    plugins: [
      [
        '@babel/plugin-proposal-class-properties',
        {
          loose: true,
        },
      ],
      '@babel/plugin-proposal-object-rest-spread',
      {
        visitor: {
          CallExpression: function (espath) {
            if (espath.get('callee').matchesPattern('Number.isFinite')) {
              espath.node.callee = importHelper.addNamed(
                espath,
                'isFiniteNumber',
                path.resolve('src/polyfills/number'),
              );
            } else if (
              espath.get('callee').matchesPattern('Number.isSafeInteger')
            ) {
              espath.node.callee = importHelper.addNamed(
                espath,
                'isSafeInteger',
                path.resolve('src/polyfills/number'),
              );
            } else if (
              espath.get('callee').matchesPattern('Number.MAX_SAFE_INTEGER')
            ) {
              espath.node.callee = importHelper.addNamed(
                espath,
                'MAX_SAFE_INTEGER',
                path.resolve('src/polyfills/number'),
              );
            }
          },
        },
      },
      ['@babel/plugin-transform-object-assign'],
      ['@babel/plugin-proposal-optional-chaining'],

      ...(stripConsole
        ? [
            [
              // Strip console.assert statements from build targets
              'transform-remove-console',
              {
                exclude: ['log', 'warn', 'error'],
              },
            ],
          ]
        : []),
    ],
  });

const buildBabelLegacyBrowsers = ({ stripConsole }) =>
  babelTsWithPresetEnvTargets({
    targets: babelPresetEnvTargets,
    stripConsole,
  });

const buildBabelEsm = ({ stripConsole }) =>
  babelTsWithPresetEnvTargets({ targets: { esmodules: true }, stripConsole });

const basePlugins = [
  nodeResolve({
    extensions,
    browser: true,
    preferBuiltins: false,
  }),
  commonjs({ transformMixedEsModules: true }),
];

function getAliasesForLightDist(format) {
  const emptyFile = format === 'esm' ? 'empty-es.js' : 'empty.js';

  let aliases = {};

  if (!addEMESupport) {
    aliases = {
      ...aliases,
      './controller/eme-controller': `./${emptyFile}`,
      './utils/mediakeys-helper': `./${emptyFile}`,
      '../utils/mediakeys-helper': `../${emptyFile}`,
    };
  }

  if (!addCMCDSupport) {
    aliases = { ...aliases, './controller/cmcd-controller': `./${emptyFile}` };
  }

  if (!addSubtitleSupport) {
    aliases = {
      ...aliases,
      './utils/cues': `./${emptyFile}`,
      './controller/timeline-controller': `./${emptyFile}`,
      './controller/subtitle-track-controller': `./${emptyFile}`,
      './controller/subtitle-stream-controller': `./${emptyFile}`,
    };
  }

  if (!addAltAudioSupport) {
    aliases = {
      ...aliases,
      './controller/audio-track-controller': `./${emptyFile}`,
      './controller/audio-stream-controller': `./${emptyFile}`,
    };
  }

  if (!addVariableSubstitutionSupport) {
    aliases = {
      ...aliases,
      './utils/variable-substitution': `./${emptyFile}`,
      '../utils/variable-substitution': `../${emptyFile}`,
    };
  }

  if (!addM2TSAdvancedCodecSupport) {
    aliases = {
      ...aliases,
      './ac3-demuxer': `../${emptyFile}`,
      './video/hevc-video-parser': `../${emptyFile}`,
    };
  }

  if (!addMediaCapabilitiesSupport) {
    aliases = {
      ...aliases,
      '../utils/mediacapabilities-helper': `../${emptyFile}`,
    };
  }

  if (!addInterstitialSupport) {
    aliases = {
      ...aliases,
      './controller/interstitials-controller': './empty.js',
      './controller/interstitial-player': './empty.js',
      './controller/interstitials-schedule': './empty.js',
      './interstitial-player': './empty.js',
      './interstitials-schedule': './empty.js',
      './interstitial-event': './empty.js',
      '../controller/interstitial-player': './empty.js',
      '../controller/interstitials-schedule': './empty.js',
      '../loader/interstitial-event': './empty.js',
      '../loader/interstitial-asset-list': './empty.js',
    };
  }

  return aliases;
}

const buildRollupConfig = ({
  type,
  minified,
  format,
  allowCircularDeps,
  includeCoverage,
  sourcemap = true,
  outputFile = null,
  input = './src/exports-default.ts',
}) => {
  const outputName = buildTypeToOutputName[type];
  const extension = format === FORMAT.esm ? 'mjs' : 'js';

  return {
    input,
    onLog: buildOnLog({ allowCircularDeps }),
    output: {
      name: 'Hls',
      file: outputFile
        ? outputFile
        : minified
          ? `./dist/${outputName}.min.${extension}`
          : `./dist/${outputName}.${extension}`,
      format,
      banner: shouldBundleWorker(format) ? workerFnBanner : null,
      footer: shouldBundleWorker(format) ? workerFnFooter : null,
      sourcemap,
      sourcemapFile: minified
        ? `${outputName}.${extension}.min.map`
        : `${outputName}.${extension}.map`,
    },
    plugins: [
      ...basePlugins,
      replace(buildConstants(type)),
      ...(!shouldBundleWorker(format)
        ? [alias({ entries: { './transmuxer-worker': '../empty.js' } })]
        : []),
      ...(type === BUILD_TYPE.light
        ? [alias({ entries: getAliasesForLightDist(format) })]
        : []),
      ...(format === 'esm'
        ? [buildBabelEsm({ stripConsole: true })]
        : [buildBabelLegacyBrowsers({ stripConsole: true })]),
      ...(minified ? [terser()] : []),
      ...(includeCoverage
        ? [istanbul({ exclude: ['tests/**/*', 'node_modules/**/*'] })]
        : []),
    ],
  };
};

const configs = Object.entries({
  full: buildRollupConfig({
    type: BUILD_TYPE.full,
    format: FORMAT.umd,
    minified: false,
  }),
  fullMin: buildRollupConfig({
    type: BUILD_TYPE.full,
    format: FORMAT.umd,
    minified: true,
  }),
  fullEsm: buildRollupConfig({
    input: './src/exports-named.ts',
    type: BUILD_TYPE.full,
    format: FORMAT.esm,
    minified: false,
  }),
  light: buildRollupConfig({
    type: BUILD_TYPE.light,
    format: FORMAT.umd,
    minified: false,
  }),
  lightMin: buildRollupConfig({
    type: BUILD_TYPE.light,
    format: FORMAT.umd,
    minified: true,
  }),
  lightEsm: buildRollupConfig({
    input: './src/exports-named.ts',
    type: BUILD_TYPE.light,
    format: FORMAT.esm,
    minified: false,
  }),
  worker: {
    input: './src/demux/transmuxer-worker.ts',
    onLog: buildOnLog(),
    output: {
      name: 'HlsWorker',
      file: './dist/hls.worker.js',
      format: FORMAT.iife,
      sourcemap: true,
      sourcemapFile: 'hls.worker.js.map',
    },
    plugins: [
      ...basePlugins,
      replace(
        buildConstants(BUILD_TYPE.full, {
          __IN_WORKER__: JSON.stringify(true),
        }),
      ),
      buildBabelLegacyBrowsers({ stripConsole: true }),
      terser(),
    ],
  },
  demo: {
    input: './demo/main.js',
    onLog: buildOnLog(),
    output: {
      name: 'HlsDemo',
      file: './dist/hls-demo.js',
      format: FORMAT.umd,
      sourcemap: true,
      sourcemapFile: 'hls-demo.js.map',
    },
    watch: {
      clearScreen: false,
    },
    plugins: [
      ...basePlugins,
      replace({
        preventAssignment: true,
        values: {
          __CLOUDFLARE_PAGES__: JSON.stringify(
            env.CF_PAGES
              ? {
                  branch: env.CF_PAGES_BRANCH,
                  commitRef: env.CF_PAGES_COMMIT_SHA,
                }
              : null,
          ),
        },
      }),
      buildBabelLegacyBrowsers({ stripConsole: false }),
    ],
  },
});

module.exports = {
  BUILD_TYPE,
  FORMAT,
  configs,
  buildRollupConfig,
};

{
  "folders": [
    {
      "path": ".",
      "folder_exclude_patterns": [".git", "node_modules", "dist", "lib"],
      "file_exclude_patterns": [
        ".gitignore",
        "hls.js.sublime-project",
        "hls.js.sublime-workspace",
      ],
    },
  ],
}

const { buildRollupConfig, BUILD_TYPE, FORMAT } = require('./build-config');

// Do not add coverage for JavaScript debugging when running `test:unit:debug`
// eslint-disable-next-line no-undef
const includeCoverage = !process.env.DEBUG_UNIT_TESTS && !process.env.CI;

const rollupPreprocessor = buildRollupConfig({
  type: BUILD_TYPE.full,
  format: FORMAT.iife,
  minified: false,
  allowCircularDeps: true,
  includeCoverage,
  sourcemap: false,
  outputFile: 'karma-temp/tests.js',
});

// preprocess matching files before serving them to the browser
// available preprocessors: https://npmjs.org/browse/keyword/karma-preprocessor
const preprocessors = {
  './tests/index.js': ['rollup'],
};
// test results reporter to use
// possible values: 'dots', 'progress'
// available reporters: https://npmjs.org/browse/keyword/karma-reporter
const reporters = ['mocha'];
const coverageReporter = {
  reporters: [],
};

if (includeCoverage) {
  reporters.push('coverage');
  coverageReporter.reporters.push({ type: 'html', subdir: '.' });
}

module.exports = function (config) {
  config.set({
    // frameworks to use
    // available frameworks: https://npmjs.org/browse/keyword/karma-adapter
    frameworks: ['mocha', 'sinon-chai'],

    // list of files / patterns to load in the browser
    files: [
      {
        pattern: 'tests/index.js',
        watched: false,
      },
    ],

    // list of files to exclude
    exclude: [],

    preprocessors,
    coverageReporter,
    reporters,

    rollupPreprocessor,

    // web server port
    port: 9876,

    // enable / disable colors in the output (reporters and logs)
    colors: true,

    // level of logging
    // possible values: config.LOG_DISABLE || config.LOG_ERROR || config.LOG_WARN || config.LOG_INFO || config.LOG_DEBUG
    logLevel: config.LOG_INFO,

    // enable / disable watching file and executing tests whenever any file changes
    autoWatch: false,

    // start these browsers
    // available browser launchers: https://npmjs.org/browse/keyword/karma-launcher
    browsers: ['ChromeHeadless'],

    // Continuous Integration mode
    // if true, Karma captures browsers, runs the tests and exits
    singleRun: true,

    // Concurrency level
    // how many browser should be started simultaneous
    concurrency: 1,
  });
};

const micromatch = require('micromatch');
const prettier = require('prettier');

const addQuotes = (a) => `"${a}"`;

module.exports = async (allStagedFiles) => {
  const prettierSupportedExtensions = (
    await prettier.getSupportInfo()
  ).languages
    .map(({ extensions }) => extensions)
    .flat();

  const eslintFiles = micromatch(allStagedFiles, '**/*.{js,ts}');
  const prettierFiles = micromatch(
    allStagedFiles,
    prettierSupportedExtensions.map((extension) => `**/*${extension}`),
  );

  return [
    eslintFiles.length &&
      `eslint --cache --fix ${eslintFiles.map(addQuotes).join(' ')}`,
    prettierFiles.length &&
      `prettier --cache --write ${prettierFiles.map(addQuotes).join(' ')}`,
  ].filter(Boolean);
};

