{
  "env": {
    "node": true,
    "es6": true,
    "browser": true
  },
  "extends": [
    "eslint:recommended",
    "plugin:@typescript-eslint/recommended",
    "plugin:react/recommended",
    "plugin:react-hooks/recommended",
    "plugin:promise/recommended",
    // "plugin:import/recommended",
    "plugin:prettier/recommended"
  ],
  "parser": "@typescript-eslint/parser",
  "plugins": [
    "@typescript-eslint",
    "react",
    "react-hooks",
    "prettier",
    "promise",
    "jest-dom",
    "testing-library"
    //"import"
  ],
  "globals": {
    "sleep": true,
    "prettyFormat": true
  },
  "settings": {
    "react": {
      "version": "detect"
    }
  },
  "parserOptions": {
    "ecmaVersion": 11,
    "sourceType": "module",
    "ecmaFeatures": {
      "jsx": true
    },
    "project": "./tsconfig.json"
  },
  "rules": {
    "@typescript-eslint/no-this-alias": "off",
    "@typescript-eslint/ban-types": "off",
    "no-unused-vars": "off",
    "no-useless-catch": "off",
    "@typescript-eslint/no-unused-vars": "off",
    "no-empty-function": "off",
    "@typescript-eslint/no-empty-function": "off",
    "react/display-name": "off",
    "react/prop-types": "off",
    "no-explicit-any": "off",
    "@typescript-eslint/no-explicit-any": "off",
    "@typescript-eslint/ban-ts-comment": "off",
    "@typescript-eslint/no-var-requires": "off",
    "promise/always-return": "off"
  },
  "overrides": [
    {
      "files": [
        "**/e2e/**/*.{ts,js,tsx,jsx}",
        "**/__e2e__/**/*.{ts,js,tsx,jsx}"
      ],
      "rules": {
        "@typescript-eslint/no-floating-promises": "error"
      }
    }
  ]
}

{
  "singleQuote": true,
  "trailingComma": "all",
  "printWidth": 120,
  "overrides": [
    {
      "files": ".prettierrc",
      "options": { "parser": "json" }
    }
  ]
}

// @ts-ignore
const axios = require('axios');
const glob = require('glob');
const path = require('path');
const fs = require('fs/promises');
const lerna = require('./lerna.json');

const files = glob.sync(path.resolve(__dirname, './node_modules/@nocobase/**/package.json'));

(async () => {
  for (const file of files) {
    const content = await fs.readFile(file);
    const json = JSON.parse(content.toString());
    const url = `https://registry.npmmirror.com/${json.name}`;
    try {
      const response = await axios.get(url);
      const latest = response?.data?.['dist-tags']?.latest;
      if (latest !== lerna.version) {
        console.log(json.name, latest);
        console.log(`https://www.npmmirror.com/package/${json.name}`);

        const res = await fetch(`https://registry-direct.npmmirror.com/-/package/${json.name}/syncs`, {
          headers: {
            accept: '*/*',
            'accept-language': 'zh-CN,zh;q=0.9',
            priority: 'u=1, i',
            'sec-ch-ua': '"Chromium";v="124", "Google Chrome";v="124", "Not-A.Brand";v="99"',
            'sec-ch-ua-mobile': '?0',
            'sec-ch-ua-platform': '"macOS"',
            'sec-fetch-dest': 'empty',
            'sec-fetch-mode': 'cors',
            'sec-fetch-site': 'same-site',
            Referer: 'https://www.npmmirror.com/',
            'Referrer-Policy': 'strict-origin-when-cross-origin',
          },
          body: null,
          method: 'PUT',
        });
        // const response = await axios.put(`https://registry-direct.npmmirror.com/-/package/${json.name}/syncs`);
        console.log(await res.json());
        await new Promise((resolve) => {
          setTimeout(() => {
            resolve(null);
          }, 1000);
        });
      }
    } catch (error) {
      // ...
    }
  }
})();

{
  "version": "1.4.22",
  "npmClient": "yarn",
  "useWorkspaces": true,
  "npmClientArgs": [
    "--ignore-engines"
  ],
  "command": {
    "version": {
      "forcePublish": true,
      "exact": true,
      "message": "chore(versions): 😊 publish %s"
    }
  }
}

{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "target": "ES6",
    "module": "CommonJS"
  }
}
