import importPlugin from 'eslint-plugin-import';
import jsdoc from "eslint-plugin-jsdoc";
import unusedImports from "eslint-plugin-unused-imports";
import prettier from "eslint-plugin-prettier";
import globals from "globals";
import tsParser from "@typescript-eslint/parser";
import tseslint from 'typescript-eslint';
import stylistic from '@stylistic/eslint-plugin'
import oxlint from 'eslint-plugin-oxlint';

export default [{
    ignores: [
        ".git/",
        "**/*.d.ts",
        "**/*.test.ts",
        "**/*.js",
        "**/cdk.out/", "**/cdk.out/*", "**/cdk.out/**",
        "**/dist/", "**/dist/*", "**/dist/**",
        "**/node_modules/", "**/node_modules/*", "**/node_modules/**",
        "**/bp_modules/", "**/bp_modules/*", "**/bp_modules/**",
        "**/.botpress/", "**/.botpress/*", "**/.botpress/**",
        "**/gen/", "**/gen/*", "**/gen/**",
        "**/.turbo/", "**/.turbo/*", "**/.turbo/**",
        "**/.genenv/", "**/.genenv/*", "**/.genenv/**",
    ],
    files: ["**/*.{ts,tsx}"],
    plugins: {
        jsdoc,
        "unused-imports": unusedImports,
        '@stylistic': stylistic,
        "@typescript-eslint": tseslint.plugin,
        prettier,
        import: importPlugin
    },

    languageOptions: {
        globals: {
            ...globals.browser,
            ...globals.node,
        },

        parser: tsParser,
        ecmaVersion: 5,
        sourceType: "commonjs",

        parserOptions: {
            project: ["./tsconfig.json"],
            tsconfigRootDir: import.meta.dirname,
        },
    },

    rules: {
        ...prettier.configs.recommended.rules,

        complexity: ["off"],
        "max-lines-per-function": "off",
        "prefer-const": "warn",

        "@stylistic/member-delimiter-style": ["error", {
            multiline: {
                delimiter: "none",
                requireLast: true,
            },

            singleline: {
                delimiter: "semi",
                requireLast: false,
            },
        }],

        "@stylistic/quotes": ["error", "single", {
            avoidEscape: true,
        }],

        "@typescript-eslint/no-floating-promises": "error",
        "@typescript-eslint/no-misused-promises": "error",
        "@stylistic/semi": ["error", "never"],
        "@stylistic/type-annotation-spacing": "error",
        "@stylistic/brace-style": "off",
        "@stylistic/eol-last": "error",
        eqeqeq: ["error", "smart"],
        "@typescript-eslint/no-shadow": "off",

        "import/order": ["warn", {
            groups: [["builtin", "external"], "parent", "index", "sibling"],
            // TODO: Eventually enable this in the future for consistency
            // 'newlines-between': 'always',
            alphabetize: {
                order: "asc",
                caseInsensitive: true,
            },

            pathGroupsExcludedImportTypes: ["builtin"],
        }],

        "jsdoc/check-alignment": "error",
        "@stylistic/linebreak-style": ["error", "unix"],
        "@stylistic/no-trailing-spaces": "error",
        "object-shorthand": "error",
        "unused-imports/no-unused-imports": "error",

        "unused-imports/no-unused-vars": ["error", {
            vars: "all",
            varsIgnorePattern: "^_",
            args: "after-used",
            argsIgnorePattern: "^_",
        }],

        "@typescript-eslint/naming-convention": ["warn", {
            selector: "memberLike",
            modifiers: ["private"],
            format: ["camelCase"],
            leadingUnderscore: "require",
        }],

        "@typescript-eslint/explicit-member-accessibility": "warn",

        // Disable every rule already covered by oxlint:
        ...oxlint.buildFromOxlintConfigFile('./.oxlintrc.json')
            .map(config => config.rules)
            .reduce((acc, rules) => ({ ...acc, ...rules }), {}),
    },
}];

import { configDefaults, defineConfig } from 'vitest/config'

export default defineConfig({
  test: {
    exclude: [
      ...configDefaults.exclude,
      '**/*.utils.test.ts',
      'packages/client/tests/manual/**',
      'packages/chat-e2e/**',
    ],
  },
})
