{
  "root": true,
  "ignorePatterns": [
    "app-builds/**/*",
    "dist/**",
    "node_modules/**/*",
    "src/app/t.const.ts"
  ],
  "overrides": [
    {
      "files": ["*.ts"],
      "parser": "@typescript-eslint/parser",
      "excludedFiles": [],
      "parserOptions": {
        "ecmaVersion": 2020,
        "sourceType": "module",
        "createDefaultProgram": true
      },
      "extends": [
        "plugin:@typescript-eslint/recommended",
        "plugin:@angular-eslint/recommended",
        "plugin:prettier/recommended"
      ],
      "plugins": ["@typescript-eslint", "prettier", "prefer-arrow"],
      "rules": {
        "@typescript-eslint/no-explicit-any": 0,
        "@typescript-eslint/ban-ts-comment": 0,
        "@typescript-eslint/no-empty-function": 0,
        "@typescript-eslint/explicit-module-boundary-types": 0,
        "prettier/prettier": "error",
        "@angular-eslint/component-selector": ["off"],
        "@typescript-eslint/no-unused-vars": [
          "error",
          {
            "args": "none"
          }
        ],
        "@typescript-eslint/explicit-function-return-type": [
          "error",
          {
            "allowExpressions": true,
            "allowTypedFunctionExpressions": true,
            "allowHigherOrderFunctions": true,
            "allowDirectConstAssertionInArrowFunctions": true,
            "allowConciseArrowFunctionExpressionsStartingWithVoid": true
          }
        ],
        "@typescript-eslint/explicit-member-accessibility": [
          "off",
          {
            "accessibility": "explicit"
          }
        ],
        "arrow-parens": ["off", "always"],
        "import/order": "off",
        "@typescript-eslint/member-ordering": "off",
        "@typescript-eslint/no-inferrable-types": "off",
        "no-underscore-dangle": "off",
        "arrow-body-style": "off",
        "@angular-eslint/no-input-rename": "off",
        "@typescript-eslint/naming-convention": [
          "error",
          {
            "selector": "default",
            "format": ["camelCase", "snake_case", "UPPER_CASE", "PascalCase"],
            "leadingUnderscore": "allowSingleOrDouble",
            "trailingUnderscore": "allow",
            "filter": {
              "regex": "(should)|@tags",
              "match": false
            }
          },
          {
            "selector": "variable",
            "format": ["camelCase", "snake_case", "UPPER_CASE", "PascalCase"],
            "leadingUnderscore": "allowSingleOrDouble",
            "trailingUnderscore": "allow"
          },
          {
            "selector": "enum",
            "format": ["PascalCase", "UPPER_CASE"]
          },
          {
            "selector": "typeLike",
            "format": ["PascalCase"]
          }
        ],
        "prefer-const": "error",
        "@typescript-eslint/no-unused-expressions": "error",
        "@typescript-eslint/no-empty-interface": "error",
        "max-len": [
          "error",
          {
            "ignorePattern": "^import \\{.+;$",
            "ignoreRegExpLiterals": true,
            "ignoreStrings": true,
            "ignoreUrls": true,
            "code": 150
          }
        ],
        "id-blacklist": "error",
        "@typescript-eslint/member-delimiter-style": "error",
        "no-shadow": "off",
        "@typescript-eslint/no-shadow": ["error"],
        "comma-dangle": ["error", "always-multiline"],
        "no-mixed-operators": "error",
        "prefer-arrow/prefer-arrow-functions": "error",
        "@angular-eslint/directive-selector": [
          "error",
          {
            "type": "attribute",
            "prefix": "",
            "style": "camelCase"
          }
        ],
        "@typescript-eslint/ban-types": "error"
      }
    },
    {
      "files": ["*.html"],
      "extends": [
        "plugin:@typescript-eslint/recommended",
        "plugin:@angular-eslint/template/recommended",
        "prettier"
      ],
      "parserOptions": {
        "ecmaVersion": 2020,
        "sourceType": "module"
      },
      "processor": "@angular-eslint/template/extract-inline-html",
      "plugins": ["prettier"],
      "rules": {
        "@angular-eslint/template/no-negated-async": "off",
        "prettier/prettier": "error"
      }
    }
  ]
}

{
  "tabWidth": 2,
  "useTabs": false,
  "semi": true,
  "singleQuote": true,
  "quoteProps": "as-needed",
  "trailingComma": "all",
  "bracketSpacing": true,
  "singleAttributePerLine": true,
  "arrowParens": "always",
  "endOfLine": "auto",
  "printWidth": 90,
  "overrides": [
    {
      "files": "*.component.html",
      "options": {
        "parser": "angular"
      }
    },
    {
      "files": "*.html",
      "options": {
        "parser": "html"
      }
    }
  ]
}

/** @type {import('stylelint').Config} */
export default {
  extends: 'stylelint-config-recommended-scss',
  rules: {
    'no-empty-source': null,
    'block-no-empty': [true, { severity: 'warning' }],
    'no-descending-specificity': null,
    'font-family-no-missing-generic-family-keyword': [
      true,
      { ignoreFontFamilies: ['Material Icons'] },
    ],
    'selector-type-no-unknown': null, // stylelint doesn't recognize angular components
    'scss/comment-no-empty': null, // allow for double slash multiline comments
    'scss/load-no-partial-leading-underscore': null, // this project uses underscored files
    'scss/operator-no-newline-after': null, // prettier conficts with this rule
    'scss/operator-no-unspaced': null, // code style not error
    'scss/load-partial-extension': null, // it's code style not an error
    'scss/no-global-function-names': null, // to pass this a big migration is required, to be re-enabled later
    'selector-pseudo-element-no-unknown': [
      true,
      {
        ignorePseudoElements: ['ng-deep'],
      },
    ],
  },
};

{
  "$schema": "./node_modules/@angular/cli/lib/config/schema.json",
  "version": 1,
  "newProjectRoot": "projects",
  "projects": {
    "sp2": {
      "root": "",
      "sourceRoot": "src",
      "projectType": "application",
      "prefix": "",
      "schematics": {
        "@schematics/angular:component": {
          "style": "scss",
          "changeDetection": "OnPush"
        },
        "@ngrx/schematics:component": {
          "styleext": "scss",
          "changeDetection": "OnPush"
        }
      },
      "architect": {
        "build": {
          "builder": "@angular-devkit/build-angular:application",
          "options": {
            "aot": true,
            "outputPath": {
              "base": "dist"
            },
            "index": "src/index.html",
            "polyfills": ["src/polyfills.ts"],
            "tsConfig": "src/tsconfig.app.json",
            "preserveSymlinks": true,
            "assets": [
              "src/favicon.ico",
              "src/assets",
              "src/manifest.json",
              "src/static"
            ],
            "styles": ["src/styles.scss"],
            "scripts": [],
            "webWorkerTsConfig": "src/tsconfig.worker.json",
            "browser": "src/main.ts",
            "stylePreprocessorOptions": {
              "includePaths": ["."],
              "sass": {
                "silenceDeprecations": [
                  "mixed-decls",
                  "color-functions",
                  "global-builtin",
                  "import"
                ]
              }
            }
          },
          "configurations": {
            "development": {
              "budgets": [
                {
                  "type": "anyComponentStyle",
                  "maximumWarning": "6kb"
                }
              ],
              "optimization": false,
              "extractLicenses": false,
              "sourceMap": true,
              "namedChunks": false,
              "aot": false,
              "serviceWorker": "ngsw-config.json"
            },
            "production": {
              "baseHref": "",
              "budgets": [
                {
                  "type": "anyComponentStyle",
                  "maximumWarning": "6kb"
                }
              ],
              "fileReplacements": [
                {
                  "replace": "src/environments/environment.ts",
                  "with": "src/environments/environment.prod.ts"
                }
              ],
              "optimization": {
                "scripts": true,
                "styles": {
                  "minify": true,
                  "inlineCritical": false
                },
                "fonts": true
              },
              "outputHashing": "all",
              "sourceMap": true,
              "namedChunks": false,
              "aot": true,
              "extractLicenses": true,
              "serviceWorker": "ngsw-config.json"
            },
            "productionWeb": {
              "budgets": [
                {
                  "type": "anyComponentStyle",
                  "maximumWarning": "6kb"
                }
              ],
              "fileReplacements": [
                {
                  "replace": "src/environments/environment.ts",
                  "with": "src/environments/environment.prod.ts"
                }
              ],
              "optimization": {
                "scripts": true,
                "styles": {
                  "minify": true,
                  "inlineCritical": false
                },
                "fonts": true
              },
              "outputHashing": "all",
              "sourceMap": true,
              "namedChunks": false,
              "aot": true,
              "extractLicenses": true,
              "serviceWorker": "ngsw-config.json"
            },
            "stage": {
              "baseHref": "",
              "budgets": [
                {
                  "type": "anyComponentStyle",
                  "maximumWarning": "6kb"
                }
              ],
              "fileReplacements": [
                {
                  "replace": "src/environments/environment.ts",
                  "with": "src/environments/environment.prod.ts"
                }
              ],
              "optimization": {
                "scripts": true,
                "styles": {
                  "minify": true,
                  "inlineCritical": false
                },
                "fonts": true
              },
              "outputHashing": "all",
              "sourceMap": true,
              "namedChunks": false,
              "aot": true,
              "extractLicenses": true,
              "serviceWorker": false
            },
            "stageWeb": {
              "budgets": [
                {
                  "type": "anyComponentStyle",
                  "maximumWarning": "6kb"
                }
              ],
              "fileReplacements": [
                {
                  "replace": "src/environments/environment.ts",
                  "with": "src/environments/environment.prod.ts"
                }
              ],
              "optimization": {
                "scripts": true,
                "styles": {
                  "minify": true,
                  "inlineCritical": false
                },
                "fonts": true
              },
              "outputHashing": "all",
              "sourceMap": true,
              "namedChunks": false,
              "aot": true,
              "extractLicenses": true,
              "serviceWorker": "ngsw-config.json"
            }
          }
        },
        "serve": {
          "builder": "@angular-devkit/build-angular:dev-server",
          "options": {
            "open": false,
            "buildTarget": "sp2:build"
          },
          "configurations": {
            "production": {
              "buildTarget": "sp2:build:production"
            },
            "development": {
              "buildTarget": "sp2:build:development"
            }
          },
          "defaultConfiguration": "development"
        },
        "extract-i18n": {
          "builder": "@angular-devkit/build-angular:extract-i18n",
          "options": {
            "buildTarget": "sp2:build"
          }
        },
        "test": {
          "builder": "@angular-devkit/build-angular:karma",
          "options": {
            "main": "src/test.ts",
            "polyfills": "src/polyfills.ts",
            "tsConfig": "src/tsconfig.spec.json",
            "preserveSymlinks": true,
            "karmaConfig": "src/karma.conf.js",
            "styles": ["src/styles.scss"],
            "scripts": [],
            "assets": ["src/favicon.ico", "src/assets", "src/manifest.json"]
          }
        },
        "lint": {
          "builder": "@angular-eslint/builder:lint",
          "options": {
            "cache": true,
            "lintFilePatterns": [
              "src/**/*.ts",
              "src/**/*.html",
              "e2e/**/*.ts",
              "electron/**/*.ts"
            ]
          }
        }
      }
    }
  },
  "cli": {
    "analytics": false,
    "schematicCollections": ["@ngrx/schematics"]
  },
  "schematics": {
    "@schematics/angular:component": {
      "style": "scss"
    }
  }
}

{
  "webDir": "dist/browser",
  "android": {
    "path": "android"
  }
}

import type { CapacitorConfig } from '@capacitor/cli';

const config: CapacitorConfig = {
  appId: 'com.superproductivity.superproductivity',
  appName: 'super-productivity',
  webDir: 'dist/browser',
};

export default config;

{
  "$schema": "./node_modules/@angular/service-worker/config/schema.json",
  "index": "/index.html",
  "assetGroups": [
    {
      "name": "app",
      "installMode": "prefetch",
      "resources": {
        "files": ["/favicon.ico", "/index.html", "/*.css", "/*.js"]
      }
    },
    {
      "name": "assets",
      "installMode": "lazy",
      "updateMode": "prefetch",
      "resources": {
        "files": ["/assets/**"]
      }
    },
    {
      "name": "assetsAlwaysNeeded",
      "installMode": "prefetch",
      "updateMode": "prefetch",
      "resources": {
        "files": [
          "/assets/icons/*.svg",
          "/assets/i18n/en.json",
          "/assets/unicorn.png",
          "/assets/fonts/MaterialIcons-Regular.ttf",
          "/*.(otf|ttf|woff|woff2)"
        ]
      }
    },
    {
      "name": "externalAssets",
      "installMode": "lazy",
      "updateMode": "prefetch",
      "resources": {
        "urls": ["**/*.(png|jpg|jpeg|gif)"]
      }
    }
  ],
  "dataGroups": []
}

