const {getESLintConfig} = require('@vis.gl/dev-tools/configuration');

module.exports = getESLintConfig({
  react: '18.0.0',
  overrides: {
    parser: '',
    parserOptions: {
      project: ['./tsconfig.json'],
      ecmaVersion: 2020
    },
    extends: ['prettier'],

    env: {
      es2020: true
      // browser: true,
      // node: true
    },

    globals: {
      Iterable: true,
      AsyncIterable: true
    },

    rules: {
      camelcase: 0,
      indent: 0,
      'import/no-unresolved': 0,
      'import/no-extraneous-dependencies': 0, // ['warn'],
      'no-console': 1,
      'no-continue': 0,
      'callback-return': 0,
      'max-depth': ['warn', 4],
      complexity: ['warn'],
      'max-statements': ['warn'],
      'default-case': ['warn'],
      'no-eq-null': ['warn'],
      eqeqeq: ['warn'],
      radix: 0,
      'react/sort-comp': 0
      // 'accessor-pairs': ['error', {getWithoutSet: false, setWithoutGet: false}]
    },

    overrides: [
      {
        files: ['**/*.js', '**/*.ts', '**/*.tsx', '**/*.d.ts'],
        rules: {
          indent: 0,
          // For parquet module
          '@typescript-eslint/no-non-null-assertion': 0,
          '@typescript-eslint/no-non-null-asserted-optional-chain': 0,
          // Gradually enable
          '@typescript-eslint/ban-ts-comment': 0,
          '@typescript-eslint/ban-types': 0,
          '@typescript-eslint/no-unsafe-member-access': 0,
          '@typescript-eslint/no-unsafe-assignment': 0,
          '@typescript-eslint/no-var-requires': 0,
          '@typescript-eslint/no-unused-vars': [
            'warn',
            {vars: 'all', args: 'none', ignoreRestSiblings: false}
          ],
          // We still have some issues with import resolution
          'import/named': 0,
          'import/no-duplicates': 0,
          // Warn instead of error
          'max-params': ['warn'],
          // 'no-undef': ['warn'],
          // camelcase: ['warn'],
          // '@typescript-eslint/no-floating-promises': ['warn'],
          // '@typescript-eslint/await-thenable': ['warn'],
          // '@typescript-eslint/no-misused-promises': ['warn'],
          '@typescript-eslint/no-shadow': ['warn'],
          '@typescript-eslint/no-empty-function': 0,
          // We use function hoisting
          '@typescript-eslint/no-use-before-define': 0,
          // We always want explicit typing, e.g `field: string = ''`
          '@typescript-eslint/no-inferrable-types': 0,
          '@typescript-eslint/restrict-template-expressions': 0,
          '@typescript-eslint/explicit-module-boundary-types': 0,
          '@typescript-eslint/no-redundant-type-constituents': 0,
          '@typescript-eslint/require-await': 0,
          '@typescript-eslint/no-explicit-any': 0,
          '@typescript-eslint/no-unsafe-argument': 0,
          '@typescript-eslint/no-unsafe-return': 0,
          '@typescript-eslint/no-unsafe-call': 0,
          '@typescript-eslint/no-empty-interface': 0,
          '@typescript-eslint/restrict-plus-operands': 0
        }
      },
      // tests are run with aliases set up in node and webpack.
      // This means lint will not find the imported files and generate false warnings
      {
        // scripts use devDependencies
        files: [
          '**/test/**/*.js',
          '**/test/**/*.ts',
          '**/scripts/**/*.js',
          '*.config.js',
          '*.config.local.js',
          '*.config.local.mjs'
        ],
        globals: {
          process: true
        },
        rules: {
          'import/no-unresolved': 0,
          'import/no-extraneous-dependencies': 0,
          'no-process-env': 0
        }
      },
      {
        files: ['examples/**/*.*', 'website/src/**/*.*'],
        rules: {
          // We want lint to pass without having to install the dependencies for examples or website
          'import/no-unresolved': 0,
          'import/named': 0,
          'no-new': 0
        }
      },
      {
        files: ['modules/widgets/**/*.tsx'],
        rules: {
          // For widgets module. Disable React-style JSX linting since they conflict with Preact JSX.
          'react/react-in-jsx-scope': 0
        }
      }
    ],

    settings: {
      // Ensure eslint finds typescript files
      'import/resolver': {
        node: {
          extensions: ['.js', '.jsx', '.mjs', '.ts', '.tsx']
        }
      }
    }
  }
});

{
  "all": "true",
  "sourceMap": false,
  "instrument": true,
  "include": [
    "modules/**/src/**/*.js",
    "modules/**/src/**/*.ts"
  ],
  "exclude": [
    "modules/test-utils/",
    "modules/**/types.ts"
  ]
}

/** @typedef {import('@vis.gl/dev-tools').OcularConfig} OcularConfig */
import {dirname, join} from 'path';
import {fileURLToPath} from 'url';

const packageRoot = dirname(fileURLToPath(import.meta.url));
const lumaModules = join(packageRoot, '../luma.gl/modules');

const LUMA_ALIASES_LOCAL = {
  '@luma.gl/constants': `${lumaModules}/constants/src`,
  '@luma.gl/core': `${lumaModules}/core/src`,
  '@luma.gl/engine': `${lumaModules}/engine/src`,
  '@luma.gl/webgl': `${lumaModules}/webgl/src`,
  '@luma.gl/shadertools': `${lumaModules}/shadertools/src`,
  '@luma.gl/test-utils': `${lumaModules}/test-utils/src`,
  '@luma.gl/experimental': `${lumaModules}/experimental/src`
};

const useLocalLuma = false;

/** @type {OcularConfig} */
const config = {
  lint: {
    paths: ['modules', 'test', 'examples']
    // paths: ['modules', 'test', 'examples', 'website']
  },

  babel: false,

  bundle: {
    globalName: 'deck',
    externals: ['h3-js'],
    target: ['chrome110', 'firefox110', 'safari15'],
    format: 'umd',
    globals: {
      '@deck.gl/*': 'globalThis.deck',
      '@luma.gl/core': 'globalThis.luma',
      '@luma.gl/engine': 'globalThis.luma',
      '@loaders.gl/core': 'globalThis.loaders',
      'h3-js': 'globalThis.h3 || {}'
    }
  },

  aliases: {
    'deck.gl-test': join(packageRoot, './test')
  },

  coverage: {
    test: 'browser'
  },

  entry: {
    test: 'test/node.ts',
    'test-browser': 'index.html',
    bench: 'test/bench/index.js',
    'bench-browser': 'test/bench/browser.html',
    size: 'test/size/import-nothing.js'
  }
};

if (useLocalLuma) {
  Object.assign(config.aliases, LUMA_ALIASES_LOCAL);
}

export default config;

{
  "version": "9.1.0-beta.1",
  "npmClient": "yarn",
  "packages": [
    "modules/*"
  ]
}

{
  "ts-node": {
    "include": [
      "modules",
      "test/modules"
    ],
    // It is faster to skip typechecking. Remove if you want ts-node to do typechecking.
    "transpileOnly": true
  },
  "compilerOptions": {
    "target": "es2020",
    "module": "es2020",
    "lib": ["DOM", "DOM.Iterable", "ES2022"],
    "jsx": "react",
    "strict": true,
    "noImplicitAny": false,
    "allowJs": true,
    "checkJs": false,
    "moduleResolution": "node",
    "esModuleInterop": true,
    "allowSyntheticDefaultImports": true,
    "resolveJsonModule": true,
    "skipLibCheck": true,
    "baseUrl": ".",
    "paths": {
      "@deck.gl/aggregation-layers": ["modules/aggregation-layers/src"],
      "@deck.gl/arcgis": ["modules/arcgis/src"],
      "@deck.gl/carto": ["modules/carto/src"],
      "@deck.gl/core": ["modules/core/src"],
      "@deck.gl/extensions": ["modules/extensions/src"],
      "@deck.gl/geo-layers": ["modules/geo-layers/src"],
      "@deck.gl/google-maps": ["modules/google-maps/src"],
      "@deck.gl/json": ["modules/json/src"],
      "@deck.gl/jupyter-widget": ["modules/jupyter-widget/src"],
      "@deck.gl/layers": ["modules/layers/src"],
      "deck.gl": ["modules/main/src"],
      "@deck.gl/mapbox": ["modules/mapbox/src"],
      "@deck.gl/mesh-layers": ["modules/mesh-layers/src"],
      "@deck.gl/react": ["modules/react/src"],
      "@deck.gl/test-utils": ["modules/test-utils/src"],

      "deck.gl-test/*": ["test/*"]
    },
    "typeRoots" : ["./node_modules/@types"],
    "plugins": [
      {
        "transform": "@vis.gl/ts-plugins/ts-transform-version-inline"
      },
      {
        "transform": "@vis.gl/ts-plugins/ts-transform-remove-glsl-comments",
        "pattern": ["**/*.glsl.ts"]
      },
      {
        "transform": "@vis.gl/ts-plugins/ts-transform-inline-webgl-constants"
      },
      {
        "transform": "@vis.gl/ts-plugins/ts-transform-append-extension",
        "extensions": [".js", ".glsl.js"],
        "after": true
      },
      {
        "transform": "@vis.gl/ts-plugins/ts-transform-append-extension",
        "extensions": [".js", ".glsl.js"],
        "afterDeclarations": true
      }
    ]
  },
  "include": [
    "modules",
    "test",
    "examples"
  ],
  "exclude": [
    "**/node_modules",
    "**/dist"
  ]
}
