import type { Config } from 'drizzle-kit';

import { env } from '@/env';

export default {
  dialect: 'postgresql',
  schema: './src/db/schema/index.ts',
  out: './drizzle',
  strict: true,
  verbose: true,
  dbCredentials: {
    url: env.DATABASE_URL,
  },
} satisfies Config;

import path from 'path';
import { fileURLToPath } from 'url';

import comments from '@eslint-community/eslint-plugin-eslint-comments/configs';
import { fixupConfigRules } from '@eslint/compat';
import { FlatCompat } from '@eslint/eslintrc';
import js from '@eslint/js';
import eslintConfigPrettier from 'eslint-config-prettier';
import jsdoc from 'eslint-plugin-jsdoc';
import * as regexpPlugin from 'eslint-plugin-regexp';
import pluginSecurity from 'eslint-plugin-security';
import tseslint from 'typescript-eslint';
import globals from 'globals';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

const compat = new FlatCompat({
  baseDirectory: __dirname,
  resolvePluginsRelativeTo: __dirname,
});

export default tseslint.config(
  {
    ignores: ['.next'],
  },

  // Base configurations
  js.configs.recommended,
  ...tseslint.configs.strictTypeChecked,
  ...tseslint.configs.stylisticTypeChecked,

  // Next.js / React
  ...fixupConfigRules(compat.extends('plugin:@next/next/recommended')),
  ...fixupConfigRules(compat.extends('plugin:react/recommended')),
  ...fixupConfigRules(compat.extends('plugin:react-hooks/recommended')),
  ...fixupConfigRules(compat.extends('plugin:jsx-a11y/strict')),

  // Tailwind CSS
  ...fixupConfigRules(compat.extends('plugin:tailwindcss/recommended')),

  // Other plugins
  comments.recommended,
  regexpPlugin.configs['flat/recommended'],
  pluginSecurity.configs.recommended,
  eslintConfigPrettier,

  // JSDoc plugin only for TypeScript files
  {
    files: ['**/*.{ts,tsx}'],
    extends: [jsdoc.configs['flat/recommended-typescript-error']],
  },

  // Settings and rule overrides
  {
    linterOptions: {
      reportUnusedDisableDirectives: true,
    },
    languageOptions: {
      parserOptions: {
        projectService: true,
        tsconfigRootDir: __dirname,
      },
      globals: {
        ...globals.node,
        ...globals.browser,
        ...globals.es2024,
      },
    },
    settings: {
      react: {
        version: 'detect',
      },
      tailwindcss: {
        callees: ['classnames', 'clsx', 'ctl', 'cn', 'cva'],
      },
    },
    rules: {
      '@typescript-eslint/no-unused-vars': [
        'error',
        { argsIgnorePattern: '^_', varsIgnorePattern: '^_' },
      ],

      '@typescript-eslint/consistent-type-imports': [
        'warn',
        { prefer: 'type-imports', fixStyle: 'separate-type-imports' },
      ],

      '@typescript-eslint/no-misused-promises': [
        'error',
        { checksVoidReturn: { attributes: false } },
      ],

      '@typescript-eslint/non-nullable-type-assertion-style': 'off',

      '@typescript-eslint/dot-notation': 'off',

      '@typescript-eslint/no-unnecessary-condition': [
        'error',
        {
          allowConstantLoopConditions: true,
        },
      ],

      'react/react-in-jsx-scope': 'off',
      'react/prop-types': 'off',

      // security
      'security/detect-non-literal-fs-filename': 'off',

      // we're not building a library here
      'jsdoc/require-jsdoc': 'off',
    },
  },
);

/**
 * Since ESLint and many plugins are written in JavaScript, we need to provide
 * or change some types to make them work with TypeScript.
 */

// From: https://github.com/t3-oss/create-t3-turbo/blob/main/tooling/eslint/types.d.ts
declare module '@eslint/js' {
  import type { Linter } from 'eslint';

  export const configs: {
    readonly recommended: { readonly rules: Readonly<Linter.RulesRecord> };
    readonly all: { readonly rules: Readonly<Linter.RulesRecord> };
  };
}

declare module '@eslint-community/eslint-plugin-eslint-comments/configs' {
  import type { Linter } from 'eslint';

  export const recommended: {
    rules: Linter.RulesRecord;
  };
}

declare module '@eslint/eslintrc' {
  import type { Linter } from 'eslint';

  export class FlatCompat {
    constructor({
      baseDirectory,
      resolvePluginsRelativeTo,
    }: {
      baseDirectory: string;
      resolvePluginsRelativeTo: string;
    });

    extends(extendsValue: string): Linter.Config & {
      [Symbol.iterator]: () => IterableIterator<Linter.Config>;
    };
  }
}

declare module '@eslint/compat' {
  import type { Linter } from 'eslint';
  import type { ConfigWithExtends } from 'typescript-eslint';

  export const fixupConfigRules: (
    config: string | Linter.Config,
  ) => ConfigWithExtends[];
}

declare module 'eslint-plugin-regexp' {
  import type { Linter } from 'eslint';
  import type { ConfigWithExtends } from 'typescript-eslint';

  export const configs: {
    'flat/recommended': {
      [Symbol.iterator]: () => IterableIterator<ConfigWithExtends>;
      rules: Linter.RulesRecord;
    };
    'flat/all': {
      [Symbol.iterator]: () => IterableIterator<ConfigWithExtends>;
      rules: Linter.RulesRecord;
    };
  };
}

declare module 'eslint-plugin-security' {
  import type { Linter } from 'eslint';
  import type { ConfigWithExtends } from 'typescript-eslint';

  export const configs: {
    recommended: {
      [Symbol.iterator]: () => IterableIterator<ConfigWithExtends>;
      rules: Linter.RulesRecord;
    };
  };
}

import { fileURLToPath } from 'node:url';

import { createJiti } from 'jiti';

const jiti = createJiti(fileURLToPath(import.meta.url));

await jiti.import('./src/env');

/** @type {import('next').NextConfig} */
const nextConfig = {
  reactStrictMode: true,
  transpilePackages: ['next-mdx-remote', 'lucide-react'],

  // We run ESLint and TypeScript separately in the CI pipeline
  eslint: {
    ignoreDuringBuilds: true,
  },
  typescript: {
    ignoreBuildErrors: true,
  },
  logging: {
    fetches: {
      fullUrl: true,
    },
  },
};

export default nextConfig;

/** @type {import('postcss-load-config').Config} */
const config = {
  plugins: {
    tailwindcss: {},
  },
};

export default config;

import type { Config } from 'tailwindcss';

import aspectRatioPlugin from '@tailwindcss/aspect-ratio';
import containerQueriesPlugin from '@tailwindcss/container-queries';
import typographyPlugin from '@tailwindcss/typography';
import tailwindAnimate from 'tailwindcss-animate';

type Scale = 'gray' | 'pink' | 'salmon' | 'indigo' | 'red';

const generateColorScale = (scale: Scale) => ({
  DEFAULT: `hsl(var(--${scale}-9))`,

  'foreground-muted': `hsl(var(--${scale}-11))`,
  foreground: `hsl(var(--${scale}-12))`,

  app: `hsl(var(--${scale}-1))`,

  subtle: `hsl(var(--${scale}-2))`,
  'subtle-border': `hsl(var(--${scale}-6))`,

  element: `hsl(var(--${scale}-3))`,
  'element-hover': `hsl(var(--${scale}-4))`,
  'element-active': `hsl(var(--${scale}-5))`,
  'element-border': `hsl(var(--${scale}-7))`,
  'element-border-hover': `hsl(var(--${scale}-8))`,

  solid: `hsl(var(--${scale}-9))`,
  'solid-hover': `hsl(var(--${scale}-10))`,
});

const config: Config = {
  content: ['./src/**/*.{js,ts,jsx,tsx,mdx}'],
  darkMode: 'class',
  theme: {
    container: {
      center: true,
      padding: '1.5rem',
      screens: {
        '2xl': '1400px',
      },
    },
    extend: {
      colors: {
        background: 'hsl(var(--gray-1))',
        foreground: 'hsl(var(--gray-12))',
        'foreground-muted': 'hsl(var(--gray-11))',
        border: 'hsl(var(--gray-4))',

        gray: generateColorScale('gray'),
        pink: generateColorScale('pink'),
        salmon: generateColorScale('salmon'),
        indigo: generateColorScale('indigo'),
        red: generateColorScale('red'),
      },
      fontFamily: {
        sans: ['var(--font-geist-sans)'],
        mono: ['var(--font-geist-mono)'],
      },
    },
  },
  plugins: [
    typographyPlugin,
    aspectRatioPlugin,
    {
      handler: containerQueriesPlugin.handler,
      config: containerQueriesPlugin.config ?? {},
    },
    tailwindAnimate,
  ],
};
export default config;
