// Installing third-party modules by tnpm or cnpm will name modules with underscore as prefix.
// In this case _{module} is also necessary.
const esm = ['internmap', 'd3-*', 'lodash-es', 'chroma-js']
  .map((d) => `_${d}|${d}`)
  .join('|');

module.exports = {
  testTimeout: 100000,
  preset: 'ts-jest/presets/js-with-ts',
  globals: {
    'ts-jest': {
      tsconfig: {
        target: 'esnext', // Increase test coverage.
        allowJs: true,
        sourceMap: true,
      },
    },
  },
  collectCoverageFrom: ['src/**/*.ts', '!**/d3-sankey/**', '!**/d3-cloud/**'],
  moduleFileExtensions: ['ts', 'tsx', 'js', 'json'],
  collectCoverage: false,
  testRegex: '(/__tests__/.*\\.(test|spec))\\.(ts|tsx|js)$',
  // Transform esm to cjs.
  transformIgnorePatterns: [`<rootDir>/node_modules/(?!(?:.pnpm/)?(${esm}))`]
};

import nodePolyfills from 'rollup-plugin-polyfill-node';
import visualizer from 'rollup-plugin-visualizer';
import terser from '@rollup/plugin-terser';
import commonjs from '@rollup/plugin-commonjs';
import resolve from '@rollup/plugin-node-resolve';
import typescript from 'rollup-plugin-typescript2';
import json from '@rollup/plugin-json';
import { optimizeLodashImports } from '@optimize-lodash/rollup-plugin';

const isBundleVis = !!process.env.BUNDLE_VIS;

const Bundles = [
  // [input, output, name]
  ['bundle/g2.std.ts', 'dist/g2.min.js', 'G2'],
  ['bundle/g2.lite.ts', 'dist/g2.lite.min.js', 'G2'],
];

export default [
  // Bundle for G2 umd entries.
  ...Bundles.map(([input, file, name], idx) => ({
    input,
    treeshake: {
      preset: 'smallest',
      // Set `src/exports` as a sideEffects file.
      moduleSideEffects: (id, external) =>
        id.includes('src/exports.ts') ? true : false,
    },
    output: [
      {
        file,
        name,
        format: 'umd',
        sourcemap: false,
        plugins: [isBundleVis && idx === Bundles.length - 1 && visualizer()],
      },
    ],
    plugins: [
      nodePolyfills(),
      resolve(),
      commonjs(),
      typescript({
        useTsconfigDeclarationDir: true,
      }),
      optimizeLodashImports(),
      json(),
      terser(),
    ],
    context: 'window', // Disable 'THIS_IS_UNDEFINED' warnings
  })),
];

import { defineConfig } from 'vite';
import type { UserConfig } from 'vite';
import { deepMix } from '@antv/util';
const { LINK, MODULE } = process.env;

if (LINK === '1' && !MODULE) {
  throw new Error(
    `Please specify MODULE, for example: $ MODULE=@antv/component npm run dev:link.`,
  );
}

/**
 * @desc Deeply merge user config into default config
 * @link https://vitejs.dev/config/
 * @type {import('vite').UserConfig}
 */
const baseOptions: UserConfig = {
  root: './__tests__/',
  server: {
    port: 8080,
    open: '/',
  },
  build: { outDir: '../' },
};

/**
 * @desc Link configuration
 * @link https://vitejs.dev/config/
 * @type {import('vite').UserConfig}
 */
const linkOptions: UserConfig = {
  server: {
    watch: {
      ignored: [`!**/node_modules/${MODULE}/**`],
    },
  },
  optimizeDeps: {
    exclude: [`${MODULE}`],
  },
};

/**
 * @desc Vite configuration
 * @link https://vitejs.dev/config/
 * @type {import('vite').UserConfig}
 */
export default defineConfig(
  deepMix(baseOptions, LINK === '1' ? linkOptions : {}),
);
