import gulp from 'gulp';
import rename from 'gulp-rename';
import uglify from 'gulp-uglify-es';
import replace from 'gulp-replace';
import jsdoc from 'gulp-jsdoc3';
import connect from 'gulp-connect';
import gutil from 'gulp-util';

import fs from 'fs';
var NodeParams = fs
  .readFileSync('./resources/doc-includes/NodeParams.txt')
  .toString();
var ContainerParams = fs
  .readFileSync('./resources/doc-includes/ContainerParams.txt')
  .toString();
var ShapeParams = fs
  .readFileSync('./resources/doc-includes/ShapeParams.txt')
  .toString();

const conf = JSON.parse(fs.readFileSync('./package.json'));

function build() {
  return gulp
    .src(['./konva.js'])
    .pipe(replace('@@shapeParams', ShapeParams))
    .pipe(replace('@@nodeParams', NodeParams))
    .pipe(replace('@@containerParams', ContainerParams))
    .pipe(replace('@@version', conf.version))
    .pipe(replace('@@date', new Date().toDateString()));
}

gulp.task('update-version-lib', function () {
  return gulp
    .src(['./lib/Global.js'])
    .pipe(replace('@@version', conf.version))
    .pipe(rename('Global.js'))
    .pipe(gulp.dest('./lib'));
});

gulp.task('update-version-cmj', function () {
  return gulp
    .src(['./cmj/Global.js'])
    .pipe(replace('@@version', conf.version))
    .pipe(rename('Global.js'))
    .pipe(gulp.dest('./cmj'));
});

gulp.task('update-version-es-to-cmj-index', function () {
  return gulp
    .src(['./lib/index.js'])
    .pipe(
      replace(`import { Konva } from './_F`, `import { Konva } from '../cmj/_F`)
    )
    .pipe(rename('index.js'))
    .pipe(gulp.dest('./lib'));
});

gulp.task('update-version-es-to-cmj-node', function () {
  return gulp
    .src(['./lib/index-node.js'])
    .pipe(
      replace(`import { Konva } from './_F`, `import { Konva } from '../cmj/_F`)
    )
    .pipe(rename('index-node.js'))
    .pipe(gulp.dest('./lib'));
});

// create usual build konva.js and konva.min.js
gulp.task('pre-build', function () {
  return build()
    .pipe(rename('konva.js'))
    .pipe(gulp.dest('./'))
    .pipe(
      uglify.default({ output: { comments: /^!|@preserve|@license|@cc_on/i } })
    )
    .on('error', function (err) {
      gutil.log(gutil.colors.red('[Error]'), err.toString());
    })
    .pipe(rename('konva.min.js'))
    .pipe(gulp.dest('./'));
});

gulp.task(
  'build',
  gulp.parallel([
    'update-version-lib',
    // 'update-version-cmj',
    // 'update-version-es-to-cmj-index',
    // 'update-version-es-to-cmj-node',
    'pre-build',
  ])
);

// local server for better development
gulp.task('server', function () {
  connect.server();
});

// // generate documentation
gulp.task('api', function () {
  return gulp.src('./konva.js').pipe(
    jsdoc({
      opts: {
        destination: './api',
      },
    })
  );
});

gulp.task('default', gulp.parallel(['server']));

import FileHound from 'filehound';
import fs from 'fs';

const files = FileHound.create().paths('./lib').ext(['js', 'ts']).find();

files.then((filePaths) => {
  filePaths.forEach((filepath) => {
    fs.readFile(filepath, 'utf8', (err, text) => {
      if (!text.match(/import .* from/g)) {
        return;
      }
      text = text.replace(/(import .* from\s+['"])(.*)(?=['"])/g, '$1$2.js');
      if (text.match(/export .* from/g)) {
        text = text.replace(/(export .* from\s+['"])(.*)(?=['"])/g, '$1$2.js');
      }

      if (err) throw err;

      // stupid replacement back
      text = text.replace(
        "import * as Canvas from 'canvas.js';",
        "import * as Canvas from 'canvas';"
      );

      // Handle import("./x/y/z") syntax.
      text = text.replace(/(import\s*\(\s*['"])(.*)(?=['"])/g, '$1$2.js');

      fs.writeFile(filepath, text, function (err) {
        if (err) {
          throw err;
        }
      });
    });
  });
});

const indexFiles = ['lib/index.js', 'lib/index-node.js', 'lib/Core.js'];
indexFiles.forEach((filepath) => {
  fs.readFile(filepath, 'utf8', (err, text) => {
    text = text.replace('exports.default =', 'module.exports =');
    fs.writeFile(filepath, text, function (err) {
      if (err) {
        throw err;
      }
    });
  });
});

// import resolve from 'rollup-plugin-node-resolve';
import typescript from 'rollup-plugin-typescript2';

const pkg = require('./package.json');

export default {
  input: `src/index.ts`,
  output: [
    {
      file: 'konva.js',
      name: 'Konva',
      format: 'umd',
      sourcemap: false,
      freeze: false,
    },
    // { file: pkg.module, format: 'es', sourcemap: true }
  ],
  // Indicate here external modules you don't wanna include in your bundle (i.e.: 'lodash')
  external: [],
  watch: {
    include: 'src/**',
  },
  plugins: [
    // Allow json resolution
    // json(),
    // Compile TypeScript files
    typescript({
      useTsconfigDeclarationDir: true,
      abortOnError: false,
      removeComments: false,
      tsconfigOverride: {
        compilerOptions: {
          module: 'ES2020',
        },
      },
    }),
    // // Allow bundling cjs modules (unlike webpack, rollup doesn't understand cjs)
    // commonjs(),
    // // Allow node_modules resolution, so you can use 'external' to control
    // // which external modules to include in the bundle
    // // https://github.com/rollup/rollup-plugin-node-resolve#usage
    // resolve(),

    // Resolve source maps to the original source
    // sourceMaps()
  ],
};

{
  "compilerOptions": {
    "outDir": "lib",
    "module": "CommonJS",
    "target": "ES2018",
    // "sourceMap": true,
    "noEmitOnError": true,
    "lib": ["ES2019", "dom"],
    "moduleResolution": "node",
    "declaration": true,
    "removeComments": false,

    "strict": true,
    "noImplicitAny": false,
    "noImplicitThis": false,
    "useUnknownInCatchVariables": false,
    "skipLibCheck": true,
    // probably we would never enable this one
    // because it's too strict, konva generates many functions on the runtime
    "strictPropertyInitialization": false,

  },
  "include": ["./src/**/*.ts"],
}
