{
  "folders": [{ "path": "." }],
  "settings": {
    "files.insertFinalNewline": true,
    "jest.jestCommandLine": "node_modules/.bin/jest",
    "jest.autoRun": "off",
    "jest.virtualFolders": [
      { "name": "aws-cdk", "rootPath": "packages/aws-cdk" },
      { "name": "aws-cdk-lib", "rootPath": "packages/aws-cdk-lib" },
      {
        "name": "cli-lib-alpha",
        "rootPath": "packages/@aws-cdk/cli-lib-alpha"
      },
      {
        "name": "cloudformation-diff",
        "rootPath": "packages/@aws-cdk/cloudformation-diff"
      },
      {
        "name": "custom-resource-handlers",
        "rootPath": "packages/@aws-cdk/custom-resource-handlers"
      },
      {
        "name": "integ-runner",
        "rootPath": "packages/@aws-cdk/integ-runner"
      },
      {
        "name": "integ-tests-alpha",
        "rootPath": "packages/@aws-cdk/integ-tests-alpha"
      },
      {
        "name": "aws-custom-resource-sdk-adapter",
        "rootPath": "packages/@aws-cdk/aws-custom-resource-sdk-adapter"
      },
      { "name": "user-input-gen", "rootPath": "tools/@aws-cdk/user-input-gen" }
    ]
  },
  "extensions": {
    "recommendations": ["dbaeumer.vscode-eslint", "Orta.vscode-jest"]
  }
}

#!/bin/bash
set -euo pipefail
exec npx yarn install --frozen-lockfile

{
  "targetDefaults": {
    "build": {
      "dependsOn": [
        "^build"
      ],
      "inputs": [
        "{projectRoot}/**/bin/!(*.d|*.generated).ts",
        "{projectRoot}/**/lib/!(*.d|*.generated).ts",
        "{projectRoot}/**/test/!(*.d).ts",
        "!{workspaceRoot}/**/tsconfig.json",
        "!{workspaceRoot}/**/tsconfig.json",
        "!{workspaceRoot}/tsconfig.base.json",
        "!{workspaceRoot}/**/tsconfig.tsbuildinfo",
        "!{workspaceRoot}/**/jest.config.js"
      ],
      "outputs": [
        "{projectRoot}/**/*.integ.*.js.snapshot/*",
        "{projectRoot}/tsconfig.json",
        "{projectRoot}/**/lib/aws-custom-resource/sdk-v2-to-v3.json",
        "{projectRoot}/**/lib/aws-custom-resource/sdk-v3-metadata.json",
        "{projectRoot}/**/build-info.json",
        "{projectRoot}/**/*.js",
        "{projectRoot}/**/*.js.map",
        "{projectRoot}/**/*.d.ts",
        "{projectRoot}/**/lib/layer.zip",
        "{projectRoot}/**/*.generated.ts",
        "{projectRoot}/**/built-ins.generated/*",
        "{projectRoot}/**/schema/*.json",
        "{projectRoot}/**/.jsii",
        "{projectRoot}/**/.jsii.gz",
        "{projectRoot}/**/spec/*.json",
        "{projectRoot}/**/.warnings.jsii.js"
      ]
    },
    "test": {
      "dependsOn": [
        "build"
      ],
      "inputs": [
        "{projectRoot}/**/test/!(*.d|*.generated).ts"
      ]
    }
  },
  "extends": "@nrwl/workspace/presets/npm.json",
  "workspaceLayout": {},
  "tasksRunnerOptions": {
    "default": {
      "runner": "@nx/workspace/tasks-runners/default",
      "options": {
        "cacheableOperations": [
          "build",
          "test",
          "lint",
          "package",
          "prepare"
        ]
      }
    }
  },
  "affected": {
    "defaultBase": "main"
  },
  "pluginsConfig": {
    "@nrwl/js": {
      "analyzeSourceFiles": false
    }
  },
  "$schema": "./node_modules/nx/schemas/nx-schema.json",
  "namedInputs": {
    "default": [
      "{projectRoot}/**/*",
      "sharedGlobals"
    ],
    "sharedGlobals": [],
    "production": [
      "default"
    ]
  }
}

{
  "majorVersion": 2,
  "releaseType": "stable"
}

{
  "version": "2.175.0",
  "alphaVersion": "2.175.0-alpha.0"
}
