{
  "name": "Unleash",
  "description": "A default setup of Unleash",
  "repository": "https://github.com/heroku/node-js-sample",
  "logo": "https://github.com/Unleash/unleash/blob/main/.github/logo.png?raw=true",
  "keywords": ["node", "feature toggles", "release"],
  "env": {
    "NPM_CONFIG_PRODUCTION": "false",
    "YARN_PRODUCTION": "false"
  },
  "addons": [
    {
      "plan": "heroku-postgresql",
      "options": {
        "version": "14"
      }
    }
  ]
}

{
  "$schema": "https://biomejs.dev/schemas/1.9.4/schema.json",
  "linter": {
    "enabled": true,
    "rules": {
      "recommended": true,
      "a11y": {
        "useSemanticElements": "off"
      },
      "correctness": {
        "noUnsafeOptionalChaining": "off",
        "useExhaustiveDependencies": "off",
        "noUnusedImports": "warn",
        "useJsxKeyInIterable": "off"
      },
      "complexity": {
        "noBannedTypes": "off",
        "noUselessConstructor": "off",
        "useOptionalChain": "warn",
        "noStaticOnlyClass": "off",
        "noForEach": "off",
        "noUselessFragments": "off"
      },
      "style": {
        "noNonNullAssertion": "off",
        "noInferrableTypes": "off",
        "noUnusedTemplateLiteral": "off",
        "useSingleVarDeclarator": "off",
        "noUselessElse": "off",
        "useNodejsImportProtocol": "off"
      },
      "suspicious": {
        "noExplicitAny": "off",
        "noExtraNonNullAssertion": "off",
        "noRedeclare": "off",
        "noPrototypeBuiltins": "off",
        "noConfusingVoidType": "off",
        "noArrayIndexKey": "off",
        "noThenProperty": "off",
        "noExportsInTest": "off"
      },
      "performance": {
        "noAccumulatingSpread": "off",
        "noDelete": "off"
      }
    },
    "ignore": [
      "node_modules",
      "docker",
      "bundle.js",
      "website/build",
      "website/global.js",
      "setupJest.js",
      "dist",
      "build",
      "src/migrations/*.js",
      "src/test/examples/*.json",
      ".docusaurus",
      "coverage",
      "CHANGELOG.md",
      "./package.json",
      "./frontend/package.json"
    ]
  },
  "organizeImports": {
    "enabled": false
  },
  "formatter": {
    "indentStyle": "space",
    "ignore": [
      "node_modules",
      "docker",
      "bundle.js",
      "setupJest.js",
      "dist",
      "build",
      "src/migrations/*.js",
      "src/migrations/*.json",
      "src/test/examples/*.json",
      "coverage",
      "website/build",
      "website/global.js",
      ".docusaurus",
      "./package.json",
      "./frontend/package.json"
    ],
    "indentWidth": 4
  },
  "javascript": {
    "formatter": {
      "semicolons": "always",
      "quoteStyle": "single",
      "jsxQuoteStyle": "single",
      "indentWidth": 4
    }
  },
  "json": {
    "formatter": {
      "indentWidth": 2
    }
  }
}

{
  "extends": ["config:base"],
  "timezone": "Europe/Madrid",
  "schedule": ["after 7pm every weekday", "before 5am every weekday"],
  "packageRules": [
    {
      "matchUpdateTypes": ["minor", "patch"],
      "matchPackagePatterns": ["*"],
      "automerge": true,
      "stabilityDays": 7,
      "internalChecksFilter": "strict"
    }
  ],
  "ignoreDeps": ["react-router-dom"]
}

{
  "compilerOptions": {
    /* Visit https://aka.ms/tsconfig.json to read more about this file */
    /* Basic Options */
    // "incremental": true,                   /* Enable incremental compilation */
    "target": "es2020" /* Specify ECMAScript target version: 'ES3' (default), 'ES5', 'ES2015', 'ES2016', 'ES2017', 'ES2018', 'ES2019', 'ES2020', or 'ESNEXT'. */,
    "module": "commonjs" /* Specify module code generation: 'none', 'commonjs', 'amd', 'system', 'umd', 'es2015', 'es2020', or 'ESNext'. */,
    "lib": [
      "es6"
    ] /* Specify library files to be included in the compilation. */,
    "allowJs": true /* Allow javascript files to be compiled. */,
    // "checkJs": true,                       /* Report errors in .js files. */
    // "jsx": "preserve",                     /* Specify JSX code generation: 'preserve', 'react-native', or 'react'. */
    "declaration": true /* Generates corresponding '.d.ts' file. */,
    "declarationMap": true /* Generates a sourcemap for each corresponding '.d.ts' file. */,
    // "sourceMap": true,                     /* Generates corresponding '.map' file. */
    // "outFile": "./",                       /* Concatenate and emit output to single file. */
    "outDir": "./dist" /* Redirect output structure to the directory. */,
    "rootDir": "./src" /* Specify the root directory of input files. Use to control the output directory structure with --outDir. */,
    // "composite": true,                     /* Enable project compilation */
    // "tsBuildInfoFile": "./",               /* Specify file to store incremental compilation information */
    // "removeComments": true,                /* Do not emit comments to output. */
    // "noEmit": true,                        /* Do not emit outputs. */
    // "importHelpers": true,                 /* Import emit helpers from 'tslib'. */
    // "downlevelIteration": true,            /* Provide full support for iterables in 'for-of', spread, and destructuring when targeting 'ES5' or 'ES3'. */
    // "isolatedModules": true,               /* Transpile each file as a separate module (similar to 'ts.transpileModule'). */
    /* Strict Type-Checking Options */
    //    "strict": true,                           /* Enable all strict type-checking options. */
    // "noImplicitAny": true,                 /* Raise error on expressions and declarations with an implied 'any' type. */
    "strictNullChecks": true /* Enable strict null checks. */,
    // "strictFunctionTypes": true,           /* Enable strict checking of function types. */
    "strictBindCallApply": true /* Enable strict 'bind', 'call', and 'apply' methods on functions. */,
    // "strictPropertyInitialization": true,  /* Enable strict checking of property initialization in classes. */
    "noImplicitThis": true /* Raise error on 'this' expressions with an implied 'any' type. */,
    "alwaysStrict": true /* Parse in strict mode and emit "use strict" for each source file. */,
    /* Additional Checks */
    // "noUnusedLocals": true,                /* Report errors on unused locals. */
    // "noUnusedParameters": true,            /* Report errors on unused parameters. */
    // "noImplicitReturns": true,             /* Report error when not all code paths in function return a value. */
    // "noFallthroughCasesInSwitch": true,    /* Report errors for fallthrough cases in switch statement. */
    // "noUncheckedIndexedAccess": true,      /* Include 'undefined' in index signature results */
    /* Module Resolution Options */
    // "moduleResolution": "node",            /* Specify module resolution strategy: 'node' (Node.js) or 'classic' (TypeScript pre-1.6). */
    // "baseUrl": "./src"                     /* Base directory to resolve non-absolute module names. */,
    // "paths": {},                           /* A series of entries which re-map imports to lookup locations relative to the 'baseUrl'. */
    // "rootDirs": [],                        /* List of root folders whose combined content represents the structure of the project at runtime. */
    // "typeRoots": [],                       /* List of folders to include type definitions from. */
    // "allowSyntheticDefaultImports": true,  /* Allow default imports from modules with no default export. This does not affect code emit, just typechecking. */
    "esModuleInterop": true /* Enables emit interoperability between CommonJS and ES Modules via creation of namespace objects for all imports. Implies 'allowSyntheticDefaultImports'. */,
    // "preserveSymlinks": true,              /* Do not resolve the real path of symlinks. */
    // "allowUmdGlobalAccess": true,          /* Allow accessing UMD globals from modules. */
    /* Source Map Options */
    // "sourceRoot": "",                      /* Specify the location where debugger should locate TypeScript files instead of source locations. */
    // "mapRoot": "",                         /* Specify the location where debugger should locate map files instead of generated locations. */
    // "inlineSourceMap": true,               /* Emit a single file with source maps instead of having a separate file. */
    // "inlineSources": true,                 /* Emit the source alongside the sourcemaps within a single file; requires '--inlineSourceMap' or '--sourceMap' to be set. */
    "sourceMap": true,
    /* Experimental Options */
    // "experimentalDecorators": true,        /* Enables experimental support for ES7 decorators. */
    // "emitDecoratorMetadata": true,         /* Enables experimental support for emitting type metadata for decorators. */
    /* Advanced Options */
    "resolveJsonModule": true /* Include modules imported with '.json' extension */,
    "skipLibCheck": true /* Skip type checking of declaration files. */,
    "forceConsistentCasingInFileNames": true /* Disallow inconsistently-cased references to the same file. */
  },
  "types": ["src/types/openapi.d.ts"],
  "exclude": [
    "bin",
    "docs",
    "docker",
    "examples",
    "migrations",
    "node_modules",
    "website",
    "src/binver-dev.js",
    "dist",
    "snapshots",
    "coverage",
    "website",
    "scripts",
    "frontend"
  ]
}
