{
  "plugins": [
    "@stylistic"
  ],
  "extends": [
    "next/core-web-vitals",
    "plugin:@stylistic/recommended-extends"
  ],
  "rules": {
    "@stylistic/indent": [
      "error",
      4,
      {
        "SwitchCase": 1
      }
    ],
    "@stylistic/no-tabs": "error",
    "@stylistic/member-delimiter-style": [
      "error",
      {
        "multiline": {
          "delimiter": "semi",
          "requireLast": true
        },
        "singleline": {
          "delimiter": "semi",
          "requireLast": false
        }
      }
    ],
    "@stylistic/eol-last": [
      "error",
      "always"
    ],
    "@stylistic/multiline-ternary": "off",
    "@stylistic/semi": [
      "error",
      "always"
    ],
    "@stylistic/quotes": "off",
    "@stylistic/comma-dangle": "off",
    "@stylistic/brace-style": [
      "error",
      "1tbs"
    ]
  }
}

import eslint from "@eslint/js";
import tseslint from "@typescript-eslint/eslint-plugin";
import typescript from "@typescript-eslint/parser";
import prettier from "eslint-config-prettier";
import vitest from "@vitest/eslint-plugin"; // Add Vitest plugin

export default [
    // JavaScript and TypeScript files
    {
        files: ["src/**/*.js", "src/**/*.cjs", "src/**/*.mjs", "src/**/*.ts"],
        languageOptions: {
            parser: typescript,
            parserOptions: {
                ecmaVersion: "latest",
                sourceType: "module",
                project: "./tsconfig.json", // Make sure your tsconfig includes @types/node
            },
            globals: {
                // Add Node.js globals
                NodeJS: "readonly",
                console: "readonly",
                process: "readonly",
                Buffer: "readonly",
                __dirname: "readonly",
                __filename: "readonly",
                module: "readonly",
                require: "readonly",
            },
        },
        plugins: {
            "@typescript-eslint": tseslint,
        },
        rules: {
            ...eslint.configs.recommended.rules,
            ...tseslint.configs.recommended.rules,
            "prefer-const": "warn",
            "no-constant-binary-expression": "error",

            // Disable no-undef as TypeScript handles this better
            "no-undef": "off",
            "@typescript-eslint/no-unsafe-function-type": "off",
            // Customize TypeScript rules
            "@typescript-eslint/no-explicit-any": "warn",
            "@typescript-eslint/no-unused-vars": [
                "error",
                {
                    argsIgnorePattern: "^_",
                    varsIgnorePattern: "^_",
                    ignoreRestSiblings: true,
                },
            ],
        },
    },
    // Vitest configuration
    {
        files: [
            "src/**/*.test.js",
            "src/**/*.test.ts",
            "src/**/*.spec.js",
            "src/**/*.spec.ts",
        ],
        plugins: {
            vitest, // Register Vitest plugin
        },
        rules: {
            ...vitest.configs.recommended.rules,
        },
    },
    // Add prettier as the last config to override other formatting rules
    prettier,
];

/** @type {import('prettier').Config} */
module.exports = {
    endOfLine: "lf",
    semi: true,
    singleQuote: false,
    tabWidth: 4,
    trailingComma: "es5",
    bracketSpacing: true,
    printWidth: 80,
    useTabs: false,         // Add this to enforce spaces
    indent: 4,             // Add this to match tabWidth
    trimTrailingWhitespace: true  // Add this for trailing spaces
};

{
    "$schema": "https://docs.renovatebot.com/renovate-schema.json",
    "extends": ["config:base"],
    "packageRules": [
        {
            "groupName": "Discord.js ecosystem",
            "matchPackagePatterns": ["^@discordjs/", "^discord.js"]
        },
        {
            "groupName": "TypeScript and related",
            "matchPackagePatterns": [
                "^@typescript-eslint/",
                "^typescript$",
                "^ts-",
                "^tslib$"
            ]
        },
        {
            "groupName": "Testing frameworks",
            "matchPackagePatterns": ["^jest$", "^@types/jest$"]
        },
        {
            "groupName": "Rollup and plugins",
            "matchPackagePatterns": ["^@rollup/", "^rollup"]
        },
        {
            "groupName": "ESLint and formatting",
            "matchPackagePatterns": ["^eslint", "^prettier"]
        },
        {
            "groupName": "SQLite related",
            "matchPackagePatterns": [
                "sqlite",
                "^@types/better-sqlite3",
                "^@types/sql.js"
            ]
        },
        {
            "groupName": "AI/ML packages",
            "matchPackagePatterns": [
                "^@anthropic-ai/",
                "^@huggingface/",
                "^openai$",
                "^tiktoken$"
            ]
        },
        {
            "groupName": "Audio processing",
            "matchPackagePatterns": [
                "^wav",
                "^@discordjs/opus",
                "^fluent-ffmpeg",
                "^ffmpeg",
                "^@types/wav"
            ]
        },
        {
            "groupName": "Solana packages",
            "matchPackagePatterns": ["^@solana/"]
        }
    ],
    "timezone": "UTC",
    "schedule": ["every weekend"],
    "prHourlyLimit": 2,
    "prConcurrentLimit": 10,
    "rangeStrategy": "pin",
    "separateMajorMinor": true,
    "dependencyDashboard": true
}

{
    "compilerOptions": {
        "target": "ES2020",
        "module": "commonjs",
        "moduleResolution": "node",
        "esModuleInterop": true,
        "skipLibCheck": true,
        "forceConsistentCasingInFileNames": true
    },
    "files": [],
    "references": [
        { "path": "packages/core" },
        { "path": "packages/client-slack" }
    ]
}

{
    "$schema": "https://turbo.build/schema.json",
    "tasks": {
        "check-types": {
            "dependsOn": ["build"]
        },
        "@elizaos/agent#check-types": {
            "dependsOn": [
                "@elizaos/plugin-solana#build",
                "@elizaos/plugin-direct#build"
            ]
        },
        "build": {
            "outputs": ["dist/**"],
            "dependsOn": ["^@elizaos/core#build"]
        },
        "@elizaos/plugin-solana#build": {
            "outputs": ["dist/**"],
            "dependsOn": [
                "@elizaos/plugin-trustdb#build",
                "@elizaos/plugin-tee#build"
            ]
        },
        "@elizaos/plugin-nft-generation#build": {
            "outputs": ["dist/**"],
            "dependsOn": ["@elizaos/plugin-node#build"]
        },
        "@elizaos/plugin-evm#build": {
            "outputs": ["dist/**"],
            "dependsOn": ["@elizaos/plugin-tee#build"]
        },
        "eliza-docs#build": {
            "outputs": ["build/**"]
        },
        "dev": {
            "persistent": true,
            "cache": false
        }
    }
}
