{
	"$schema": "https://biomejs.dev/schemas/1.9.3/schema.json",
	"files": {
		"ignore": ["**/smoke/**", "**/fixtures/**", "**/_temp-fixtures/**", "**/vendor/**"],
		"include": ["test/**", "e2e/**", "packages/**", "scripts/**", "benchmark"],
	},
	"vcs": {
		"enabled": true,
		"clientKind": "git",
		"useIgnoreFile": true,
	},
	"formatter": {
		"indentStyle": "tab",
		"indentWidth": 2,
		"lineWidth": 100,
		"ignore": [".changeset", "pnpm-lock.yaml", "*.astro"],
	},
	"organizeImports": {
		"enabled": true,
	},
	"linter": {
		"enabled": true,
		"rules": {
			"recommended": false,
			"style": {
				"useNodejsImportProtocol": "error",
				// Enforce separate type imports for type-only imports to avoid bundling unneeded code
				"useImportType": "error",
			},
			"suspicious": {
				// This one is specific to catch `console.log`. The rest of logs are permitted
				"noConsoleLog": "warn",
			},
			"correctness": {
				"noUnusedVariables": "info",
				"noUnusedFunctionParameters": "info",
				"noUnusedImports": "warn",
			},
		},
	},
	"javascript": {
		"formatter": {
			"trailingCommas": "all",
			"quoteStyle": "single",
			"semicolons": "always",
		},
	},
	"json": {
		"parser": {
			"allowComments": true,
			"allowTrailingCommas": true,
		},
		"formatter": {
			"indentStyle": "space",
			"trailingCommas": "none",
		},
	},
	"overrides": [
		{
			// Workaround to format files like npm does
			"include": ["package.json"],
			"json": {
				"formatter": {
					"lineWidth": 1,
				},
			},
		},
		{
			// We don"t want to have node modules in code that should be runtime agnostic
			"include": ["packages/astro/src/runtime/**/*.ts"],
			"linter": {
				"rules": {
					"correctness": {
						"noNodejsModules": "error",
					},
				},
			},
		},
		{
			"include": ["*.test.js"],
			"linter": {
				"rules": {
					"suspicious": {
						"noFocusedTests": "error",
						"noConsole": "off",
					},
				},
			},
		},
		{
			"include": ["*.astro", "client.d.ts", "jsx-runtime.d.ts"],
			"linter": {
				"rules": {
					"correctness": {
						"noUnusedVariables": "off",
						"noUnusedImports": "off",
					},
				},
			},
		},
		{
			"include": ["packages/integrations/**/*.ts"],
			"linter": {
				"rules": {
					"suspicious": {
						"noConsole": {
							"level": "error",
							"options": {
								"allow": ["warn", "error", "info", "debug"],
							},
						},
					},
				},
			},
		},
		{
			"include": [
				"packages/db/**/cli/**/*.ts",
				"benchmark/**/*.js",
				"packages/astro/src/cli/**/*.ts",
				"packages/astro/astro.js",
			],
			"linter": {
				"rules": {
					"suspicious": {
						"noConsole": "off",
						"noConsoleLog": "off",
					},
				},
			},
		},
	],
}

import path from 'node:path';
import { fileURLToPath } from 'node:url';

import tseslint from 'typescript-eslint';

// plugins
import regexpEslint from 'eslint-plugin-regexp';
const typescriptEslint = tseslint.plugin;

// parsers
const typescriptParser = tseslint.parser;

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

export default [
	// If ignores is used without any other keys in the configuration object, then the patterns act as global ignores.
	// ref: https://eslint.org/docs/latest/use/configure/configuration-files#globally-ignoring-files-with-ignores
	{
		ignores: [
			'**/.*',
			'**/*.d.ts',
			'packages/**/*.min.js',
			'packages/**/dist/',
			'packages/**/fixtures/',
			'packages/astro/vendor/vite/',
			'benchmark/**/dist/',
			'examples/',
			'scripts/',
			'.github/',
			'.changeset/',
		],
	},

	...tseslint.configs.recommendedTypeChecked,
	...tseslint.configs.stylisticTypeChecked,
	regexpEslint.configs['flat/recommended'],
	{
		languageOptions: {
			parser: typescriptParser,
			parserOptions: {
				project: ['./packages/*/tsconfig.json', './tsconfig.eslint.json'],
				tsconfigRootDir: __dirname,
			},
		},
		plugins: {
			'@typescript-eslint': typescriptEslint,
			regexp: regexpEslint,
		},
		rules: {
			// These off/configured-differently-by-default rules fit well for us
			'@typescript-eslint/switch-exhaustiveness-check': 'error',
			'@typescript-eslint/no-shadow': 'error',
			'no-console': 'off',

			// Todo: do we want these?
			'@typescript-eslint/no-unused-vars': 'off',
			'@typescript-eslint/array-type': 'off',
			'@typescript-eslint/ban-ts-comment': 'off',
			'@typescript-eslint/class-literal-property-style': 'off',
			'@typescript-eslint/consistent-indexed-object-style': 'off',
			'@typescript-eslint/consistent-type-definitions': 'off',
			'@typescript-eslint/dot-notation': 'off',
			'@typescript-eslint/no-base-to-string': 'off',
			'@typescript-eslint/no-empty-function': 'off',
			'@typescript-eslint/no-floating-promises': 'off',
			'@typescript-eslint/no-misused-promises': 'off',
			'@typescript-eslint/no-redundant-type-constituents': 'off',
			'@typescript-eslint/no-this-alias': 'off',
			'@typescript-eslint/no-unsafe-argument': 'off',
			'@typescript-eslint/no-unsafe-assignment': 'off',
			'@typescript-eslint/no-unsafe-call': 'off',
			'@typescript-eslint/no-unsafe-member-access': 'off',
			'@typescript-eslint/no-unused-expressions': 'off',
			'@typescript-eslint/only-throw-error': 'off',
			'@typescript-eslint/no-unsafe-return': 'off',
			'@typescript-eslint/no-unnecessary-type-assertion': 'off',
			'@typescript-eslint/prefer-nullish-coalescing': 'off',
			'@typescript-eslint/prefer-optional-chain': 'off',
			'@typescript-eslint/prefer-promise-reject-errors': 'off',
			'@typescript-eslint/prefer-string-starts-ends-with': 'off',
			'@typescript-eslint/require-await': 'off',
			'@typescript-eslint/restrict-plus-operands': 'off',
			'@typescript-eslint/restrict-template-expressions': 'off',
			'@typescript-eslint/sort-type-constituents': 'off',
			'@typescript-eslint/unbound-method': 'off',
			'@typescript-eslint/no-explicit-any': 'off',

			// Used by Biome
			'@typescript-eslint/consistent-type-imports': 'off',
			// These rules enabled by the preset configs don't work well for us
			'@typescript-eslint/await-thenable': 'off',
			'prefer-const': 'off',

			// In some cases, using explicit letter-casing is more performant than the `i` flag
			'regexp/use-ignore-case': 'off',
			'regexp/prefer-regexp-exec': 'warn',
			'regexp/prefer-regexp-test': 'warn',
		},
	},
	{
		files: ['packages/astro/src/runtime/client/**/*.ts'],
		languageOptions: {
			globals: {
				browser: true,
			},
		},
	},
	{
		files: ['packages/astro/src/core/errors/errors-data.ts'],
		rules: {
			// This file is used for docs generation, as such the code need to be in a certain format, we can somewhat ensure this with these rules
			'object-shorthand': ['error', 'methods', { avoidExplicitReturnArrows: true }],
			'arrow-body-style': ['error', 'never'],
		},
	},

	{
		files: ['packages/db/src/runtime/**/*.ts'],
		rules: {
			'no-restricted-imports': 'off',
			'@typescript-eslint/no-restricted-imports': [
				'error',
				{
					patterns: [
						{
							group: ['../core/*'],
							allowTypeImports: true,
						},
					],
				},
			],
		},
	},
];

/** @type {import("prettier").Config} */
export default {
	printWidth: 100,
	semi: true,
	singleQuote: true,
	tabWidth: 2,
	trailingComma: 'all',
	useTabs: true,
	plugins: ['prettier-plugin-astro'],
	overrides: [
		{
			files: ['.*', '*.md', '*.toml', '*.yml'],
			options: {
				useTabs: false,
			},
		},
		{
			files: ['**/*.astro'],
			options: {
				parser: 'astro',
			},
		},
	],
};

{
  "compilerOptions": {
    "allowJs": true
  },
  "extends": "./tsconfig.base.json"
}

// Yes this file is intentionally empty!
// ---
// Having a blank `tsconfig.json` file prevents TypeScript from crawling up your directory tree
// and possibly picking up a parent `tsconfig.json` (which, unsurprisingly, is very hard to debug)
{}

{
	"$schema": "https://turborepo.org/schema.json",
	"tasks": {
		"build": {
			"dependsOn": [
				"^build"
			],
			"inputs": [
				"**/*",
				"!test/**/*",
				"!e2e/**/*",
				"!performance/**/*",
				"!.astro/**/*",
				"!.cache/**/*",
				"!mod.js",
				"!mod.js.map"
			],
			"outputs": [
				"dist/**/*",
				"!vendor/**",
				"mod.js",
				"mod.js.map"
			],
			"outputLogs": "new-only"
		},
		"build:ci": {
			"dependsOn": [
				"^build:ci"
			],
			"inputs": [
				"**/*",
				"!test/**/*",
				"!e2e/**/*",
				"!performance/**/*",
				"!.astro/**/*",
				"!.cache/**/*",
				"!mod.js",
				"!mod.js.map"
			],
			"outputs": [
				"dist/**/*",
				"!vendor/**",
				"mod.js",
				"mod.js.map"
			],
			"outputLogs": "new-only"
		},
		"dev": {
			"cache": false,
			"persistent": true
		},
		"test": {
			"dependsOn": [
				"^test"
			],
			"env": [
				"RUNNER_OS",
				"NODE_VERSION"
			],
			"outputLogs": "new-only"
		},
		"test:hosted": {
			"cache": false,
			"outputLogs": "new-only"
		}
	}
}
