{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "rangeStrategy": "replace",
  "semanticCommits": "enabled",
  "semanticCommitType": "build",
  "semanticCommitScope": "",
  "separateMajorMinor": false,
  "prHourlyLimit": 2,
  "labels": ["target: minor", "action: merge"],
  "timezone": "America/Tijuana",
  "postUpgradeTasks": {
    "commands": [
      "git restore .yarn/releases/yarn-4.5.0.cjs pnpm-lock.yaml",
      "yarn install --immutable",
      "yarn bazel run @npm2//:sync || true"
    ],
    "fileFilters": [".aspect/rules/external_repository_action_cache/**/*", "pnpm-lock.yaml"],
    "executionMode": "branch"
  },
  "lockFileMaintenance": {
    "enabled": true
  },
  "dependencyDashboard": true,
  "schedule": ["after 10:00pm every weekday", "before 4:00am every weekday", "every weekend"],
  "baseBranches": ["main"],
  "ignoreDeps": ["@types/node", "@types/express", "build_bazel_rules_nodejs", "rules_pkg", "yarn"],
  "includePaths": [
    "WORKSPACE",
    "package.json",
    "packages/**/package.json",
    "tests/legacy-cli/e2e/ng-snapshot/package.json",
    ".github/workflows/**/*.yml"
  ],
  "packageRules": [
    {
      "matchPackageNames": ["quicktype-core"],
      "schedule": ["before 4:00am on the first day of the month"]
    },
    {
      "matchCurrentVersion": "0.0.0-PLACEHOLDER",
      "enabled": false
    },
    {
      "matchCurrentVersion": "0.0.0-EXPERIMENTAL-PLACEHOLDER",
      "enabled": false
    },
    {
      "groupName": "angular",
      "matchDepNames": ["/^@angular/.*/", "/angular/dev-infra/"]
    },
    {
      "groupName": "babel",
      "matchDepNames": ["/^@babel/.*/"]
    },
    {
      "groupName": "bazel",
      "matchDepNames": ["/^@bazel/.*/", "/^build_bazel.*/"]
    },
    {
      "separateMinorPatch": true,
      "matchPackageNames": ["typescript", "rxjs", "tslib"]
    },
    {
      "enabled": false,
      "matchPackageNames": ["typescript", "rxjs", "tslib"],
      "matchUpdateTypes": ["major"]
    },
    {
      "enabled": false,
      "matchPackageNames": ["typescript"],
      "matchUpdateTypes": ["minor"]
    },
    {
      "matchFileNames": [
        "packages/angular_devkit/schematics_cli/blank/project-files/package.json",
        "packages/angular_devkit/schematics_cli/schematic/files/package.json",
        "packages/schematics/angular/utility/latest-versions/package.json"
      ],
      "matchPackageNames": ["*"],
      "groupName": "schematics dependencies",
      "groupSlug": "all-schematics-dependencies",
      "lockFileMaintenance": {
        "enabled": false
      }
    },
    {
      "matchFileNames": [
        "!packages/angular_devkit/schematics_cli/blank/project-files/package.json",
        "!packages/angular_devkit/schematics_cli/schematic/files/package.json",
        "!packages/schematics/angular/utility/latest-versions/package.json"
      ],
      "matchPackageNames": ["*", "!/^@angular/.*/", "!/angular/dev-infra/"],
      "matchUpdateTypes": ["minor", "patch"],
      "groupName": "all non-major dependencies",
      "groupSlug": "all-minor-patch"
    },
    {
      "matchFileNames": [".github/workflows/scorecard.yml"],
      "matchPackageNames": ["*"],
      "groupName": "scorecard action dependencies",
      "groupSlug": "scorecard-action"
    }
  ]
}

/**
 * Root tsconfig file for use building all Angular packages. Note there is no rootDir
 * and therefore any tsconfig in the package directory will need to define its own
 * rootDir.
 */
{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "module": "esnext",
    "target": "es2022",
    "lib": ["es2020"],
    // don't auto-discover @types/node, it results in a ///<reference in the .d.ts output
    "types": [],
    "sourceMap": true,
    "inlineSources": true,
    "importHelpers": true
  }
}

{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "types": ["node"]
  }
}

{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "inlineSourceMap": true,
    "sourceRoot": ".",
    // Inline sources are necessary for our tests to show the proper sources, since we are using
    // Istanbul (not Constantinople) as well, and applying both source maps to get the original
    // source in devtools.
    "inlineSources": true,
    "types": ["node", "jasmine"],
    "plugins": [
      {
        "name": "@bazel/tsetse",
        // TODO: Cleanup tests and remove this rule disable
        "disabledRules": ["must-type-assert-json-parse"]
      }
    ]
  }
}
