module.exports = {
	/**
	 * https://prettier.io/docs/en/options.html#semicolons
	 */
	semi: true,

	/**
	 * https://prettier.io/docs/en/options.html#trailing-commas
	 */
	trailingComma: 'all',

	/**
	 * https://prettier.io/docs/en/options.html#bracket-spacing
	 */
	bracketSpacing: true,

	/**
	 * https://prettier.io/docs/en/options.html#tabs
	 */
	useTabs: true,

	/**
	 * https://prettier.io/docs/en/options.html#tab-width
	 */
	tabWidth: 2,

	/**
	 * https://prettier.io/docs/en/options.html#arrow-function-parentheses
	 */
	arrowParens: 'always',

	/**
	 * https://prettier.io/docs/en/options.html#quotes
	 */
	singleQuote: true,

	/**
	 * https://prettier.io/docs/en/options.html#quote-props
	 */
	quoteProps: 'as-needed',

	/**
	 * https://prettier.io/docs/en/options.html#end-of-line
	 */
	endOfLine: 'lf',

	/**
	 * https://prettier.io/docs/en/options.html#print-width
	 */
	printWidth: 100,
};

{
	"$schema": "./node_modules/@biomejs/biome/configuration_schema.json",
	"vcs": {
		"clientKind": "git",
		"enabled": true,
		"useIgnoreFile": true
	},
	"files": {
		"ignore": [
			"**/.turbo",
			"**/coverage",
			"**/dist",
			"**/package.json",
			"**/pnpm-lock.yaml",
			"**/CHANGELOG.md"
		]
	},
	"formatter": {
		"enabled": true,
		"formatWithErrors": false,
		"indentStyle": "tab",
		"indentWidth": 2,
		"lineEnding": "lf",
		"lineWidth": 100,
		"attributePosition": "auto",
		"ignore": [
			// Handled by prettier
			"**/*.vue"
		]
	},
	"organizeImports": { "enabled": false },
	"linter": {
		"enabled": false
	},
	"javascript": {
		"parser": {
			"unsafeParameterDecoratorsEnabled": true
		},
		"formatter": {
			"jsxQuoteStyle": "double",
			"quoteProperties": "asNeeded",
			"trailingCommas": "all",
			"semicolons": "always",
			"arrowParentheses": "always",
			"bracketSpacing": true,
			"bracketSameLine": false,
			"quoteStyle": "single",
			"attributePosition": "auto"
		}
	}
}

const { compilerOptions } = require('./tsconfig.json');

/** @type {import('ts-jest').TsJestGlobalOptions} */
const tsJestOptions = {
	isolatedModules: true,
	tsconfig: {
		...compilerOptions,
		declaration: false,
		sourceMap: true,
	},
};

const { baseUrl, paths } = require('get-tsconfig').getTsconfig().config?.compilerOptions;

/** @type {import('jest').Config} */
const config = {
	verbose: true,
	testEnvironment: 'node',
	testRegex: '\\.(test|spec)\\.(js|ts)$',
	testPathIgnorePatterns: ['/dist/', '/node_modules/'],
	transform: {
		'^.+\\.ts$': ['ts-jest', tsJestOptions],
	},
	// This resolve the path mappings from the tsconfig relative to each jest.config.js
	moduleNameMapper: Object.entries(paths || {}).reduce((acc, [path, [mapping]]) => {
		path = `^${path.replace(/\/\*$/, '/(.*)$')}`;
		mapping = mapping.replace(/^\.?\.\/(?:(.*)\/)?\*$/, '$1');
		mapping = mapping ? `/${mapping}` : '';
		acc[path] = mapping.startsWith('/test')
			? '<rootDir>' + mapping + '/$1'
			: '<rootDir>' + (baseUrl ? `/${baseUrl.replace(/^\.\//, '')}` : '') + mapping + '/$1';
		return acc;
	}, {}),
	setupFilesAfterEnv: ['jest-expect-message'],
	collectCoverage: process.env.COVERAGE_ENABLED === 'true',
	coverageReporters: ['text-summary'],
	collectCoverageFrom: ['src/**/*.ts'],
};

if (process.env.CI === 'true') {
	config.workerIdleMemoryLimit = 1024;
	config.coverageReporters = ['cobertura'];
}

module.exports = config;

{
	"compilerOptions": {
		"types": ["node", "jest"]
	}
}

{
	"compilerOptions": {
		"types": ["node"],
		"noUnusedLocals": false,
		"noUnusedParameters": false,
		"declaration": true
	},
	"tsc-alias": {
		"replacers": {
			"base-url": {
				"enabled": false
			}
		}
	}
}

{
	"$schema": "https://turbo.build/schema.json",
	"ui": "stream",
	"globalEnv": ["CI", "COVERAGE_ENABLED"],
	"tasks": {
		"clean": {
			"cache": false
		},
		"build:backend": {
			"dependsOn": ["n8n#build"]
		},
		"build:frontend": {
			"dependsOn": ["n8n-editor-ui#build"]
		},
		"build:nodes": {
			"dependsOn": ["n8n-nodes-base#build", "@n8n/n8n-nodes-langchain#build"]
		},
		"build": {
			"dependsOn": ["^build"],
			"outputs": ["dist/**"]
		},
		"typecheck": {
			"dependsOn": ["^typecheck"]
		},
		"format": {},
		"format:check": {},
		"lint:backend": {
			"dependsOn": [
				"@n8n/api-types#lint",
				"@n8n/config#lint",
				"@n8n/di#lint",
				"@n8n/client-oauth2#lint",
				"@n8n/imap#lint",
				"@n8n/permissions#lint",
				"@n8n/task-runner#lint",
				"n8n-workflow#lint",
				"n8n-core#lint",
				"n8n-node-dev#lint",
				"n8n#lint"
			]
		},
		"lint:frontend": {
			"dependsOn": [
				"@n8n/chat#lint",
				"@n8n/codemirror-lang#lint",
				"@n8n/storybook#lint",
				"n8n-cypress#lint",
				"n8n-design-system#lint",
				"n8n-editor-ui#lint"
			]
		},
		"lint:nodes": {
			"dependsOn": [
				"n8n-nodes-base#lint",
				"@n8n/n8n-nodes-langchain#lint",
				"@n8n/json-schema-to-zod#lint"
			]
		},
		"lint": {},
		"lintfix": {},
		"test:backend": {
			"dependsOn": [
				"@n8n/api-types#test",
				"@n8n/config#test",
				"@n8n/di#test",
				"@n8n/client-oauth2#test",
				"@n8n/imap#test",
				"@n8n/permissions#test",
				"@n8n/task-runner#test",
				"n8n-workflow#test",
				"n8n-core#test",
				"n8n#test"
			],
			"outputs": ["coverage/**"]
		},
		"test:frontend": {
			"dependsOn": [
				"@n8n/chat#test",
				"@n8n/codemirror-lang#test",
				"n8n-design-system#test",
				"n8n-editor-ui#test"
			],
			"outputs": ["coverage/**"]
		},
		"test:nodes": {
			"dependsOn": [
				"n8n-nodes-base#test",
				"@n8n/n8n-nodes-langchain#test",
				"@n8n/json-schema-to-zod#test"
			],
			"outputs": ["coverage/**"]
		},
		"test": {},
		"watch": {
			"cache": false,
			"persistent": true
		},
		"dev": {
			"cache": false,
			"persistent": true
		}
	}
}
