import { defineConfig, type Options } from 'tsup'

const OUT_DIR = 'dist'

function makeBuildOptions(
  fileName: string,
  entryPoint: string,
  format?: 'esm' | 'browser',
  overwrites: Options = {}
): Options[] {
  const options = {
    entryPoints: { [fileName]: entryPoint },
    format: 'esm',
    outDir: OUT_DIR,
    platform: 'browser',
    target: 'es2022',
    cjsInterop: false,
    dts: true,
    sourcemap: true,
    external: ['react', 'vue', 'lenis'],
    outExtension: () =>
      format === 'esm' ? { js: '.mjs', dts: '.d.ts' } : { js: '.js' },
    ...overwrites,
  } satisfies Options

  const minifyOptions = {
    ...options,
    minify: 'terser',
    terserOptions: {
      mangle: {
        reserved: ['Lenis'],
      },
    },
    outExtension: () => ({ js: '.min.js' }),
    ...overwrites,
  } satisfies Options

  return format === 'esm' ? [options] : [options, minifyOptions]
}

// Builds
export const coreESMOptions = makeBuildOptions(
  'lenis',
  'packages/core/index.ts',
  'esm'
)
const coreBrowserOptions = makeBuildOptions(
  'lenis',
  'packages/core/browser.ts',
  'browser',
  { dts: false }
)
const coreCSSOptions = makeBuildOptions(
  'lenis',
  'packages/core/lenis.css',
  undefined,
  { dts: false, sourcemap: false, minify: true }
)

