module.exports = {
  plugins: {
    'posthtml-expressions': {
      root: __dirname,
      locals: {
        NODE_ENV: process.env.NODE_ENV
      }
    },
    'posthtml-include': {
      root: __dirname
    },
    'posthtml-md': {
      root: __dirname
    }
  }
};

const path = require('path')

const CWD = process.cwd()

module.exports = {
  includePaths: [
    path.resolve(CWD, 'node_modules'),
    path.resolve(CWD, 'src')
  ]
}

