		{
			"files": [
				"*.js"
			],
			"rules": {
				"jsdoc/no-types": "off"
			}
		},
		{
			"files": [
				"**/*.test.ts"
			],
			"rules": {
				"local/code-must-use-super-dispose": "off",
				"local/code-no-test-only": "error",
				"local/code-no-test-async-suite": "warn",
				"local/code-no-unexternalized-strings": "off",
				"local/code-must-use-result": [
					"warn",
