{
    "git": {
        "commitMessage": "chore(release): release v${version}",
        "tagName": "v${version}"
    },
    "npm": false,
    "github": {
        "release": true
    },
    "plugins": {
        "@release-it/conventional-changelog": {
            "preset": "angular",
            "infile": "CHANGELOG.md",
            "ignoreRecommendedBump": true
        },

        "@release-it-plugins/workspaces": {
            "publish": false,
            "skipChecks": true,
            "workspaces": [
                "common/*",
                "packages/*",
                "packages-experimental/action-recorder"
            ]
        }
    }
}

import antfu from '@antfu/eslint-config';
import { baseRules, facadePreset, specPreset, tailwindcssPreset, typescriptPreset, univerSourcePreset } from '@univerjs-infra/shared/eslint';
import header from 'eslint-plugin-header';
import barrel from 'eslint-plugin-no-barrel-import';
import penetrating from 'eslint-plugin-no-penetrating-import';

header.rules.header.meta.schema = false;

export default antfu(
    {
        stylistic: {
            indent: 4,
            semi: true,
        },
        regexp: false,
        react: true,
        yaml: {
            overrides: {
                'yaml/indent': ['error', 4, { indicatorValueIndent: 2 }],
            },
        },
        markdown: false,
        typescript: true,
        formatters: {
            css: true,
            html: true,
        },
        rules: baseRules,
        ignores: [
            'mockdata/**/*.json',
        ],
    }, {
        files: ['**/*.ts', '**/*.tsx'],
        ignores: [
            'packages/engine-render/src/components/docs/**/*.ts',
            '**/*.tsx',
            '**/*.d.ts',
            '**/vite.config.ts',
            'playwright.config.ts',
            '**/*.spec.ts',
            '**/*.spec.tsx',
            '**/*.test.ts',
            '**/*.test.tsx',
        ], // do not check test files
        rules: {
            complexity: ['warn', { max: 20 }],
            'max-lines-per-function': ['warn', 80],
        },
    }, {
        files: ['**/*.ts', '**/*.tsx'],
        ignores: ['**/*.d.ts', '**/vite.config.ts', '**/vitest.config.ts', 'playwright.config.ts'],
        plugins: {
            header,
            barrel,
        },
        rules: {
            'barrel/no-barrel-import': 2,
            'header/header': [
                2,
                'block',
                [
                    '*',
                    ' * Copyright 2023-present DreamNum Inc.',
                    ' *',
                    ' * Licensed under the Apache License, Version 2.0 (the "License");',
                    ' * you may not use this file except in compliance with the License.',
                    ' * You may obtain a copy of the License at',
                    ' *',
                    ' *     http://www.apache.org/licenses/LICENSE-2.0',
                    ' *',
                    ' * Unless required by applicable law or agreed to in writing, software',
                    ' * distributed under the License is distributed on an "AS IS" BASIS,',
                    ' * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.',
                    ' * See the License for the specific language governing permissions and',
                    ' * limitations under the License.',
                    ' ',
                ],
                2,
            ],
        },
    }, {
    // Not penetrating for source files
        files: ['**/*.ts', '**/*.tsx'],
        plugins: {
            penetrating,
        },
        ignores: [
            '**/__tests__/**/*',
            '**/__testing__/**/*',
            'examples/**/*',
        ],
        rules: {
            'penetrating/no-penetrating-import': 2,
        },
    }, {
        files: ['**/*/src/index.ts'],
        rules: {
            'perfectionist/sort-exports': 'off',
        },
    },
    typescriptPreset(),
    univerSourcePreset(),
    facadePreset(),
    tailwindcssPreset(),
    specPreset()
);

/* eslint-disable node/prefer-global/process */
import { defineConfig, devices } from '@playwright/test';

/**
 * If you are running tests in CI, you can use the `CI` environment variable to adjust the configuration.
 */
const isCI = !!process.env.CI;
const HEADLESS = !!process.env.HEADLESS;

/**
 * Read environment variables from file.
 * https://github.com/motdotla/dotenv
 */
// require('dotenv').config();

/**
 * See https://playwright.dev/docs/test-configuration.
 */
export default defineConfig({
    testDir: './e2e',
    /* Run tests in files in parallel */
    fullyParallel: true,
    /* Fail the build on CI if you accidentally left test.only in the source code. */
    forbidOnly: !!process.env.CI,
    timeout: 30_000,
    /* Retry on CI only */
    retries: process.env.CI ? 2 : 0,
    /* Opt out of parallel tests on CI. */
    workers: process.env.CI ? 1 : undefined,
    /* Reporter to use. See https://playwright.dev/docs/test-reporters */
    reporter: [
        ['json', { outputFile: 'playwright-report.json' }],
        ['html', { attachments: true }],
    ],
    // outputDir: './playwright-report',
    /* Shared settings for all the projects below. See https://playwright.dev/docs/api/class-testoptions. */
    use: {
        /* Base URL to use in actions like `await page.goto('/')`. */
        baseURL: 'http://127.0.0.1:3000',

        /* Collect trace when retrying the failed test. See https://playwright.dev/docs/trace-viewer */
        trace: 'retain-on-failure',
    },
    /* Configure projects for major browsers */
    projects: [
        {
            name: 'chromium',
            use: { ...devices['Desktop Chrome'], headless: isCI || HEADLESS },
        },

        // {
        //   name: 'firefox',
        //   use: { ...devices['Desktop Firefox'] },
        // },

        // {
        //   name: 'webkit',
        //   use: { ...devices['Desktop Safari'] },
        // },

        /* Test against mobile viewports. */
        // {
        //   name: 'Mobile Chrome',
        //   use: { ...devices['Pixel 5'] },
        // },
        // {
        //   name: 'Mobile Safari',
        //   use: { ...devices['iPhone 12'] },
        // },

        /* Test against branded browsers. */
        // {
        //   name: 'Microsoft Edge',
        //   use: { ...devices['Desktop Edge'], channel: 'msedge' },
        // },
        // {
        //   name: 'Google Chrome',
        //   use: { ...devices['Desktop Chrome'], channel: 'chrome' },
        // },
    ],

    webServer: {
        command: 'pnpm serve:e2e',
        url: 'http://localhost:3000',
        reuseExistingServer: !isCI,
        timeout: 10_000,
        stdout: 'ignore',
        stderr: 'pipe',
    },
});

{
    "$schema": "https://turbo.build/schema.json",
    "extends": [
        "//"
    ],
    "tasks": {
        "test": {
        },

        "coverage": {
            "cache": false
        },

        "build": {
        },

        "build:e2e": {},

        "dev:demo": {
            "cache": false
        },

        "lint:types": {}
    }
}
