}

{
    "version": "3.7.0",
    "npmClient": "pnpm",
    "conventionalCommits": true,
    "command": {
        "version": {
            "message": "chore(release): published new version"
        }
    },
    "$schema": "node_modules/lerna/schemas/lerna-schema.json"
}

{
    "targetDefaults": {
