            }
        },
        "local": {
            "runner": "nx/tasks-runners/default",
            "options": {
                "cacheableOperations": [
                    "build",
                    "build:ci",
                    "lint",
                    "test",
                    "e2e"
                ]
            }
        }
    },
    "affected": {
        "defaultBase": "main"
    },
    "pluginsConfig": {
        "@nrwl/js": {
            "analyzeSourceFiles": false
        }
    },
    "$schema": "./node_modules/nx/schemas/nx-schema.json",
    "namedInputs": {
        "default": [
            "{projectRoot}/**/*",
            "sharedGlobals"
        ],
        "sharedGlobals": [],
        "production": [
            "default"
        ]
    }
}

