{
  "$schema": "https://tolgee.io/cli-schema.json",
  "projectId": 8454,
  "pull": {
    "path": "./apps/builder/src/i18n",
    "delimiter": null
  }
}

{
  "$schema": "./node_modules/@biomejs/biome/configuration_schema.json",
  "organizeImports": {
    "ignore": [
      "emojiList.json",
      "iconNames.ts",
      ".last-run.json",
      "openapi/*.json"
    ],
    "enabled": true
  },
  "linter": {
    "ignore": ["node_modules", ".next", "dist", "openapi/*.json"],
    "enabled": true,
    "rules": {
      "recommended": true,
      "security": {
        "noDangerouslySetInnerHtml": "off",
        "noGlobalEval": "off"
      },
      "a11y": {
        "noSvgWithoutTitle": "off",
        "useMediaCaption": "off",
        "useIframeTitle": "off",
        "useKeyWithClickEvents": "off",
        "useButtonType": "off",
        "noRedundantAlt": "off",
        "noAutofocus": "off",
        "useSemanticElements": "off",
        "useFocusableInteractive": "off"
      },
      "performance": {
        "noAccumulatingSpread": "off"
      },
      "style": {
        "noNonNullAssertion": "off",
        "useNodejsImportProtocol": "off",
        "noUnusedTemplateLiteral": "off",
        "useTemplate": "off",
        "useSelfClosingElements": "off",
        "noUselessElse": "off",
        "noParameterAssign": "off",
        "useShorthandFunctionType": "off",
        "useExponentiationOperator": "off",
        "useSingleVarDeclarator": "off"
      },
      "correctness": {
        "useExhaustiveDependencies": "off",
        "noSwitchDeclarations": "off",
        "noEmptyPattern": "off",
        "noUnsafeOptionalChaining": "off",
        "useJsxKeyInIterable": "off"
      },
      "complexity": {
        "noForEach": "off",
        "noUselessTernary": "off",
        "useLiteralKeys": "off",
        "useOptionalChain": "off",
        "noBannedTypes": "off"
      },
      "suspicious": {
        "noArrayIndexKey": "off",
        "noAssignInExpressions": "off",
        "noImplicitAnyLet": "off",
        "noExplicitAny": "off",
        "noExportsInTest": "off",
        "noConfusingVoidType": "off",
        "noGlobalIsNan": "off",
        "useIsArray": "off",
        "noThenProperty": "off",
        "noAsyncPromiseExecutor": "off",
        "noMisleadingCharacterClass": "off"
      }
    }
  },
  "formatter": {
    "ignore": [
      "emojiList.json",
      "iconNames.ts",
      ".last-run.json",
      "openapi/*.json"
    ],
    "indentStyle": "space"
  },
  "vcs": {
    "enabled": true,
    "clientKind": "git",
    "useIgnoreFile": true
  }
}

{
  "extends": "./packages/tsconfig/base.json"
}

{
  "$schema": "https://turbo.build/schema.json",
  "globalDependencies": [".env"],
  "globalEnv": ["DATABASE_URL", "SKIP_ENV_CHECK"],
  "globalPassThroughEnv": ["ENCRYPTION_SECRET"],
  "tasks": {
    "dev": {
      "dependsOn": ["^db:generate", "^db:push", "@typebot.io/react#build"],
      "persistent": true
    },
    "build": {
      "env": ["VERCEL_*", "NEXTAUTH_URL", "SENTRY_*", "LANDING_PAGE_URL"],
      "dependsOn": ["^build", "^db:generate"],
      "outputs": [
        ".next/**",
        "!.next/cache/**",
        "dist/**",
        "build/**",
        "public/__ENV.js"
      ],
      "inputs": ["$TURBO_DEFAULT$", ".env"],
      "outputLogs": "new-only"
    },
    "docs#build": {
      "dependsOn": ["api:generate"],
      "outputs": ["build/**"],
      "outputLogs": "new-only"
    },
    "api:generate": {
      "dependsOn": ["bot-engine#build", "@typebot.io/prisma#db:generate"],
      "cache": false
    },
    "db:generate": {
      "cache": false
    },
    "db:migrate": {
      "cache": false
    },
    "db:push": {
      "cache": false
    },
    "db:cleanDatabase": {
      "env": ["NEXTAUTH_URL"],
      "dependsOn": ["@typebot.io/prisma#db:generate"],
      "cache": false
    },
    "checkAndReportChatsUsage": {
      "env": ["STRIPE_*", "NEXTAUTH_URL", "SMTP_*"],
      "dependsOn": ["@typebot.io/prisma#db:generate"],
      "cache": false
    },
    "//#format-and-lint": {},
    "//#format-and-lint:fix": {
      "cache": false
    }
  }
}
