require('./build/gulpfile');

{
	"setup": [
		"setup integration tests",
		"setup extension tests"
	],
	"pr": [
		"dacpac.*"
	],
	"windows": [
		"bds.*"
	]
}

{
	"include": [
		"src/sql/**/*.ts"
	],
}
