{
  "packages": [
    "packages/*",
    "packages-serverless/*",
    "packages-legacy/*",
    "packages-resource/*"
  ],
  "command": {
    "bootstrap": {
      "hoist": true,
      "noCi": true,
      "npmClientArgs": [
        "--no-package-lock",
        "--legacy-peer-deps"
      ],
      "nohoist": [
        "@midwayjs/faas-middleware-upload",
        "midway-test-component",
        "typeorm"
      ]
    },
    "publish": {
      "ignoreChanges": [
        "*.md",
        "**/test/**",
        "jest.config.js",
        "jest.setup.js",
        "site/**"
      ]
    },
    "version": {
      "forcePublish": "@midwayjs/version"
    }
  },
  "changelog": {
    "repo": "midwayjs/midway",
    "labels": {
      "pr: breaking change": ":boom: Breaking Change",
      "pr: new feature": ":rocket: New Feature",
      "pr: bug fix": ":bug: Bug Fix",
      "pr: performance": ":running_woman: Performance",
      "pr: polish": ":nail_care: Polish",
      "pr: documentation": ":memo: Documentation",
      "pr: maintenance": ":wrench: Maintenance",
      "pr: dependencies": ":package: Dependencies"
    },
    "cacheDir": ".changelog"
  },
  "npmClient": "npm",
  "version": "3.19.3"
}

{
  "extends": [
    "config:base"
  ],
  "ignoreDeps": [
    "egg",
    "egg-mock",
    "egg-cluster",
    "react",
    "react-dom",
    "@midwayjs/cookies",
    "@midwayjs/logger",
    "@midwayjs/gateway-common-http",
    "@midwayjs/serverless-spec-builder"
  ],
  "ignorePaths": ["**/fixtures/**", "**/example/**", "**/site/**", "**/benchmark/**"],
  "schedule": ["before 3am on Friday"],
  "labels": ["pr: dependencies"],
  "packageRules": [
    {
      "matchDepTypes": ["devDependencies"],
      "matchUpdateTypes": ["minor", "patch"],
      "schedule": ["at any time"],
      "prConcurrentLimit": 2,
      "platformAutomerge": true,
      "requiredStatusChecks": [
        "Node.js CI",
        "Pre Check"
      ]
    },
    {
      "matchPackageNames": ["cache-manager"],
      "matchFileNames": ["!packages-legacy/cache/package.json"]
    },
    {
      "groupName": "mongoose monorepo",
      "matchPackageNames": [
        "mongoose",
        "@typegoose/typegoose"
      ],
      "automerge": false
    },
    {
      "groupName": "bull monorepo",
      "matchPackageNames": [
        "bull",
        "@bull-board/api",
        "@bull-board/ui"
      ],
      "automerge": false
    },
    {
      "groupName": "otel",
      "matchPackageNames": [
        "@opentelemetry/api",
        "@opentelemetry/sdk-trace-base",
        "@opentelemetry/sdk-trace-node"
      ],
      "automerge": false
    }
  ]
}
