{
  "extends": [
    "group:babelMonorepo",
    "group:docusaurusMonorepo",
    ":prHourlyLimit2",
    "workarounds:all",
    "helpers:pinGitHubActionDigests"
  ],
  "timezone": "UTC",
  "rangeStrategy": "bump",
  "separateMajorMinor": true,
  "prConcurrentLimit": 2,
  "semanticCommits": "enabled",
  "commitMessagePrefix": "build(deps):",
  "ignoreDeps": [
    "@mdx-js/react",
    "@types/react",
    "execa",
    "chalk",
    "husky"
  ],
  "packageRules": [
    {
      "matchPaths": ["**/*"],
      "matchUpdateTypes": ["major"],
      "enabled": false
    },
    {
      "matchPaths": ["package.json"],
      "matchPackagePatterns": ["jest"],
      "excludePackageNames": ["eslint-plugin-jest"],
      "matchUpdateTypes": ["patch", "minor"],
      "groupName": "Jest packages"
    },
    {
      "extends": ["packages:eslint"],
      "excludePackageNames": ["eslint-plugin-prettier", "eslint-config-prettier"],
      "groupName": "ESLint packages"
    },
    {
      "matchPackagePatterns": ["prettier"],
      "groupName": "Prettier packages"
    },
    {
      "matchPackagePrefixes": ["esbuild"],
      "groupName": "Esbuild packages"
    },
    {
      "matchFiles": ["package.json"],
      "matchDepTypes": ["dependencies"],
      "matchPackagePatterns": ["jest"],
      "enabled": false
    },
    {
      "matchDepTypes": ["peerDependencies"],
      "enabled": false
    },
    {
      "matchPackagePatterns": ["@commitlint"],
      "enabled": false
    }
  ]
}

{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "target": "ES5",
    "declaration": true,
    "stripInternal": true,
    "outDir": "dist"
  },
  "include": ["src"],
  "exclude": ["**/*.spec.ts", "**/__mocks__", "src/__helpers__"]
}

{
  "extends": "./tsconfig.json",
  "include": ["src", "examples", "website", "scripts", "presets", "utils", "e2e", "*.config.ts"]
}
