{
  "extends": "stylelint-config-standard",
  "rules": {
    "declaration-block-single-line-max-declarations": 3,
    "selector-class-pattern": "maplibregl-[a-z-]+",
    "at-rule-no-unknown": [true, {
      "ignoreAtRules": ["svg-load"]
    }],
    "property-no-vendor-prefix": true,
    "function-no-unknown": [true, {
      "ignoreFunctions": ["svg-load", "svg-inline"]
    }]
  },
  "ignoreFiles": [
      "dist/**/*.css"
  ]
}

import typescriptEslint from '@typescript-eslint/eslint-plugin';
import stylisticTs from '@stylistic/eslint-plugin-ts';
import tsdoc from 'eslint-plugin-tsdoc';
import vitest from 'eslint-plugin-vitest';
import globals from 'globals';
import tsParser from '@typescript-eslint/parser';
import react from 'eslint-plugin-react';
import html from 'eslint-plugin-html';

export default [
    {
        ignores: ['build/*.js', 'staging/**', 'coverage/**', 'node_modules/**', 'docs/**', 'dist/**']
    },
    {
        ignores: ['test/bench/**'],
        files: ['**/*.ts', '**/*.js'],
        plugins: {
            '@typescript-eslint': typescriptEslint,
            '@stylistic': stylisticTs,
            tsdoc,
            vitest,
        },

        linterOptions: {
            reportUnusedDisableDirectives: true,
        },

        languageOptions: {
            globals: {
                ...Object.fromEntries(Object.entries(globals.browser).map(([key]) => [key, 'off'])),
                performance: true,
            },

            parser: tsParser,
            ecmaVersion: 5,
            sourceType: 'module',

            parserOptions: {
                createDefaultProgram: true,
            },
        },

        rules: {
            'no-dupe-class-members': 'off',
            '@typescript-eslint/no-dupe-class-members': ['error'],
            '@typescript-eslint/consistent-type-imports': ['error',{
                'fixStyle': 'inline-type-imports'
            }],
            '@typescript-eslint/no-unused-vars': ['warn', {
                argsIgnorePattern: '^_',
            }],

            '@stylistic/member-delimiter-style': ['error'],
            'no-useless-constructor': 'off',
            '@typescript-eslint/no-useless-constructor': ['error'],
            'no-undef': 'off',
            'no-use-before-define': 'off',
            'no-duplicate-imports': 'off',
            'implicit-arrow-linebreak': 'off',
            'arrow-parens': 'off',
            'arrow-body-style': 'off',
            'no-confusing-arrow': 'off',
            'no-control-regex': 'off',
            'no-invalid-this': 'off',
            'no-buffer-constructor': 'off',
            'array-bracket-spacing': 'error',
            'consistent-return': 'off',
            'global-require': 'off',
            'key-spacing': 'error',
            'no-eq-null': 'off',
            'no-lonely-if': 'off',
            'no-new': 'off',

            'no-restricted-properties': [2, {
                object: 'Object',
                property: 'assign',
            }],

            'no-unused-vars': 'off',
            'no-warning-comments': 'error',
            'object-curly-spacing': ['error', 'never'],
            'prefer-arrow-callback': 'error',

            'prefer-const': ['error', {
                destructuring: 'all',
            }],

            'prefer-template': 'error',
            'prefer-spread': 'off',
            quotes: 'off',
            '@stylistic/quotes': ['error', 'single'],
            'no-redeclare': 'off',
            '@typescript-eslint/no-redeclare': ['error'],
            'space-before-function-paren': 'off',
            'template-curly-spacing': 'error',
            'no-useless-escape': 'off',
            indent: 'off',
            '@stylistic/indent': ['error'],
            '@stylistic/semi': ['error'],

            'no-multiple-empty-lines': ['error', {
                max: 1,
            }],

            'tsdoc/syntax': 'warn'
        },
    },
    {
        files: ['**/*.html'],
        plugins: {
            html
        },
        rules: {
            'no-restricted-properties': 'off',
            'new-cap': 'off',
            '@typescript-eslint/no-unused-vars': 'off'
        }
    },
    {
        files: ['test/bench/**/*.jsx', 'test/bench/**/*.js', 'test/bench/**/*.ts'],
        plugins: {
            react
        },
        rules: {
            'react/jsx-uses-vars': [2],
            'no-restricted-properties': 'off'
        },
        languageOptions: {
            globals: {
                ...Object.fromEntries(Object.entries(globals.browser).map(([key]) => [key, 'off'])),
                performance: true,
            },

            parser: tsParser,
            ecmaVersion: 5,
            sourceType: 'module',

            parserOptions: {
                createDefaultProgram: true,
            },
        },
    },
];

import autoprefixer from 'autoprefixer';
import cssnanoPlugin from 'cssnano';
import postcssInlineSvg from 'postcss-inline-svg';
const config = {
    plugins: [
        autoprefixer(),
        postcssInlineSvg(),
        cssnanoPlugin({
            preset: [
                'default',
                {
                    svgo: {
                        plugins: [
                            {
                                name: 'preset-default',
                                params: {
                                    overrides: {
                                        removeViewBox: false,
                                    },
                                },
                            },
                        ],
                    },
                },
            ],
        }),
    ],
};

export default config;

import {plugins} from './build/rollup_plugins';
import banner from './build/banner';
import {type InputOption, type ModuleFormat, type RollupOptions} from 'rollup';

// a config for generating a special GL JS bundle with static web worker code (in a separate file)
// https://github.com/mapbox/mapbox-gl-js/issues/6058

const {BUILD} = process.env;

const production: boolean = (BUILD !== 'dev');
const outputPostfix: string = production ? '' : '-dev';

const config = (input: InputOption, file: string, format: ModuleFormat): RollupOptions => ({
    input,
    output: {
        name: 'maplibregl',
        file,
        format,
        sourcemap: true,
        indent: false,
        banner
    },
    treeshake: production,
    plugins: plugins(production)
});

const configs = [
    config('src/index.ts', `dist/maplibre-gl-csp${outputPostfix}.js`, 'umd'),
    config('src/source/worker.ts', `dist/maplibre-gl-csp-worker${outputPostfix}.js`, 'iife')
];

export default configs;

import fs from 'fs';
import sourcemaps from 'rollup-plugin-sourcemaps2';
import {plugins, watchStagingPlugin} from './build/rollup_plugins';
import banner from './build/banner';
import {type RollupOptions} from 'rollup';

const {BUILD} = process.env;

const production = BUILD === 'production';
const outputFile = production ? 'dist/maplibre-gl.js' : 'dist/maplibre-gl-dev.js';

const config: RollupOptions[] = [{
    // Before rollup you should run build-tsc to transpile from typescript to javascript (except when running rollup in watch mode)
    // Rollup will use code splitting to bundle GL JS into three "chunks":
    // - staging/maplibregl/index.js: the main module, plus all its dependencies not shared by the worker module
    // - staging/maplibregl/worker.js: the worker module, plus all dependencies not shared by the main module
    // - staging/maplibregl/shared.js: the set of modules that are dependencies of both the main module and the worker module
    //
    // This is also where we do all of our source transformations using the plugins.
    input: ['src/index.ts', 'src/source/worker.ts'],
    output: {
        dir: 'staging/maplibregl',
        format: 'amd',
        sourcemap: 'inline',
        indent: false,
        chunkFileNames: 'shared.js',
        amd: {
            autoId: true,
        },
        minifyInternalExports: production
    },
    onwarn: (message) => {
        console.error(message);
        throw message;
    },
    treeshake: production,
    plugins: plugins(production)
}, {
    // Next, bundle together the three "chunks" produced in the previous pass
    // into a single, final bundle. See rollup/bundle_prelude.js and
    // rollup/maplibregl.js for details.
    input: 'build/rollup/maplibregl.js',
    output: {
        name: 'maplibregl',
        file: outputFile,
        format: 'umd',
        sourcemap: true,
        indent: false,
        intro: fs.readFileSync('build/rollup/bundle_prelude.js', 'utf8'),
        banner
    },
    watch: {
        // give the staging chunks a chance to finish before rebuilding the dev build
        buildDelay: 1000
    },
    treeshake: false,
    plugins: [
        // Ingest the sourcemaps produced in the first step of the build.
        // This is the only reason we use Rollup for this second pass
        sourcemaps(),
        // When running in development watch mode, tell rollup explicitly to watch
        // for changes to the staging chunks built by the previous step. Otherwise
        // only they get built, but not the merged dev build js
        ...production ? [] : [watchStagingPlugin]
    ],
}];

export default config;

{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "strict": true,
    "types": [
      "geojson",
      "offscreencanvas"
    ]
  },
  "include": [
    "dist/maplibre-gl.d.ts"
  ],
  "exclude": []
}

{
    "$schema": "https://developer.microsoft.com/json-schemas/tsdoc/v0/tsdoc.schema.json",
    "extends": ["typedoc/tsdoc.json"]
}

import {defineConfig} from 'vitest/config';

export default defineConfig({
    test: {
        globals: true,
        name: 'build',
        environment: 'node',
        include: [
            'test/build/**/*.test.{ts,js}',
        ],
        coverage: {
            provider: 'v8',
            reporter: ['json', 'html'],
            exclude: ['node_modules/', 'dist/', '**/*.test.ts'],
            all: true,
            include: ['src'],
            reportsDirectory: './coverage/vitest/build',
        },
    }
});

import {defineConfig} from 'vitest/config';

export default defineConfig({
    test: {
        globals: true,
        name: 'integration',
        environment: 'node',
        include: [
            'test/integration/**/*.test.{ts,js}',
        ],
        coverage: {
            provider: 'v8',
            reporter: ['json', 'html'],
            exclude: ['node_modules/', 'dist/', '**/*.test.ts'],
            all: true,
            include: ['src'],
            reportsDirectory: './coverage/vitest/integration',
        },
    },
});

import {defineConfig} from 'vitest/config';

export default defineConfig({
    test: {
        name: 'unit',
        environment: 'jsdom',
        environmentOptions: {
            jsdom: {
                url: 'http://localhost/',
            }
        },
        setupFiles: [
            'vitest-webgl-canvas-mock',
            './test/unit/lib/web_worker_mock.ts'
        ],
        include: [
            'src/**/*.test.{ts,js}'
        ],
        coverage: {
            provider: 'v8',
            reporter: ['json', 'html'],
            exclude: ['node_modules/', 'dist/', '**/*.test.ts'],
            all: true,
            include: ['src'],
            reportsDirectory: './coverage/vitest/unit',
        },
    },
});
