import typescript from '@rollup/plugin-typescript';
import terser from '@rollup/plugin-terser';
import json from '@rollup/plugin-json';
import copy from 'rollup-plugin-copy';

export default {
  input: 'src/start-server.ts',
  output: {
    dir: 'build',
    format: 'es',
  },
  plugins: [
    typescript({
      exclude: ['**/*.test.ts', 'start-test.js', 'cookbook', 'docs'],
    }),
    terser(),
    json(),
    copy({
      targets: [{ src: 'src/public/*', dest: 'build/public' }],
    }),
  ],
};

import { spawn } from 'node:child_process';

console.log('Starting the application...');

const app = spawn('node', ['build/start-server.js', '--headless'], {
  stdio: 'inherit',
});

// Listen for errors when spawning the process
app.on('exit', (err) => {
  console.error('Failed to start the app:', err);
  process.exit(1); // Exit with a failure code if there is an error
});

// Listen for when the process starts
app.on('spawn', () => {
  console.log('App started successfully');
  setTimeout(() => {
    app.kill();
    process.exit(0);
  }, 3000);
});
