{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "extends": [
    "config:recommended"
  ],
  "labels": [
    "dependencies",
    "filigran team"
  ],
  "prConcurrentLimit": 2,
  "packageRules": [
    {
      "matchUpdateTypes": [
        "minor"
      ],
      "prPriority": 5
    }
  ],
  "timezone": "Europe/Paris",
  "schedule": [
    "after 10pm every weekday",
    "every weekend",
    "before 5am every weekday"
  ],
  "updateNotScheduled": false
}
