{
  "plugins": ["mocha", "import"],
  "env": {
    "mocha": true
  },
  "extends": ["@malept/eslint-config/src/common"],
  "globals": {
    "NodeJS": "readonly"
  },
  "rules": {
    "no-unused-vars": [
      "error",
      {
        "args": "after-used",
        "argsIgnorePattern": "^_",
        "ignoreRestSiblings": true,
        "vars": "all"
      }
    ],
    "mocha/no-exclusive-tests": "error",
    "import/order": [
      "error",
      {
        "groups": ["builtin", "external", "internal", "parent", "sibling", "index", "object", "type"],
        "newlines-between": "always",
        "alphabetize": {
          "order": "asc",
          "caseInsensitive": true
        }
      }
    ],
    "sort-imports": [
      "error",
      {
        "ignoreDeclarationSort": true,
        "allowSeparatedGroups": true,
        "ignoreCase": true
      }
    ],
    "import/no-named-as-default": "error"
  },
  "overrides": [
    {
      "files": ["**/*.ts"],
      "extends": ["@malept/eslint-config/src/typescript"],
      "parserOptions": {
        "project": ["./tsconfig.base.json"]
      },
      "rules": {
        "no-throw-literal": "off",
        "@typescript-eslint/no-explicit-any": "warn",
        "@typescript-eslint/no-unused-expressions": "warn",
        "@typescript-eslint/no-unused-vars": [
          "error",
          {
            "args": "after-used",
            "argsIgnorePattern": "^_",
            "ignoreRestSiblings": true,
            "vars": "all"
          }
        ],
        "@typescript-eslint/only-throw-error": "error"
      }
    },
    {
      "files": ["ci/**/*.js"],
      "rules": {
        "node/no-unpublished-require": "off"
      }
    },
    {
      "files": ["packages/*/*/test/**/*_spec*.ts", "packages/*/*/test/fixture/**/*.ts"],
      "rules": {
        "global-require": "off",
        "import/no-dynamic-require": "off",
        "node/no-extraneous-import": "off",
        "node/no-unpublished-import": "off",
        "node/no-unpublished-require": "off"
      }
    },
    {
      "files": ["packages/api/cli/src/**/*.ts", "packages/external/create-electron-app/src/**/*.ts", "tools/*.{js,ts}"],
      "rules": {
        "no-process-exit": "off",
        "node/shebang": [
          "error",
          {
            "convertPath": {
              "src/*.ts": ["src/(.+?)\\.ts$", "dist/$1.js"]
            }
          }
        ]
      }
    },
    {
      "files": ["packages/api/core/test/**/*.ts", "packages/maker/*/src/Maker*.ts"],
      "rules": {
        "@typescript-eslint/no-require-imports": "off"
      }
    },
    {
      "files": ["packages/*/*/test/fixture/**/*.js", "packages/*/*/test/fixtures/**/*.js"],
      "rules": {
        "node/no-extraneous-require": "off",
        "node/no-missing-require": "off",
        "node/no-unpublished-require": "off"
      }
    },
    {
      "files": ["packages/plugin/webpack/test/fixtures/**/*.js"],
      "rules": {
        "no-process-exit": "off",
        "no-undef": "off",
        "import/no-unresolved": "off",
        "node/no-missing-import": "off",
        "node/no-unpublished-import": "off",
        "node/no-unsupported-features/es-syntax": "off"
      }
    },
    {
      "files": ["packages/utils/web-multi-logger/static/*.js"],
      "env": {
        "browser": true,
        "node": false
      }
    },
    {
      "files": ["tools/*.{js,ts}"],
      "rules": {
        "node/no-unpublished-import": "off",
        "node/no-unpublished-require": "off",
        "node/shebang": "off"
      }
    },
    {
      "files": ["packages/plugin/vite/src/**/*.ts"],
      "rules": {
        "node/no-unpublished-import": [
          "error",
          {
            "allowModules": ["vite"]
          }
        ]
      }
    }
  ]
}

{
  "extends": "@electron/lint-roller/configs/markdownlint.json"
}

const path = require('path');
const minimist = require('minimist');

const {
  _: [_node, _mocha, ...args], // _ contains the node binary, the mocha binary, and any positional args
  ...flags
} = minimist(process.argv);

process.env.LINK_FORGE_DEPENDENCIES_ON_INIT = true;

let testGlob;

/**
 * Determine which sets of tests to run. Priority goes:
 * 1. If positional arguments are passed in, run those files.
 * 2. If `--suite` is either slow or fast, run slow or fast test.
 * 3. Otherwise, run all spec tests.
 */
if (args.length === 0) {
  if (flags.suite === 'fast') {
    testGlob = 'packages/**/*_spec.ts';
  } else if (flags.suite === 'slow') {
    testGlob = 'packages/**/*_spec_slow.ts';
  } else {
    testGlob = 'packages/**/**/*_spec*';
  }
}

// In CI, use the JUnit reporter to upload results to CircleCI.
// Locally, use the default 'spec' reporter.
const reporterConfig = process.env.CI
  ? {
      reporter: 'mocha-junit-reporter',
      'reporter-option': ['mochaFile=./reports/out/test_output.xml'],
    }
  : {};

const opts = {
  extension: ['ts'],
  require: ['ts-node/register', path.join(__dirname, 'tools', 'test-setup.ts')],
  spec: testGlob,
  timeout: 800000,
  recursive: true,
  ...reporterConfig,
};

module.exports = opts;

{
  "$schema": "node_modules/lerna/schemas/lerna-schema.json",
  "version": "7.6.0",
  "npmClient": "yarn"
}

{
  "tasksRunnerOptions": {
    "default": {
      "runner": "nx/tasks-runners/default",
      "options": {
        "cacheableOperations": ["coverage:base", "test", "test:base", "test:fast", "test:slow"]
      }
    }
  },
  "targetDefaults": {
    "coverage:base": {
      "dependsOn": ["^coverage:base"]
    },
    "test": {
      "dependsOn": ["^test"]
    },
    "test:base": {
      "dependsOn": ["^test:base"]
    },
    "test:fast": {
      "dependsOn": ["^test:fast"]
    },
    "test:slow": {
      "dependsOn": ["^test:slow"]
    }
  },
  "$schema": "./node_modules/nx/schemas/nx-schema.json",
  "namedInputs": {
    "default": ["{projectRoot}/**/*", "sharedGlobals"],
    "sharedGlobals": [],
    "production": ["default"]
  }
}

{
  "$schema": "https://typedoc.org/schema.json",
  "excludeInternal": true
}

{
  "$schema": "https://typedoc.org/schema.json",
  "entryPointStrategy": "packages",
  "entryPoints": ["packages/api/core", "packages/maker/*", "packages/plugin/*", "packages/plugin/*", "packages/publisher/*", "packages/utils/types"]
}

/// <reference types="vitest/config" />
import { defineConfig } from 'vitest/config';

export default defineConfig({
  test: {
    clearMocks: true,
  },
});
