{
  "name": "windicss",
  "version": "3.5.6",
  "packageManager": "pnpm@7.0.1",
  "description": "Next generation utility-first CSS framework.",
  "main": "index.js",
  "module": "index.mjs",
  "types": "index.d.ts",
  "bin": {
    "windicss": "cli/index.js"
  },
  "scripts": {
    "dev": "rollup -cw",
    "tsd": "tsc -p ./src --emitDeclarationOnly",
    "lint": "eslint . --ext .ts",
    "lint:fix": "npm run lint -- --fix",
    "typecheck": "tsc -p ./src --noEmit",
    "build": "rollup -c && pnpm tsd",
    "build:prod": "cross-env NODE_ENV=production pnpm build",
    "test": "sucrase-node test/run.ts",
    "test:update": "cross-env UPDATE_SNAPSHOT=true sucrase-node test/run.ts",
    "test:dts": "tsc -p ./test/dts --noEmit",
    "coverage": "nyc --nycrc-path=.nycrc.json pnpm test",
    "release": "bumpp --tag --commit --push"
  },
  "exports": {
    "./package.json": "./package.json",
    ".": {
      "import": "./index.mjs",
      "require": "./index.js"
    },
    "./colors": {
      "import": "./colors.mjs",
      "require": "./colors.js"
    },
    "./defaultConfig": {
      "import": "./defaultConfig.mjs",
      "require": "./defaultConfig.js"
    },
    "./defaultTheme": {
      "import": "./defaultTheme.mjs",
      "require": "./defaultTheme.js"
    },
    "./config": {
      "import": "./config/index.mjs",
      "require": "./config/index.js"
    },
    "./lib": {
      "import": "./lib/index.mjs",
      "require": "./lib/index.js"
    },
    "./plugin": {
      "import": "./plugin/index.mjs",
      "require": "./plugin/index.js"
    },
    "./plugin/aspect-ratio": {
      "import": "./plugin/aspect-ratio/index.js",
      "require": "./plugin/aspect-ratio/index.js"
    },
    "./plugin/filters": {
      "import": "./plugin/filters/index.js",
      "require": "./plugin/filters/index.js"
    },
    "./plugin/forms": {
      "import": "./plugin/forms/index.js",
      "require": "./plugin/forms/index.js"
    },
    "./plugin/line-clamp": {
      "import": "./plugin/line-clamp/index.js",
      "require": "./plugin/line-clamp/index.js"
    },
    "./plugin/typography": {
      "import": "./plugin/typography/index.js",
      "require": "./plugin/typography/index.js"
    },
    "./plugin/scroll-snap": {
      "import": "./plugin/scroll-snap/index.js",
      "require": "./plugin/scroll-snap/index.js"
    },
    "./helpers": {
      "import": "./helpers/index.mjs",
      "require": "./helpers/index.js"
    },
    "./utils": {
      "import": "./utils/index.mjs",
      "require": "./utils/index.js"
    },
    "./utils/parser": {
      "import": "./utils/parser/index.mjs",
      "require": "./utils/parser/index.js"
    },
    "./utils/style": {
      "import": "./utils/style/index.mjs",
      "require": "./utils/style/index.js"
    }
  },
  "repository": {
    "type": "git",
    "url": "https://github.com/windicss/windicss.git"
  },
  "keywords": [
    "windicss",
    "tailwindcss",
    "css",
    "compiler"
  ],
  "engines": {
    "node": ">= 12"
  },
  "author": "Veritas Raven",
  "license": "MIT",
  "homepage": "https://github.com/windicss/windicss",
  "devDependencies": {
    "@rollup/plugin-commonjs": "^21.0.1",
    "@rollup/plugin-json": "^4.1.0",
    "@rollup/plugin-node-resolve": "^13.1.1",
    "@rollup/plugin-replace": "^3.0.0",
    "@rollup/plugin-sucrase": "^4.0.1",
    "@rollup/plugin-typescript": "^8.3.0",
    "@types/color-string": "^1.5.2",
    "@types/diff": "^5.0.1",
    "@types/fs-extra": "^9.0.13",
    "@types/jasmine": "^3.10.2",
    "@types/js-yaml": "^4.0.5",
    "@types/lodash.merge": "^4.6.6",
    "@types/minimatch": "^3.0.5",
    "@types/node": "^17.0.0",
    "@types/react": "^17.0.37",
    "@typescript-eslint/eslint-plugin": "^5.7.0",
    "@typescript-eslint/parser": "^5.7.0",
    "arg": "^5.0.1",
    "bumpp": "^7.1.1",
    "caller-path": "^3.0.0",
    "color-string": "^1.9.0",
    "cross-env": "^7.0.3",
    "diff": "^5.0.0",
    "domhandler": "^4.3.0",
    "eslint": "^8.4.1",
    "fast-glob": "^3.2.7",
    "fs-extra": "^10.0.0",
    "jasmine": "^3.10.0",
    "jasmine-spec-reporter": "^7.0.0",
    "js-yaml": "^4.1.0",
    "mini-svg-data-uri": "^1.4.3",
    "minimatch": "^3.0.4",
    "nyc": "^15.1.0",
    "picocolors": "^1.0.0",
    "pirates": "^4.0.4",
    "rollup": "^2.61.1",
    "sucrase": "^3.20.3",
    "tosource": "^2.0.0-alpha.3",
    "tslib": "^2.3.1",
    "typescript": "^4.5.4"
  }
}

import fs from 'fs';
import path from 'path';
import replace from '@rollup/plugin-replace';
import resolve from '@rollup/plugin-node-resolve';
import sucrase from '@rollup/plugin-sucrase';
import typescript from '@rollup/plugin-typescript';
import commonjs from '@rollup/plugin-commonjs';
import json from '@rollup/plugin-json';
import pkg from './package.json';

const output_dir = './dist';

const prod = process.env.NODE_ENV === 'production';

const ts_plugin = prod
  ? typescript({
    target: 'es5',
    include: 'src/**',
    outDir: output_dir,
    typescript: require('typescript'),
  })
  : sucrase({
    exclude: ['node_modules/**'],
    transforms: ['typescript'],
  });

const dump = (file) => path.join(output_dir, file);

const copy = (files) => files.forEach((file) => fs.copyFileSync(file, dump(file)));

const rmdir = (dir) =>  fs.existsSync(dir) && fs.statSync(dir).isDirectory() && fs.rmdirSync(dir, { recursive: true });

const mkdir = (dir) => !(fs.existsSync(dir) && fs.statSync(dir).isDirectory()) && fs.mkdirSync(dir);

const pack = (dir, mjs = true) => {
  return {
    writeBundle() {
      fs.writeFileSync(
        `${dump(dir)}/package.json`,
        JSON.stringify(
          {
            main: './index.js',
            module: './index.mjs',
            types: './index.d.ts',
          },
          null,
          '  '
        )
      );
    },
  };
};

const types = (dest = "index.d.ts", src = "../types/index", module = "*") => {
  return {
    writeBundle() {
      fs.writeFileSync(dump(dest), `export ${module} from "${src}";`);
    },
  };
};

export default [
  // main
  {
    input: 'src/index.ts',
    output: [
      {
        file: dump('index.js'),
        format: 'cjs',
        exports: 'default',
        paths: (id) => `./${path.relative('./src', id)}/index.js`,
      },
      {
        file: dump('index.mjs'),
        format: 'esm',
        paths: (id) => `./${path.relative('./src', id)}/index.mjs`,
      },
    ],
    external: (id) => id.startsWith('./'),
    plugins: [
      ts_plugin,
      rmdir(output_dir),
      mkdir(output_dir),
      copy(['package.json', 'README.md', 'LICENSE']),
      types("index.d.ts", "./types/lib", "{ Processor as default }"),
    ],
  },

  // colors
  {
    input: 'src/colors.ts',
    output: [
      {
        file: dump('colors.js'),
        format: 'cjs',
        exports: 'default',
        paths: (id) => `./${path.relative('./src', id)}/index.js`,
      },
      {
        file: dump('colors.mjs'),
        format: 'esm',
        paths: (id) => `./${path.relative('./src', id)}/index.mjs`,
      },
    ],
    external: (id) => id.startsWith('./'),
    plugins: [
      ts_plugin,
      types("colors.d.ts", "./types/config", "{ colors as default }"),
    ],
  },

  // defaultConfig
  {
    input: 'src/defaultConfig.ts',
    output: [
      {
        file: dump('defaultConfig.js'),
        format: 'cjs',
        exports: 'default',
        paths: (id) => `./${path.relative('./src', id)}/index.js`,
      },
      {
        file: dump('defaultConfig.mjs'),
        format: 'esm',
        paths: (id) => `./${path.relative('./src', id)}/index.mjs`,
      },
    ],
    external: (id) => id.startsWith('./'),
    plugins: [
      ts_plugin,
      types("defaultConfig.d.ts", "./types/defaultConfig", "{ default }")],

  },

  // defaultTheme
  {
    input: 'src/defaultTheme.ts',
    output: [
      {
        file: dump('defaultTheme.js'),
        format: 'cjs',
        exports: 'default',
        paths: (id) => `./${path.relative('./src', id)}/index.js`,
      },
      {
        file: dump('defaultTheme.mjs'),
        format: 'esm',
        paths: (id) => `./${path.relative('./src', id)}/index.mjs`,
      },
    ],
    external: (id) => id.startsWith('./'),
    plugins: [
      ts_plugin,
      types("defaultTheme.d.ts", "./types/defaultTheme", "{ default }")
    ],
  },

  // resolveConfig
  {
    input: 'src/resolveConfig.ts',
    output: [
      {
        file: dump('resolveConfig.js'),
        format: 'cjs',
        exports: 'default',
        paths: (id) => `./${path.relative('./src', id)}/index.js`,
      },
      {
        file: dump('resolveConfig.mjs'),
        format: 'esm',
        paths: (id) => `./${path.relative('./src', id)}/index.mjs`,
      },
    ],
    external: (id) => id.startsWith('./'),
    plugins: [
      ts_plugin,
      types("resolveConfig.d.ts", "./types/resolveConfig", "{ default }")
    ],
  },

  // plugin
  {
    input: 'src/plugin/index.ts',
    output: [
      {
        file: dump('plugin/index.js'),
        exports: 'default',
        format: 'cjs',
      },
      {
        file: dump('plugin/index.mjs'),
        format: 'esm',
      },
    ],
    plugins: [
      ts_plugin,
      resolve(),
      pack('plugin'),
      types(`plugin/index.d.ts`, `../types/plugin/index`, "{ default }"),
    ],
  },

  // plugin deep
  ...fs.readdirSync('src/plugin').filter(dir => fs.statSync(`src/plugin/${dir}`).isDirectory())
    .map((dir) => ({
      input: `src/plugin/${dir}/index.ts`,
      output: [
        {
          file: dump(`plugin/${dir}/index.js`),
          exports: 'default',
          format: 'cjs',
        },
      ],
      plugins: [
        ts_plugin,
        resolve(),
        commonjs(),
        types(`plugin/${dir}/index.d.ts`, `../../types/plugin/${dir}/index`, "{ default }"),
      ],
    })),

  // cli
  {
    input: 'src/cli/index.ts',
    output: [
      {
        file: dump('cli/index.js'),
        banner: '#!/usr/bin/env node',
        format: 'cjs',
        paths: (id) =>
          id.match(/\/src\/(lib|utils|plugin|config|colors)/) &&
          `../${path.dirname(path.relative('./src', id))}/index.js`,
      },
    ],
    onwarn: (warning) => {
      if (warning.code === 'CIRCULAR_DEPENDENCY') return;
    },
    external: (id) =>
      id.match(/\/src\/(lib|utils|plugin|config|colors)/),
    plugins: [
      replace({
        preventAssignment: true,
        __NAME__: pkg.name,
        __VERSION__: pkg.version,
      }),
      ts_plugin,
      resolve(),
      commonjs(),
    ],
  },

  // utils
  ...fs.readdirSync('src/').filter((dir) => ['config', 'lib', 'utils', 'helpers'].includes(dir) && fs.statSync(`src/${dir}`).isDirectory())
    .map((dir) => ({
      input: `src/${dir}/index.ts`,
      output: [
        {
          file: dump(`${dir}/index.js`),
          format: 'cjs',
        },
        {
          file: dump(`${dir}/index.mjs`),
          format: 'esm',
        },
      ],
      plugins: [
        ts_plugin,
        json(),
        resolve(),
        commonjs(),
        pack(dir),
        types(`${dir}/index.d.ts`, `../types/${dir}/index`),
      ],
    })),

  // utils deep
  ...fs
    .readdirSync('src/utils')
    .filter(
      (dir) =>
        dir !== 'algorithm' && fs.statSync(`src/utils/${dir}`).isDirectory()
    )
    .map((dir) => ({
      input: `src/utils/${dir}/index.ts`,
      output: [
        {
          file: dump(`utils/${dir}/index.js`),
          format: 'cjs',
        },
        {
          file: dump(`utils/${dir}/index.mjs`),
          format: 'esm',
        },
      ],
      plugins: [
        ts_plugin,
        json(),
        resolve(),
        commonjs(),
        pack(`utils/${dir}`),
        types(`utils/${dir}/index.d.ts`, `../../types/utils/${dir}/index`),
      ],
    })),
];
