{
  "trailingComma": "es5"
}

import { writeFile } from "node:fs/promises";
import { fileURLToPath } from "node:url";
import { resolve } from "pathe";
import { normalize } from "pathe";
import { defineBuildConfig } from "unbuild";

const srcDir = fileURLToPath(new URL("src", import.meta.url));

export const subpaths = [
  "cli",
  "config",
  "core",
  "kit",
  "presets",
  "rollup",
  "runtime",
  "meta",
  "types",
];

export default defineBuildConfig({
  declaration: true,
  name: "nitro",
  entries: [
    // CLI
    { input: "src/cli/index.ts" },
    // Config
    { input: "src/config/index.ts" },
    // Core
    { input: "src/core/index.ts" },
    // Runtime
    { input: "src/runtime/", outDir: "dist/runtime", format: "esm" },
    // Kit
    { input: "src/kit/index.ts" },
    // Meta
    { input: "src/meta/index.ts" },
    // Presets
    { input: "src/presets/", outDir: "dist/presets", format: "esm" },
    // Rollup
    { input: "src/rollup/index.ts" },
    // Types
    { input: "src/types/index.ts" },
  ],
  alias: {
    nitropack: "nitropack",
    "nitropack/meta": resolve(srcDir, "../meta.ts"),
    "nitropack/runtime/meta": resolve(srcDir, "../runtime-meta.mjs"),
    ...Object.fromEntries(
      subpaths.map((subpath) => [
        `nitropack/${subpath}`,
        resolve(srcDir, `${subpath}/index.ts`),
      ])
    ),
  },
  hooks: {
    async "build:prepare"(ctx) {
      for (const subpath of subpaths) {
        await writeFile(
          `./${subpath}.d.ts`,
          `export * from "./dist/${subpath}/index";`
        );
      }
    },
  },
  externals: [
    "nitro",
    "nitropack",
    "nitropack/runtime/meta",
    ...subpaths.map((subpath) => `nitropack/${subpath}`),
    "firebase-functions",
    "@scalar/api-reference",
  ],
  rollup: {
    output: {
      chunkFileNames(chunk: any) {
        const id = normalize(chunk.moduleIds.at(-1));
        if (id.includes("/src/cli/")) {
          return "cli/[name].mjs";
        }
        return "_chunks/[name].mjs";
      },
    },
  },
});

import unjs from "eslint-config-unjs";

export default unjs({
  ignores: [
    "**/.output",
    "**/.nitro",
    "**/.netlify",
    "**/.nuxt",
    "**/*.gen.*",
    "**/dist",
  ],
  rules: {
    "unicorn/no-null": 0,
    "no-undef": 0,
    "@typescript-eslint/no-unused-vars": 0,
    "unicorn/filename-case": 0,
    "unicorn/consistent-function-scoping": 0,
    "@typescript-eslint/no-empty-object-type": 0,
  },
});

{
  "extends": ["github>unjs/renovate-config"]
}

export declare const pkgDir: string;
export declare const runtimeDir: string;
export declare const subpaths: string[];
export declare const runtimeDependencies: string[];

{
  "compilerOptions": {
    /* Base options: */
    "esModuleInterop": false,
    "allowSyntheticDefaultImports": true,
    "skipLibCheck": true,
    "target": "es2022",
    "allowJs": true,
    "resolveJsonModule": true,
    "moduleDetection": "force",
    "isolatedModules": true,
    "verbatimModuleSyntax": true,
    /* Strictness */
    "strict": true,
    // TODO: enable noUncheckedIndexedAccess in subsequent PR
    // "noUncheckedIndexedAccess": true,
    "forceConsistentCasingInFileNames": true,
    "noImplicitOverride": true,
    /* If NOT transpiling with TypeScript: */
    "module": "preserve",
    "noEmit": true,
    "jsx": "preserve",
    "jsxFactory": "h",
    "jsxFragmentFactory": "Fragment",
    "lib": ["es2022", "webworker", "dom.iterable"],
    "paths": {
      // CLI
      "nitro/cli": ["./src/cli"],
      "nitropack/cli": ["./src/cli"],
      // Config
      "nitro/config": ["./src/config"],
      "nitropack/config": ["./src/config"],
      // Core
      "nitro/core": ["./src/core"],
      "nitropack/core": ["./src/core"],
      // Core (alias)
      "nitro": ["./src/core"],
      "nitropack": ["./src/core"],
      // Runtime
      "nitro/runtime": ["./src/runtime"],
      "nitropack/runtime": ["./src/runtime"],
      // Runtime subpaths
      "nitro/runtime/*": ["./src/runtime/*"],
      "nitropack/runtime/*": ["./src/runtime/*"],
      // Runtime meta
      "nitro/runtime/meta": ["./runtime-meta"],
      "nitropack/runtime/meta": ["./runtime-meta"],
      // Kit
      "nitro/kit": ["./src/kit"],
      "nitropack/kit": ["./src/kit"],
      // Meta
      "nitro/meta": ["./src/meta"],
      "nitropack/meta": ["./src/meta"],
      // Presets
      "nitro/presets": ["./src/presets"],
      "nitropack/presets": ["./src/presets"],
      "nitro/presets/*": ["./src/presets/*"],
      "nitropack/presets/*": ["./src/presets/*"],
      // Rollup
      "nitro/rollup": ["./src/rollup"],
      "nitropack/rollup": ["./src/rollup"],
      // Types
      "nitro/types": ["./src/types"],
      "nitropack/types": ["./src/types"],
      // Virtual files
      "#nitro-internal-virtual/*": ["./src/types/virtual/*"],
      // Compatibility (#internal/nitro)
      "#internal/nitro": ["./src/runtime"],
      "#internal/nitro/*": ["./src/runtime/*"]
    }
  },
  "include": ["src", "test", "scripts/gen-mirror.ts"],
  "exclude": ["dist", "examples", "playground", "test/fixture"]
}

import { defineConfig } from "vitest/config";

export default defineConfig({
  test: {
    testTimeout: 30_000,
    coverage: {
      reporter: ["text", "clover", "json"],
    },
    include: ["test/**/*.test.ts"],
  },
});
