{
	"lineWidth": 100,
	"indentWidth": 4,
	"useTabs": true,
	"newLineKind": "lf",
	"includes": [
		"src/**/*.{ts,tsx,js,jsx,json}",
		"packages/**/src/**/*.{ts,tsx,js,jsx,json}"
	],
	"typescript": {
		// JavaScript and TypeScript configuration
		// https://dprint.dev/plugins/typescript/config/
		//"preferSingleLine": true,
		// "conditionalType.preferSingleLine": true,
		//"forInStatement.preferHanging": true,
		//"preferHanging": true,
		"quoteProps": "asNeeded",
		"trailingCommas": "never",
		"operatorPosition": "sameLine",
		"arguments.preferHanging": "always",
		"bracePosition": "maintain",
		"parameters.preferHanging": "always",
		"arrowFunction.useParentheses": "preferNone",
		"singleBodyPosition": "maintain",
		"conditionalExpression.preferSingleLine": true,
		"typeLiteral.preferSingleLine": true,
		"useBraces": "whenNotSingleLine",
		"spaceSurroundingProperties": false,
		"typeLiteral.separatorKind": "comma",
		"typeAssertion.spaceBeforeExpression": false,
		"unionAndIntersectionType.preferHanging": true,
		"importDeclaration.spaceSurroundingNamedImports": false,
		"exportDeclaration.spaceSurroundingNamedExports": false
	},
	"json": {
		// https://dprint.dev/plugins/json/config/
		"lineWidth": 100,
		"trailingCommas": "never",
		"newLineKind": "lf",
		"useTabs": true
	},
	"plugins": [
		"https://plugins.dprint.dev/typescript-0.89.3.wasm",
		"https://plugins.dprint.dev/json-0.19.2.wasm"
	]
}

{
	"plugins": [
		"@semantic-release/commit-analyzer",
		["@semantic-release/exec", {
			"prepareCmd": "cross-env VERSION=${nextRelease.version} npm run build"
		}],
		"@semantic-release/release-notes-generator",
		"@semantic-release/changelog",
		"@semantic-release/npm",
		["@semantic-release/github", {
			"successComment": false,
			"releasedLabels": false
		  }],
		"@semantic-release/git"
	],
	"tagFormat": "${version}",
	"branches": [
		"latest",
		{
			"name": "next",
			"prerelease": true
		}
	]
}

import eslint from "@eslint/js";
import jsdoc from "eslint-plugin-jsdoc";
import tseslint from 'typescript-eslint';

export default [
    {
        ignores: ["**/*.*(m|c)js", "node_modules/"]
    },
    ...[
        eslint.configs.recommended,
        ...tseslint.configs.recommended,
        jsdoc.configs["flat/recommended"],
    ].map(conf => ({
        ...conf,
        files: ["src/**/*.ts"]
    })),
    {
        files: ["src/**/*.ts"],        
        plugins: {
            jsdoc,
            "@typescript-eslint": tseslint.plugin,
        },
        languageOptions: {
            sourceType: "module",
            // parser: "@typescript-eslint/parser",
            parser: tseslint.parser,
            parserOptions: {
                warnOnUnsupportedTypeScriptVersion: false
            },
        },
        linterOptions: {
            reportUnusedDisableDirectives: false
        },
        rules: {
            // deprecated rules
            // https://eslint.org/blog/2023/10/deprecating-formatting-rules/#the-deprecated-rules
            "linebreak-style": 0,
            "comma-dangle": 0,
            "array-element-newline": 0,
            "spaced-comment": ["error", "always", {
                exceptions: ["-", "+"],
                markers: ["/"]
            }],
            "indent": 0,
            "keyword-spacing": 0,
            // end of deprecated rules

            "array-bracket-newline": 0,
            "consistent-this": 0,
            "camelcase": 0,
            "no-nested-ternary": 0,
            "object-shorthand": 0,
            "no-lonely-if": 0,
            "no-shadow": 0,
            "default-case": 0,
            "class-methods-use-this": 0,
            "no-useless-escape": 0,
            "no-loop-func": 0,
            "no-use-before-define": 0,
            "no-console": [
                "error", {
                    allow: ["error"]
                }
            ],
            "import/prefer-default-export": 0,
            "no-constant-binary-expression": 0,
            "no-empty": 0,
            "no-sequences": 0,
            "no-unused-vars": "off",

            // typescript rules
            "@typescript-eslint/ban-ts-comment": 0,
            "@typescript-eslint/ban-types": 0,
            "@typescript-eslint/no-explicit-any": 0,
            "@typescript-eslint/no-this-alias": 0,
            "@typescript-eslint/no-unused-expressions": 0,
            "@typescript-eslint/no-unsafe-function-type": 0,
            "@typescript-eslint/no-unused-vars": [
                "error", {
                    vars: "all",
                    args: "after-used",
                    ignoreRestSiblings: false
                }
            ],

            // jsdoc rules
            "jsdoc/newline-after-description": 0,
            "jsdoc/require-returns-description": 0,
            "jsdoc/no-undefined-types": 0,
            "jsdoc/valid-types": 0,
            "jsdoc/check-values": 0,
            "jsdoc/no-multi-asterisks": 0,
            "jsdoc/tag-lines": 0,
            "jsdoc/no-defaults": 0
        }
    }
];

{
	"tags": {
		"allowUnknownTags": false
	},
	"source": {
		"include": [
			"./src",
			"./README.md"
		],
		"includePattern": ".+\\.ts$",
		"excludePattern": "(node_modules|doc|theme|types)/"
	},
	"plugins": [
		"plugins/markdown",
		"node_modules/better-docs/typescript"
	],
	"opts": {
		"template": "node_modules/docdash",
		"encoding": "utf8",
		"destination": "doc/",
		"recurse": true,
		"verbose": true
	},
	"templates": {
		"cleverLinks": false,
		"monospaceLinks": false,
		"default": {
			"outputSourceFiles": true,
			"includeDate": false,
			"staticFiles": {
				"include": ["config/jsdoc/"]
			}
		}
	},
	"docdash": {
		"static": true,
		"sort": true,
		"sectionOrder": [
			"Namespaces",
			"Classes"
		],
		"meta": {
			"title": "billboard.js API doc",
			"description": "Re-usable, easy interface JavaScript chart library based on D3 v4+",
			"keyword": "chart, SVG, D3, Data Visualization"
		},
		"search": true,
		"typedefs": true,
		"private": false,
		"scripts": [
			"static/doc.css",
			"static/analytics.js"
		],
		"menu":{
			"Examples": {
				"href":"https://naver.github.io/billboard.js/demo/",
				"target":"_blank"
			},
			"GitHub": {
				"href":"https://github.com/naver/billboard.js",
				"target":"_blank"
			}
		}
	}
}

{
	"compilerOptions": {
		"module": "ES2015",
		"moduleResolution": "node",
		"noImplicitAny": false,
		"noImplicitThis": false,
		"strictNullChecks": true,
		"strictFunctionTypes": false,
		"baseUrl": "../",
		"lib": [
			"es6", "dom"
		]
	},
	"include": [
		"src/**/index*.ts",
	],
	"exclude": [
		"test/**/*.ts"
	],
	"noEmit": true,
	"forceConsistentCasingInFileNames": true,
	"types": [
      "@vitest/browser/providers/webdriverio",
      "@vitest/browser/providers/playwright"
    ]
}

import {resolve} from "node:path";
import {defineConfig} from "vitest/config";

export default defineConfig({
    optimizeDeps: {
        include: ["@vitest/coverage-istanbul"]
    },
    css: {
        preprocessorOptions: {
            scss: {
                api: "modern-compiler",
                silenceDeprecations: ["legacy-js-api"]
            }        
        }
    },
    test: {
        testTimeout: 3_000,
        hookTimeout: 5_000,
        globals: true,
        coverage: {
            provider: "istanbul",
            reporter: ["text-summary", "html", "lcovonly"],
            enabled: true,
            include: [
                "src/**/**",
            ],
            extension: ["ts"]
        },
        include: [
            "test/**/*-spec.ts"
        ],
        exclude: [
            "src/**/**",
            "demo/**/**",
            "config/**/**",
            "packages/**/**",
        ],
        setupFiles: [
            "test/assets/common.css",
            "src/scss/billboard.scss"
        ],
        pool: "threads",
        browser: {
            enabled: true,
            provider: "playwright",
            name: "chromium",
            headless: true,
            viewport: {
                width: 800,
                height: 600
            },
            providerOptions: {
                launch: {
                  devtools: true
                }
            },
            screenshotFailures: false
        },
        alias: [
            {
                find: /(.*)\/module\/util/i,
                replacement: "./test/assets/module/util",
                customResolver(source, importer, options) {
                    if (/src/.test(<string>importer)) {
                        return resolve(__dirname, source + ".ts");
                    }
                },
            }
        ],
        css: {
            include: [
                /.+/
            ]
        },
        open: true
    }
});

/* eslint-disable */
const pkg = require("./package.json");
const path = require("path");
const webpack = require("webpack");
const BundleAnalyzerPlugin = require("webpack-bundle-analyzer").BundleAnalyzerPlugin;
const WebpackBar = require("webpackbar");

const {env} = process;
const config = {
	entry: {
		billboard: [
			"./src/scss/billboard.scss",
			"./src/index.ts"
		]
	},
	output: {
		path: path.resolve(__dirname, "dist"),
		chunkFilename: "[name].bundle.js",
		filename: "[name].js",
		libraryTarget: "umd",
		umdNamedDefine: true,
		globalObject: "this",
		publicPath: "/dist"
	},
	externals: ({context, request}, callback) => {
		// every 'd3-*' import, will be externally required as their name except root as 'd3'
		if (/^d3-/.test(request)) {
			return callback(null, {
				commonjs: request,
				commonjs2: request,
				amd: request,
				root: "d3"
			});
		}

		callback();
	},
	devtool: false,
	resolve: {
		extensions: [".ts", ".js"]
	},
	// https://webpack.js.org/migrate/5/#need-to-support-an-older-browser-like-ie-11
	target: ["web", "es5"],
	module: {
		rules: [
			{
				test: /\.[jt]s$/,
				loader: "esbuild-loader",
				options: {
					target: "es2015"
				},
				exclude: {
					and: [/node_modules/],
					not: [/(d3\-.*)$/, /internmap/]
				}
			},
			{
				test: /(\.[jt]s)$/,
				loader: "string-replace-loader",
				options: {
					search: /__VERSION__/ig,
					replace: env.VERSION || pkg.version
				}
			}
		]
	},
	optimization: {
		usedExports: true
	},
	plugins: [
		new webpack.optimize.ModuleConcatenationPlugin(),
		new WebpackBar()
	],
	node: false,
	stats: "minimal",
	mode: "none"
};

module.exports = () => {
	let mode = "development";

	if (env.NODE_ENV) {
		mode = env.NODE_ENV;
	}

	env.ANALYZER && config.plugins.push(
		new BundleAnalyzerPlugin()
	);

	mode === "packaged" && delete config.externals;

	return require(`./config/webpack/${mode}.cjs`)(config, env);
};
