{
  "parser": "@typescript-eslint/parser",
  "plugins": [
    "@typescript-eslint",
    "eslint-plugin-import",
    "typescript-sort-keys",
    "prefer-arrow"
  ],
  "extends": [
    "eslint:recommended",
    "plugin:react/recommended",
    "plugin:@typescript-eslint/recommended",
    "plugin:jest/recommended",
    "plugin:jest/style",
    "plugin:typescript-sort-keys/recommended",
    "prettier"
  ],
  "ignorePatterns": [
    "**/node_modules/*",
    "**/out/*",
    "**/.next/*",
    "**/*package.json"
  ],
  "rules": {
    "jest/no-conditional-expect": 0,
    "no-console": 2,
    "multiline-comment-style": ["error", "separate-lines"],
    "no-return-await": 2,
    "arrow-body-style": ["error", "as-needed"],
    "import/no-amd": 2,
    "import/no-commonjs": 2,
    // "import/no-default-export": 2,
    "import/no-namespace": 2,
    "import/no-nodejs-modules": 0,
    "react/prefer-stateless-function": 2,
    "react/prop-types": 0,
    "sort-keys": 1,
    "@typescript-eslint/explicit-function-return-type": 0,
    "@typescript-eslint/explicit-module-boundary-types": 0,
    "@typescript-eslint/no-explicit-any": 0,
    "@typescript-eslint/no-unused-vars": [
      2,
      {
        "argsIgnorePattern": "^_"
      }
    ],
    "@typescript-eslint/no-var-requires": 0,
    "prefer-arrow/prefer-arrow-functions": [
      "error",
      {
        "disallowPrototype": true,
        "singleReturnOnly": false,
        "classPropertiesAllowed": false
      }
    ],
    "prefer-destructuring": [
      "error",
      { "object": true, "array": true }
    ]
  },
  "overrides": [
    {
      "files": ["./src/createClient"],
      "rules": {
        "import/no-default-export": 0
      }
    },
    {
      "files": ["cypress/**/*"],
      "plugins": ["cypress"],
      "env": {
        "cypress/globals": true
      },
      "rules": {
        "import/no-default-export": 0,
        "jest/expect-expect": 0
      }
    },
    {
      "files": ["examples/**/*"],
      "rules": {
        "react/react-in-jsx-scope": 0
      }
    },
    {
      "files": ["examples/**/pages/**/*"],
      "rules": {
        "import/no-default-export": 0
      }
    },
    {
      "files": ["**/*.config.js"],
      "rules": {
        "import/no-commonjs": 0,
        "no-undef": 0
      }
    }
  ],
  "settings": {
    "react": {
      "pragma": "React",
      "version": "detect"
    }
  }
}

// @ts-check

const fullEsmMaxSize = '5KB'
// Recent Nextjs version have some optimizations for commonjs that
// aren't available on bare-bone webpack. don't worry about threshold
// difference here for cjs. We can keep this till cjs is supported.
const fullCjsMaxSize = '29KB'

const getSimpleExamplePageLimits = () => {
  const dir = './examples/simple/.next'
  let manifest
  try {
    manifest = require(`${dir}/build-manifest.json`)
  } catch (e) {
    throw new Error(
      'Cannot find a NextJs build folder, did you forget to run build:examples ?'
    )
  }
  const limitCfg = {
    defaultSize: '90kb',
    pages: {
      '/': '85kb',
      '/404': '90kb',
      '/_app': '105kb',
      '/_error': '82Kb',
      '/second-page': '85Kb',
    },
  }
  let pageLimits = []
  for (const [uri, paths] of Object.entries(manifest.pages)) {
    pageLimits.push({
      name: `Example app: page '${uri}'`,
      limit: limitCfg.pages?.[uri] ?? limitCfg.defaultSize,
      webpack: false,
      path: paths.map(p => `${dir}/${p}`),
    })
  }
  return pageLimits
}

const modifyWebpackConfig = config => {
  config.resolve = {}
  config.resolve.fallback = { path: false, fs: false }
}

/**
 * Will ensure esm tree-shakeability and total size are within expectations.
 *
 * @link https://github.com/ai/size-limit/
 * @type {{name: string, path: string[], limit: string, import?: string, webpack?: boolean, modifyWebpackConfig: any}[]}
 */
module.exports = [
  // ###################################################
  // Dist ESM full bundle
  // ###################################################
  {
    name: 'ESM (import everything *)',
    path: ['dist/esm/index.js'],
    import: '*',
    limit: fullEsmMaxSize,
    modifyWebpackConfig,
  },
  // ###################################################
  // Fist commonjs full bundle
  // Tip: older versions of nextjs will not tree-shake
  //      cjs very well. This explains threshold differences
  // ###################################################
  {
    name: 'CJS (require everything *)',
    path: ['dist/commonjs/index.js'],
    import: '*',
    webpack: true,
    limit: fullCjsMaxSize,
    modifyWebpackConfig,
  },
  // ###################################################
  // Example apps
  // ###################################################
  ...getSimpleExamplePageLimits(),
]

{
  "env": {
    "es": {
      "presets": [
        "@babel/preset-typescript",
        [
          "@babel/env",
          {
            "modules": false,
            "targets": {
              "node": "current"
            }
          }
        ],
        ["@babel/preset-react"]
      ]
    },
    "cjs": {
      "presets": [
        "@babel/preset-typescript",
        [
          "@babel/preset-env",
          {
            "useBuiltIns": "usage",
            "corejs": {
              "version": 3
            }
          }
        ],
        [
          "next/babel",
          {
            "transform-runtime": {
              "corejs": false,
              "helpers": true,
              "regenerator": true,
              "useESModules": false
            }
          }
        ]
      ],
      "plugins": [
        "@babel/proposal-class-properties",
        [
          "add-module-exports",
          {
            "addDefaultProperty": true
          }
        ]
      ]
    },
    "esm": {
      "presets": [
        "@babel/preset-typescript",
        [
          "@babel/preset-env",
          {
            "modules": false,
            "targets": {
              "browsers": ["last 2 versions", "ie >= 11"]
            }
          }
        ],
        [
          "next/babel",
          {
            "transform-runtime": {
              "corejs": false,
              "helpers": true,
              "regenerator": true,
              "useESModules": false
            },
            "preset-env": {
              "modules": false
            }
          }
        ]
      ],
      "plugins": [
        "@babel/proposal-class-properties",
        [
          "add-module-exports",
          {
            "addDefaultProperty": true
          }
        ]
      ]
    },
    "test": {
      "presets": [
        "@babel/preset-typescript",
        [
          "@babel/env",
          {
            "targets": {
              "node": "current"
            },
            "modules": "commonjs"
          }
        ],
        ["@babel/preset-react"]
      ],
      "plugins": [
        "@babel/proposal-class-properties",
        [
          "add-module-exports",
          {
            "addDefaultProperty": true
          }
        ]
      ]
    }
  }
}

import { defineConfig } from 'cypress'

export default defineConfig({
  e2e: {
    baseUrl: 'http://localhost:3000',
    supportFile: false,
    //
    // setupNodeEvents(on, config) {
    // // implement node event listeners here
    // },
    //
  },
})

module.exports = {
  automock: false,
  collectCoverage: true,
  collectCoverageFrom: ['src/**/*.{js,jsx,ts,tsx}'],
  moduleDirectories: ['node_modules', 'src'],
  moduleNameMapper: {
    createClient: '<rootDir>/src/createClient/browser.ts',
  },
  rootDir: '.',
  testPathIgnorePatterns: [
    '<rootDir>/.next/',
    '<rootDir>/dist/',
    '<rootDir>/node_modules/',
  ],
}

