import loguxTsConfig from '@logux/eslint-config/ts'

/** @type {import('eslint').Linter.FlatConfig[]} */
export default [
  { ignores: ['**/errors.ts'] },
  ...loguxTsConfig,
  {
    rules: {
      '@typescript-eslint/no-explicit-any': 'off',
      '@typescript-eslint/unified-signatures': 'off',
      'camelcase': 'off',
      'consistent-return': 'off',
      'symbol-description': 'off'
    }
  },
  {
    files: ['deprecated/index.js'],
    rules: {
      'no-console': 'off'
    }
  }
]

{
  "name": "nanostores",
  "version": "0.11.3",
  "description": "A tiny (265 bytes) state manager for React/Preact/Vue/Svelte with many atomic tree-shakable stores",
  "keywords": [
    "store",
    "state",
    "state manager",
    "react",
    "react native",
    "preact",
    "vue",
    "svelte"
  ],
  "scripts": {
    "test:lint": "eslint .",
    "test:coverage": "c8 pnpm bnt",
    "test:types": "check-dts",
    "test:size": "size-limit",
    "test": "pnpm run /^test:/"
  },
  "author": "Andrey Sitnik <andrey@sitnik.ru>",
  "license": "MIT",
  "repository": "nanostores/nanostores",
  "sideEffects": false,
  "type": "module",
  "types": "./index.d.ts",
  "exports": {
    ".": "./index.js",
    "./package.json": "./package.json"
  },
  "engines": {
    "node": "^18.0.0 || >=20.0.0"
  },
  "funding": [
    {
      "type": "github",
      "url": "https://github.com/sponsors/ai"
    }
  ],
  "devDependencies": {
    "@logux/eslint-config": "^53.4.0",
    "@sinonjs/fake-timers": "^13.0.1",
    "@size-limit/preset-small-lib": "^11.1.5",
    "@types/node": "^22.5.4",
    "@types/sinonjs__fake-timers": "^8.1.5",
    "@types/ws": "^8.5.12",
    "@typescript-eslint/eslint-plugin": "^8.4.0",
    "@typescript-eslint/parser": "^8.4.0",
    "benchmark": "^2.1.4",
    "better-node-test": "^0.7.1",
    "c8": "^10.1.2",
    "check-dts": "^0.8.2",
    "clean-publish": "^5.0.0",
    "eslint": "^9.10.0",
    "nanodelay": "^2.0.2",
    "size-limit": "^11.1.5",
    "tsx": "^4.19.0",
    "typescript": "^5.5.4"
  },
  "prettier": {
    "arrowParens": "avoid",
    "jsxSingleQuote": false,
    "quoteProps": "consistent",
    "semi": false,
    "singleQuote": true,
    "trailingComma": "none"
  },
  "c8": {
    "exclude": [
      "deprecated",
      "**/*.test.*"
    ],
    "lines": 100,
    "check-coverage": true,
    "reporter": [
      "text",
      "lcov"
    ],
    "skip-full": true,
    "clean": true
  },
  "size-limit": [
    {
      "name": "Atom",
      "import": {
        "./index.js": "{ atom }"
      },
      "limit": "265 B"
    },
    {
      "name": "Popular Set",
      "import": {
        "./index.js": "{ map, computed, }"
      },
      "limit": "803 B"
    }
  ],
  "clean-publish": {
    "cleanDocs": true
  }
}

