if (fs.existsSync('config.env')) {
    require('dotenv').config({ path: './config.env' });
} else {
    require('dotenv');
}

const convertToLogLevel = (value: string) => {
    var log: any = false;
    if (typeof value === "string") {
        if (value.toLowerCase() === "true") {
            log = console.log;
        }
    }
    return log;
}

// Declare these environment variables first
