// Execute this script on the Hugging Face files list page to export JSON data. Don't forget to click "Load more files".
// Run console.log(JSON.stringify(modelsJson, null, 2)) to output the JSON to the console.

let modelsJson = []

function extractValue(text, prefix) {
  let ret
  text.split('\n').forEach(line => {
    if (!ret && line.startsWith(prefix))
      ret = line.replace(prefix, '').trim()
  })
  return ret || ''
}

document.querySelectorAll('.grid.h-10.grid-cols-12.place-content-center.gap-x-3.border-t.px-3.dark\\:border-gray-800').forEach(async e => {
  let data = {}
  data.name = e.children[0].children[0].textContent.trim()

  if (!data.name.endsWith('.bin') && !data.name.endsWith('.pth'))
    return

  data.desc = { en: '', zh: '', ja: '' }
  const rawText = await (await fetch(e.children[1].href.replace('/resolve/', '/raw/'))).text()

  data.size = parseInt(extractValue(rawText, 'size'))
  data.SHA256 = extractValue(rawText, 'oid sha256:')
  data.lastUpdated = e.children[3].children[0].getAttribute('datetime')
  data.url = e.children[1].href.replace('/resolve/', '/blob/').replace('?download=true', '')
  data.downloadUrl = e.children[1].href.replace('?download=true', '')
  data.tags = []

  modelsJson.push(data)
})

setTimeout(() => {
  console.log(JSON.stringify(modelsJson, null, 2))
}, 500)

package main

import (
	"embed"
	"fmt"
	"net/http"
	"os"
	"runtime/debug"
	"strings"

	backend "rwkv-runner/backend-golang"

	"github.com/wailsapp/wails/v2"
	wailsLogger "github.com/wailsapp/wails/v2/pkg/logger"
	"github.com/wailsapp/wails/v2/pkg/options"
	"github.com/wailsapp/wails/v2/pkg/options/assetserver"
	"github.com/wailsapp/wails/v2/pkg/options/windows"
)

type FileLoader struct {
	http.Handler
}

func NewFileLoader() *FileLoader {
	return &FileLoader{}
}

func (h *FileLoader) ServeHTTP(res http.ResponseWriter, req *http.Request) {
	var err error
	requestedFilename := strings.TrimPrefix(req.URL.Path, "/")
	requestedFilename = strings.TrimPrefix(requestedFilename, "=>") // absolute path
	println("Requesting file:", requestedFilename)
	fileData, err := os.ReadFile(requestedFilename)
	if err != nil {
		res.WriteHeader(http.StatusBadRequest)
		res.Write([]byte(fmt.Sprintf("Could not load file %s", requestedFilename)))
	}

	res.Write(fileData)
}

//go:embed all:frontend/dist
var assets embed.FS

//go:embed all:py310/Lib/site-packages/cyac
var cyac embed.FS

//go:embed all:py310/Lib/site-packages/cyac-1.9.dist-info
var cyacInfo embed.FS

//go:embed backend-python
var py embed.FS

//go:embed backend-rust
var webgpu embed.FS

//go:embed all:finetune
var finetune embed.FS

//go:embed midi
var midi embed.FS

//go:embed assets/sound-font
var midiAssets embed.FS

//go:embed components
var components embed.FS

func main() {
	// Create an instance of the app structure
	app := backend.NewApp()
	app.Dev = true

	if buildInfo, ok := debug.ReadBuildInfo(); !ok || strings.Contains(buildInfo.String(), "-ldflags") {
		app.Dev = false

		backend.CopyEmbed(assets)
		os.RemoveAll("./py310/Lib/site-packages/cyac-1.7.dist-info")
		backend.CopyEmbed(cyac)
		backend.CopyEmbed(cyacInfo)
		backend.CopyEmbed(py)
		backend.CopyEmbed(webgpu)
		backend.CopyEmbed(finetune)
		backend.CopyEmbed(midi)
		backend.CopyEmbed(midiAssets)
		backend.CopyEmbed(components)
	}

	var zoomFactor float64 = 1.0
	data, err := app.ReadJson("config.json")
	if err == nil {
		app.HasConfigData = true
		app.ConfigData = data.(map[string]any)
		if dpiScaling, ok := app.ConfigData["settings"].(map[string]any)["dpiScaling"]; ok {
			zoomFactor = dpiScaling.(float64) / 100
		}
	} else {
		app.HasConfigData = false
	}

	var logger wailsLogger.Logger
	if app.Dev {
		logger = wailsLogger.NewDefaultLogger()
	} else {
		logger = wailsLogger.NewFileLogger("crash.log")
	}

	// Create application with options
	err = wails.Run(&options.App{
		Title:                    "RWKV-Runner",
		Width:                    1024,
		Height:                   700,
		MinWidth:                 375,
		MinHeight:                640,
		EnableDefaultContextMenu: true,
		Windows: &windows.Options{
			ZoomFactor:           zoomFactor,
			IsZoomControlEnabled: true,
		},
		AssetServer: &assetserver.Options{
			Assets:  assets,
			Handler: NewFileLoader(),
		},
		OnStartup:     app.OnStartup,
		OnBeforeClose: app.OnBeforeClose,
		Bind: []any{
			app,
		},
		Logger: logger,
	})

	if err != nil {
		println("Error:", err.Error())
	}
}

import json
import sys


def extract_data(log_file):
    entries = []

    with open(log_file, 'r', encoding="utf-8") as file:
        lines = file.readlines()

        for i, line in enumerate(lines):
            if line.startswith('Generation Prompt:') and not lines[i + 1].startswith("<pad>"):
                current_entry = {'prompt': "", 'response': ""}

                prompt_end_point = i + 1
                for j in range(i + 1, len(lines)):
                    if lines[j].strip().endswith('- INFO'):
                        current_entry['prompt'] = current_entry['prompt'].rstrip()
                        break
                    current_entry['prompt'] += lines[j]
                    prompt_end_point = j

                for j in range(prompt_end_point + 1, len(lines)):
                    if lines[j].startswith('Url:') and lines[j].strip().endswith("/completions"):
                        for k in range(j + 1, len(lines)):
                            if lines[k].startswith('Data:'):
                                for l in range(k + 1, len(lines)):
                                    if "RequestsNum: " in lines[l]:
                                        current_entry['response'] = current_entry['response'].rstrip()
                                        entries.append(current_entry)
                                        break
                                    current_entry['response'] += lines[l]
                                else:
                                    continue
                                break
                        else:
                            continue
                        break
    return entries


def main():
    log_file = 'D:\\RWKV_Runner\\api.log' if len(sys.argv) < 2 else sys.argv[1]
    entries = extract_data(log_file)

    try:
        import cyac
        trie = cyac.Trie()
        histories = []
        for entry in entries:
            v = entry['prompt'] + entry['response']
            trie.insert(v)
        for entry in entries:
            v = entry['prompt'] + entry['response']
            for id in trie.predict(v):
                pass
            if trie[id] == v:
                histories.append(entry)
        json_data = json.dumps(histories, indent=2)
    except ModuleNotFoundError:
        json_data = json.dumps(entries, indent=2)

    print(json_data.encode('utf-8').decode('unicode_escape'))


if __name__ == "__main__":
    main()

