{
  "reject": ["prettier", "mime", "npm-packlist", "@types/mime", "@types/npm-packlist"]
}

{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "dependencyDashboard": false,
  "extends": ["config:base", ":semanticCommitTypeAll(chore)", "schedule:earlyMondays"],
  "rebaseWhen": "conflicted",
  "timezone": "UTC",
  "packageRules": [
    {
      "matchUpdateTypes": ["minor", "patch", "pin", "digest"],
      "automerge": true
    },
    {
      "groupName": "astro",
      "matchPackagePatterns": ["astro*"]
    }
  ]
}

import { Node } from 'ts-morph'
import { expect } from 'vitest'

expect.addSnapshotSerializer({
  serialize(value) {
    return value.getKindName()
  },
  test(val) {
    return Node.isNode(val)
  },
})

{
  "extends": "./tsconfig.build.json",
  "compilerOptions": {
    "paths": {
      "@pandacss/fixture": ["./packages/fixture/src/index.ts"],
      "@pandacss/types": ["./packages/types/src/index.ts"]
    }
  },
  "include": ["packages/**/*.ts"],
  "types": ["vitest/globals"],
  "exclude": [
    "node_modules",
    "./packages/extractor/__tests__/npm-bench",
    "./packages/extractor/__tests__/samples",
    "./packages/config/__tests__/samples"
  ]
}

import { defineConfig } from 'vitest/config'
import tsconfigPaths from 'vite-tsconfig-paths'

const resolve = (val: string) => new URL(val, import.meta.url).pathname

export default defineConfig({
  root: process.cwd(),
  plugins: [tsconfigPaths()],
  test: {
    globals: true,
    setupFiles: ['tests-setup.ts'],
    hideSkippedTests: true,
    environment: 'happy-dom',
    // https://vitest.dev/config/#exclude defaults + sandbox/codegen/frameworks
    exclude: [
      '**/node_modules/**',
      '**/dist/**',
      '**/cypress/**',
      '**/.{idea,git,cache,output,temp}/**',
      '**/{karma,rollup,webpack,vite,vitest,jest,ava,babel,nyc,cypress,tsup,build}.config.*',
      'sandbox/codegen/__tests__/frameworks',
    ],
  },
  resolve: {
    alias: [
      {
        find: '@pandacss/config/ts-path',
        replacement: resolve('./packages/config/src/resolve-ts-path-pattern.ts'),
      },
      {
        find: '@pandacss/dev',
        replacement: resolve('./packages/cli/src'),
      },
      {
        find: /^@pandacss\/(.*)$/,
        replacement: resolve('./packages/$1/src'),
      },
    ],
  },
})
