{
  "trailingComma": "es5",
  "singleQuote": true,
  "semi": false
}

{
  "default": {
    "requireModule": ["ts-node/register"],
    "require": ["features/**/*.ts"],
    "format": [
      "progress-bar",
      ["rerun", "@rerun.txt"],
      ["usage", "reports/usage.txt"],
      ["message", "reports/messages.ndjson"],
      ["junit", "reports/junit.xml"],
      ["html", "reports/html-formatter.html"]
    ],
    "retry": 2,
    "retryTagFilter": "@flaky"
  }
}

