{
  "compilerOptions": {
    "esModuleInterop": true,
    "lib": ["es2022"],
    "module": "node16",
    "noImplicitAny": true,
    "noImplicitReturns": true,
    "noImplicitThis": true,
    "resolveJsonModule": true,
    "sourceMap": true,
    "inlineSources": true,
    "target": "es2022",
    "typeRoots": ["./node_modules/@types", "./src/types"],
    "paths": {
      "@sinonjs/fake-timers": ["./node_modules/@types/sinonjs__fake-timers"]
    }
  }
}

{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "declaration": true,
    "outDir": "lib"
  },
  "include": ["src/**/*.ts"],
  "exclude": ["**/*_spec.ts"]
}

{
  "customTitle": "cucumber-js",
  "entryPoints": ["src/index.ts", "src/api/index.ts"],
  "favicon": "./docs/images/logo.svg",
  "footerLastModified": true,
  "navigationLinks": {
    "GitHub": "https://github.com/cucumber/cucumber-js"
  },
  "out": "./_site",
  "plugin": ["typedoc-plugin-extras"],
  "readme": "none",
  "tsconfig": "tsconfig.node.json"
}
