module.exports = {
  helpers: {
    pathToKey: (input) => {
      while (input.charAt(0) === '/') {
        input = input.substr(1);
      }

      input = input.replace(/\//g, '.');

      return input;
    },
  },
};

import { defineConfig } from 'cypress';

export default defineConfig({
  e2e: {
    setupNodeEvents() {
      // implement node event listeners here
    },
  },
  video: false,
});

import globals from 'globals';
import pluginJs from '@eslint/js';
import tseslint from 'typescript-eslint';
import pluginVue from 'eslint-plugin-vue';

export default [
  {
    ignores: [
      '**/tools',
      '**/coverage',
      '**/cypress',
      '**/i18n',
      '**/node_modules',
      '**/.nuxt',
      '**/.output',
      '**/_templates',
      '**/.storybook',
      '**/storybook-static',
      '**/src/test',
      '**/*.spec.ts',
      '**/*.stories.ts',
    ],
  },
  {
    files: ['**/*.{ts,vue}'],
    rules: {},
  },
  {
    files: ['**/*.vue'],
    languageOptions: {
      parserOptions: {
        parser: tseslint.parser,
      },
    },
  },
  {
    languageOptions: {
      globals: {
        ...globals.browser,
        ...globals.node,
      },
    },
  },
  pluginJs.configs.recommended,
  ...tseslint.configs.recommended,
  ...pluginVue.configs['flat/essential'],
  {
    rules: {
      'comma-dangle': ['error', 'always-multiline'],
      'array-bracket-spacing': ['error', 'never'],
      '@typescript-eslint/no-explicit-any': 0,
      'vue/multi-word-component-names': 0,

      'vue/attribute-hyphenation': [
        'error',
        'always',
        {
          ignore: ['modelValue'],
        },
      ],

      'vue/v-on-event-hyphenation': [
        'error',
        'always',
        {
          autofix: false,
          ignore: ['modelValue'],
        },
      ],

      'import/no-named-as-default': 0,
      'vue/no-v-text-v-html-on-component': 0,
      'vue/no-v-html': 0,
      'import/no-duplicates': 0,
      '@typescript-eslint/consistent-type-imports': 'error',
    },
  },
];

const themeColor = '#A38AE0';
const isProd = process.env.NODE_ENV === 'production';

export default defineNuxtConfig({
  app: {
    head: {
      link: [
        { rel: 'dns-prefetch', href: 'https://fonts.bunny.net/' },
        { rel: 'preconnect', href: 'https://fonts.bunny.net/' },
        { rel: 'preconnect', href: 'https://fonts.bunny.net/', crossorigin: '' },
        {
          rel: 'stylesheet',
          href: 'https://fonts.bunny.net/css?family=Inter:400,600,900|Lora:400,600&display=swap',
        },
        { rel: 'apple-touch-icon', href: '/apple-touch-icon.png' },
      ],
      meta: [{ name: 'theme-color', content: themeColor }],
    },
  },
  auth: {
    isEnabled: true,
    provider: {
      type: 'authjs',
      trustHost: false,
    },
  },
  components: {
    dirs: [],
  },
  colorMode: {
    preference: 'system',
    fallback: 'light',
    hid: 'nuxt-color-mode-script',
    globalName: '__NUXT_COLOR_MODE__',
    componentName: 'ColorScheme',
    classPrefix: '',
    classSuffix: '',
    storage: 'cookie',
    storageKey: 'nuxt-color-mode',
  },
  compatibilityDate: '2024-07-21',
  css: ['@/assets/global.scss'],
  devtools: { enabled: true },
  i18n: {
    baseUrl: process.env.BASE_URL,
    strategy: 'no_prefix',
    vueI18n: 'src/plugins/vue-i18n/vue-i18n',
    // Don't forget to update the extract-i18n-script
    locales: [
      { code: 'en-US', iso: 'en-US', file: 'en-US.json', isCatchallLocale: true },
      { code: 'de-DE', iso: 'de-DE', file: 'de-DE.json' },
    ],
    defaultLocale: 'en-US',
    lazy: true,
    langDir: '../i18n/',
    detectBrowserLanguage: {
      useCookie: true,
      cookieDomain: null,
      cookieKey: 'i18n_redirected',
      alwaysRedirect: false,
      fallbackLocale: 'en-US',
      cookieSecure: true,
    },
  },
  image: {
    densities: [1, 2, 3],
    screens: {
      xs: 320,
      sm: 640,
      md: 768,
      lg: 1024,
      xl: 1280,
      xxl: 1440,
      '2xl': 1440,
    },
  },
  imports: {
