    '@vite-pwa/nuxt',
    '@nuxt/devtools',
    '@nuxt/eslint',
  ],
  nitro: {
    compressPublicAssets: true,
    publicAssets: isProd
      ? [
          {
            baseURL: '/_nuxt',
            maxAge: 31536000,
            dir: '~/.nuxt/dist/client/_nuxt',
          },
        ]
      : [],
  },
  pwa: {
    registerType: 'autoUpdate',
    manifest: {
      name: 'Vuesion',
      short_name: 'VuesionPWA',
      theme_color: themeColor,
      background_color: '#ffffff',
      icons: [
        { src: '/android-chrome-192x192.png', sizes: '192x192', type: 'image/png' },
        { src: '/android-chrome-512x512.png', sizes: '512x512', type: 'image/png', purpose: 'any maskable' },
      ],
      start_url: '/',
      display: 'standalone',
      prefer_related_applications: true,
    },
    workbox: {
      cleanupOutdatedCaches: true,
      navigateFallback: '/en-US',
      globPatterns: ['**/*.{js,css,html,png,svg,ico}'],
      globIgnores: ['storybook/**.*', 'storybook/**/**.*'],
    },
    client: {
      installPrompt: true,
      periodicSyncForUpdates: 3600,
    },
    devOptions: {
      enabled: false,
      type: 'module',
    },
  },
  runtimeConfig: {
    public: {},
  },
  robots: {
    groups: [
      {
        userAgent: '*',
        disallow: '/*?*',
        allow: '/',
      },
    ],
  },
  routeRules: {
    // Homepage pre-rendered at build time
    // '/en-US': { prerender: true },
    // '/de-DE': { prerender: true },
    // Product page generated on-demand, revalidates in background
    // '/products/**': { swr: 3600 },
    // Blog post generated on-demand once until next deploy
    // '/blog/*': { isr: true },
    // Admin dashboard renders only on client-side
    // '/admin/**': { ssr: false },
    // Add cors headers on API routes
    // '/api/**': { cors: true },
  },
  rootDir: '.',
  security: {
    headers: {
      crossOriginResourcePolicy: 'cross-origin',
      crossOriginEmbedderPolicy: 'unsafe-none',
      contentSecurityPolicy: {
        'base-uri': ["'self'"],
        'font-src': ["'self'", 'https:', 'data:', 'fonts.bunny.net'],
        'form-action': ["'self'"],
        'frame-ancestors': ["'self'"],
        'img-src': ['*', "'self'", 'data:', 'https://d21tg1j9k9a9uf.cloudfront.net'],
        'object-src': ["'none'"],
        'script-src-attr': ["'none'"],
        'style-src': ["'self'", 'https:', "'unsafe-inline'", 'fonts.bunny.net'],
        'upgrade-insecure-requests': true,
        'script-src': [
          "'self'",
          "'unsafe-inline'",
          "'unsafe-eval'",
          "'wasm-unsafe-eval'",
          'data:',
          'blob:',
          'https://storage.googleapis.com',
          'https://cdn.jsdelivr.net',
        ],
      },
      originAgentCluster: '?1',
      referrerPolicy: 'no-referrer',
      strictTransportSecurity: {
        maxAge: 15552000,
        includeSubdomains: true,
      },
      xContentTypeOptions: 'nosniff',
      xDNSPrefetchControl: 'off',
      xDownloadOptions: 'noopen',
      xFrameOptions: 'SAMEORIGIN',
      xPermittedCrossDomainPolicies: 'none',
      xXSSProtection: '0',
      permissionsPolicy: {
        camera: [],
        'display-capture': [],
        fullscreen: [],
        geolocation: [],
        microphone: [],
      },
    },
    requestSizeLimiter: {
      maxRequestSizeInBytes: 500 * 1024 * 1024, // 500mb
      maxUploadFileRequestInBytes: 500 * 1024 * 1024, // 500mb
    },
    rateLimiter: { tokensPerInterval: 500, interval: 500000, throwError: true },
    xssValidator: false,
    corsHandler: {
      origin: '*',
      methods: ['GET', 'HEAD', 'PUT', 'PATCH', 'POST', 'DELETE'],
      preflight: { statusCode: 204 },
    },
    allowedMethodsRestricter: { methods: '*' },
    hidePoweredBy: true,
    basicAuth: false,
    enabled: true,
    csrf: false,
  },
  srcDir: './src',
  typescript: {
    shim: true,
  },
  vite: {
    css: {
      preprocessorOptions: {
        scss: {
          api: 'modern-compiler',
        },
      },
    },
  },
});

