    //  '5xl': '3.052rem',
    //}
  },
  plugins: [
    forms(),
    typography(),

    function ({ addBase }: { addBase: (config: any) => void }) {
      addBase({
        '@font-face': [
          {
            fontFamily: 'Zilla Slab',
            src: "url('./fonts/zs/ZillaSlab-Regular.woff2') format('woff2'), url('./fonts/zs/ZillaSlab-Regular.woff') format('woff')",
